-- ----------------------------
-- Table structure for aboutme
-- ----------------------------
DROP TABLE IF EXISTS `aboutme`;
CREATE TABLE `aboutme` (
  `aboutId` int(11) NOT NULL AUTO_INCREMENT COMMENT '句子id',
  `name` varchar(12) DEFAULT NULL,
  `alias` varchar(25) DEFAULT NULL,
  `sex` varchar(1) DEFAULT NULL COMMENT '性别：1为男，2为女',
  `position` varchar(30) DEFAULT NULL,
  `birthDate` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '出生日期',
  `wechat` varchar(64) DEFAULT NULL,
  `address` varchar(150) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL COMMENT '地址',
  `lifeSentence` text COMMENT '人生物语',
  `skill` text COMMENT '技能',
  `meId` int(11) DEFAULT NULL COMMENT '用户id',
  `aboutMe` text COMMENT '关于我',
  `picture` varchar(255) DEFAULT NULL COMMENT '头像图片',
  `tags` varchar(255) DEFAULT NULL COMMENT '标签',
  `email` varchar(32) DEFAULT NULL,
  `weixin` varchar(64) DEFAULT NULL COMMENT '微信',
  `qq` decimal(11,0) DEFAULT NULL COMMENT 'QQ',
  `weibo` varchar(64) DEFAULT NULL COMMENT '微博',
  `reddit` varchar(255) DEFAULT NULL COMMENT '红迪网',
  `github` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`aboutId`),
  KEY `aboutuser` (`meId`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of aboutme
-- ----------------------------
INSERT INTO `aboutme` VALUES ('1', '何坤', 'Babysbreath满天星❀', '男', 'PHP软件工程师', '1999-05-20 00:00:00', 'index/img/❀.jpg', '四川 · 德阳', '每个人都有一个死角， 自己走不出来，别人也闯不进去。我把最深沉的秘密放在那里。你不懂我，我不怪你。每个人都有一道伤口， 或深或浅，盖上布，以为不存在。|\r\n心若没有栖息的地方，到哪里都是在流浪。太柔软的心不敢敞开给人任意伤。只能背起坚硬的壳，继续流浪。|\r\n其实我不敢想像，没有你，生活会是怎样。|\r\n我还在原地，傻傻的等你回来。|\r\n我没有很想你，只是在早上醒来时，看看有没有你发来信息和未接来电；我没有很想你，只是把你来电调成唯一的铃音；我没有很想你，只是在听歌时，被某句歌词击中，脑中出现短暂的空白；我没有很想你，只是想看看你的样子，听听你的声音；我又没有很想你，只是每次醒来时，第一个想到你。|\r\n有一次，你说爱我。那一瞬间，我以为是永远。|\r\n每次抬头看见圆圆的月亮，你不在我身边，我就会疯狂想你。原来，寂寞的时候不要看月亮，因为那会让你更寂寞。|\r\n漫步云端，跃下，半空中，放歌；爱上你，堕落也似飞翔。|\r\n每晚睡前听你说晚安，是属于我的，最简单而持久的幸福。|\r\n你爱我，我会陪你；你不爱我，我给你自由。这就是傻傻的我，那个在乎你的我。', '在我身上你或许会看见秋天\r\n						在我身上你或许会看见秋天，\r\n						当黄叶，或尽脱，或只三三两两\r\n						挂在瑟缩的枯枝上索索抖颤——\r\n						荒废的歌坛，那里百鸟曾合唱。\r\n						在我身上你或许会看见暮霭，\r\n						它在日落后向西方徐徐消退：\r\n						黑夜，死的化身，渐渐把它赶开，\r\n						严静的安息笼住纷坛的万类。\r\n						在我身上你或许会看见余烬，\r\n						它在青春的寒灰里奄奄一息，\r\n						在惨淡灵床上早晚总要断魂，\r\n						给那滋养过它的烈焰所销毁。\r\n						看见了这些，你的爱就会加强，\r\n						因为他转瞬要辞你溘然长往。', '1', '心若没有栖息的地方，到哪里都是在流浪。太柔软的心不敢敞开给人任意伤。只能背起坚硬的壳，继续流浪。', 'admin/Upload/20180228/pic.jpg', 'php', '520@kkstars.cn', '18615760807', '3549596754', '讓我們細數滿天繁星', null, 'github.com/kk-stars');

-- ----------------------------
-- Table structure for admin
-- ----------------------------
DROP TABLE IF EXISTS `admin`;
CREATE TABLE `admin` (
  `adminId` int(11) NOT NULL AUTO_INCREMENT COMMENT '管理员id',
  `adminName` varchar(255) DEFAULT NULL COMMENT '管理员名称',
  `loginName` varchar(64) DEFAULT NULL,
  `loginPassword` varchar(32) DEFAULT NULL COMMENT '管理员密码',
  `headPortrait` text COMMENT '头像',
  `position` int(1) DEFAULT NULL COMMENT '职位：1.文章管理 2.栏目管理 3.评论管理 4.留言管理 5.友情链接管理 6.用户管理 7.全部',
  `contactNumber` decimal(11,0) DEFAULT NULL COMMENT '联系电话',
  `addTime` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '添加时间',
  `lastModifyTime` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '最后修改时间',
  `lastLoginTime` timestamp NULL DEFAULT NULL COMMENT '最近登陆时间',
  `numberOfLogin` decimal(11,0) DEFAULT '0' COMMENT '登录次数',
  `status` int(1) NOT NULL DEFAULT '1' COMMENT '状态值：默认值为1,1表示正常显示，0表示已删除',
  PRIMARY KEY (`adminId`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of admin
-- ----------------------------
INSERT INTO `admin` VALUES ('1', '满天星', 'hekun', '1d19f67059db99586c03d54030832789', null, '1', '18615760807', '2018-02-28 16:08:54', '2018-02-28 17:16:43', '2018-04-26 10:53:15', '80', '1');

-- ----------------------------
-- Table structure for article
-- ----------------------------
DROP TABLE IF EXISTS `article`;
CREATE TABLE `article` (
  `articleId` int(11) NOT NULL AUTO_INCREMENT COMMENT '文章id',
  `articleTitle` varchar(99) DEFAULT NULL COMMENT '文章标题',
  `articleCate` int(11) DEFAULT NULL COMMENT '文章所属栏目',
  `articleTags` varchar(99) DEFAULT NULL COMMENT '文章标签',
  `articlePic` text COMMENT '文章标题图片',
  `articleContent` text COMMENT '文章内容',
  `articleKeywords` varchar(99) DEFAULT NULL COMMENT '文章关键字',
  `articleDescription` text COMMENT '文章描述',
  `articleComments` int(11) DEFAULT '0' COMMENT '文章评论',
  `praiseClicks` int(11) DEFAULT '0' COMMENT '点赞次数',
  `articleClicks` int(11) DEFAULT '0' COMMENT '文章点击数',
  `saveImg` text COMMENT '是否保存到数据库',
  `ator` varchar(11) DEFAULT '' COMMENT '作者',
  `addTime` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '添加时间',
  `lastModifyTime` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '最后修改时间',
  `slider` int(1) DEFAULT NULL COMMENT '是否轮播:1为是，0为否',
  `overt` int(1) NOT NULL DEFAULT '1' COMMENT '公开:默认为1,1表示公开的，0表示私密的',
  `status` int(1) NOT NULL DEFAULT '1' COMMENT '状态值：默认为1，1表示正常显示，0表示已删除',
  PRIMARY KEY (`articleId`),
  KEY `cateArticle` (`articleCate`),
  KEY `userAtor` (`ator`),
  CONSTRAINT `article_ibfk_1` FOREIGN KEY (`articleCate`) REFERENCES `cate` (`cateId`)
) ENGINE=InnoDB AUTO_INCREMENT=46 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of article
-- ----------------------------
INSERT INTO `article` VALUES ('1', '再见曾经的美好', '12', '曾经', 'admin/Upload/20180418/a9d6e37bbdb2cce2d67e84ca5fc27957.jpg', '<p style=\"text-indent: 2em; text-align: left;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial; font-size: 14px; line-height: 39.2px; text-indent: 28px; widows: 1; background-color: rgb(255, 255, 255);\">你牵着时光的狗，我们倾听海的声音，一起放牧粉红的夕阳!海风泛起你的发丝，你笑着的对我许下天荒地老的诺言!我在你的童话里丢了我自己。时光的转角，你牵着时光的狗消失在平静的海面，连回忆都带的一干二静，我独自徘徊在有风的海岸，一遍又一遍温习你我曾走过的沙滩。金色的阳光下有颗忧伤的泪从我的深眸出走，划破海的心脏。从此有只忧伤的琵琶的站立在灯火阑珊处弹尽五百次回眸的离歌。你曾经在有海鸥的海岸温暖的对我说，无论风雨兼程无论我们天各一方，只要你把心贴在海的地平线，在水一方的我就会风风秒秒倾听你的心跳。雨淋湿了你我走过的痕迹，也拐走了见证我们风花雪月的时光的狗。连回忆都带的一干二静，我拿什么来祭奠我们的曾经!</span></p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial; font-size: 14px; line-height: 39.2px; text-indent: 28px; widows: 1; background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(102, 102, 102); font-family: Arial; font-size: 14px; line-height: 39.2px; text-indent: 28px; widows: 1; background-color: rgb(255, 255, 255);\">站在时光的中央，我回眸前方一片没有你的空城，繁花零落的诺言，在我驻守的童话的金色麦田早已杂草丛生!我一一的把曾经凉晒在时光的岩石，只看见有一道幽深幽深的雨巷穿过，在有雨的时候我会如约而至，相信你会撑着一把油纸伞牵着时光的狗来我的世界，在转身的瞬间，只见我的回忆住在了那雨巷，没见你来过!有风的夕阳下，折一只心系的小船，浪迹在有你的天涯。错乱的四季跋涉在时光的天涯望断早已没有你的海角，我问遍秋天里的殇，是否撞见过天换地老的诺言，是否见过有一个叫做地久天长的伊甸园!时光缄默秋天无语。我静默的走过一个人海，走出</span>寂寞<span style=\"color: rgb(102, 102, 102); font-family: Arial; font-size: 14px; line-height: 39.2px; text-indent: 28px; widows: 1; background-color: rgb(255, 255, 255);\">的空城，放逐自己驻守的童话，再见大海再见曾经的美好!</span></span></p>', '曾经', '美好', '9', '23', '141', null, '满天星', '2018-02-06 16:52:22', '2018-04-28 16:03:15', '1', '1', '1');
INSERT INTO `article` VALUES ('2', '关于你，我有太多东西关于你', '12', '关于你', 'admin/Upload/20180418/f1b9db378a8cb3c06666645f79fda21f.jpg', '<p>&nbsp; &nbsp;关于你</p><p>　　 关于你，我有太多东西关于你。</p><p>　　 清醒的时候放不下矜持，不敢说我喜欢你，只有在某个夜晚多愁善感又萦绕在心头，或是朋友聚会上的大醉，才敢借着情绪说，我喜欢你，喜欢了好久好久。</p><p>　　关于你，2012年的九月，那是我们认识的季节，在四十多平米的教室里，我认识了你。我们是同学，彼此都还不熟悉的同学。那一句个子还高嘛，是我关注你的开始，有意无意的从四组向一组瞟你，小心翼翼的眼神怕你发现，却又怕你不明白，一点点的喜欢不敢声张。后来的打过来骂过去，后来聊天的默契，互道的晚安，才明白原来就是喜欢，没有其他。确定是喜欢，可是还是不敢声张。只因当初在人群中你说了一句，从此，你便住在了心里。不敢轻易靠近打扰，是怕自己扰了你的生活步调；不敢轻易对你开口言爱，是怕那样做是一种冒犯，是一种伤害我们关系的因素。</p><p>　　 关于你。就是因为我的害怕，我亲眼看你走向她。我喜欢你是事实，你有女朋友也是事实。我以为我不说出口就会保持这样的朋友关系，却还是她赶了个巧，那应该是个寒假，你有女朋友了，我还是听说，我那么爱，却还是无果。于是我和她之间就莫名其妙的就多少有了敌人的感觉，或许就是别人口中的那样，世界上完全不相干的两个女人，会因为一个男人要么很友好，要么是仇恨。关于她我做不到友好，但也不是仇恨，只是你选择了她，我就希望她好好爱你，照顾你，连我的份也一起爱了。自爱上你的那天起，思念便成了戒不掉的瘾。你的一言一笑，一颦一蹙，无不牵动我的心，百千尘思，唯念一缕；万千红颜，唯恋一人。我愿意一生漂泊浪迹在你的故事里，甘愿为你鞍前马后，马首是瞻，即使你从未给我一句承诺，即使你从未给我半分爱情，依然无悔无怨。</p><p>　　 关于你。一场高考，考散了我们，毕业了会不会就是终生最后一见，很高兴，我们不是。各奔东西了，你们继续上了大学，值得欣慰的是，你们也是异地，这样不能算是我心机，只能说是恰巧，你们的安排恰合我意。后来也有听说，她从她的城市去看你，而那时的我能说什么呢。她是真心爱你，这是事实。我也曾和闺蜜说过，一生至少该有一次，为了某个人而忘了自己，不求有结果，不求同行，不求曾经拥有，甚至不求你爱我，只求在我最美的年华里，遇到你。今生遇见你，我觉得是幸福的，尽管这幸福交杂着万般痛苦。我也曾幻想着悄悄地去到你的学校，在某个阳光明媚的下午，在你去教室的路上和你来场偶遇，精心准备的偶遇，。可是我还来不及去和你偶遇，你们就毕了业。爱是种很玄的东西，说不清，道不明，剪不断，理还乱。世上，有种爱明知没有结果，却依然坚守原地，不舍离去，哪怕握不住你的一丝余温，依然选择默默为你守候。一路来去，心门只为你独开，山城只为你独驻，白天只为你旖旎，黑夜只为你流连。因为爱你，哪怕心入住荒岛，还是会以最深情的眼神，看着你幸福。</p><p>　　 关于你。一次聊天，我说我要回家了，你说你也要回家了，我没有想过在这个季节，我们会同在我们的城市，可是即使我们在同一个城市又怎样，最远的距离就是，我们在同一个城市却感觉离得如此遥远。突然听说你们分手了，我心里的感觉就连我自己也说不出来，我本应该高兴，可我却高兴不起来，四年，你们一路走来有分有和，算是真爱，为什么我会高兴不起来，大概是已经习惯了默默地喜欢着你，不求结果。我们也有说过要约，那晚终于约了，见面第一眼，真的此时的心情，我是很激动的，有欢喜的成分，也有些担心，担心的是我怕我不能把我最好的状态展现给你，更多的还是感动，能和你并肩走着，是上学时我想都不敢想的事情。如果我是风中的叶子，便希望能以最美的姿态落下，因为不想让你看到我的忧伤，也许是这世上的美，都有些苍凉，缘是云水深处无言的守候，是一纸素笺的暖，是光阴写意最美的诗行。我们围着公园绕了一圈，原以为会尴尬，会词穷，可是你一句我一句，我们沐浴在晚风中，我是享受这样的感觉，直到你送我到楼下，你要离开，多想给你一个拥抱，把我有多爱你告诉你。可是我还是没有，只是默默看着你离开的背影，在黑夜慢慢远去直到消失在拐角处。我多么希望，你能懂我沉默，懂我无声，懂我的欲言又止。有时你看不见我，是因为我悄悄藏在了你身后；有时你听不见我，是因为我偷偷用静默伪装了自己。其实我害怕寂寞，但却因为你会让自己陷进很深的寂寞；其实也害怕孤独，但因为你山高水远，而我又无能为力。纵然你有万事牵绊，只要你需要，我一定会义无反顾。</p><p>　　 关于你。你说你要去当兵了，其实心里是一些欢喜，因为在部队，你可能不会喜欢上别人，而我就想抱着这种侥幸的心理等你，脱开了别人的束缚，不知道这次我算不算迟到，告诉你，我喜欢你，告诉你，我会等你，等你回来。你走的时候我可能不会去送你，但是你要告诉我你在哪个城市，我会去看你。</p><p>　　&nbsp;人生，有多少别离，就会有多少相逢，喧嚣的尘世，总有一些孤独的灵魂，走在寂寞的路上，爱你，便是一缕暗香，穿过茫茫人海，幽幽而来，如花间清露，润人心田。它静静地流淌在光阴中，让相见或不见，天涯或咫尺，都变成一场欣喜和期待。因为爱你，岁月，将不再写意迷茫；因为爱你，人生将不再枯燥；因为爱你，所有的千回百转都是值得。</p><p>　　 关于你，我的岁月都关于你，从前是，现在是，以后是。</p><p><br/></p>', '你', '你', '5', '21', '11', null, '满天星', '2018-02-28 15:40:02', '2018-04-28 16:03:15', '1', '1', '1');
INSERT INTO `article` VALUES ('3', '那些以玩笑说出口的话，往往是最真的表达', '12', '玩笑', 'admin/Upload/20180418/605b4c6ef9970a3b7dadf8223d981738.jpg', '<p>&nbsp;&nbsp;有人说世上从来没有所谓的玩笑，所有的玩笑都有认真的成分，多少真心话在玩笑中说出口，只是不想让懂的人，怎么都不会懂。</p><p>　　是啊，有多少想要真心表达的话却因为各种各样的原因无奈说不出口。</p><p>　　很想念一个人，清醒的时候放不下矜持，隔绝不下彼此之间的距离，于是会在某一次大醉过后借助酒劲把所有想说的话诉之于他，然后第二天酒醒过后，身边有人说起，你矢口否认，把它解释成酒后胡言乱语，可是你心里最清楚酒后的胡言却是最真的真言；</p><p>　　很喜欢一个人，却害怕得不到想要的回应，最后连朋友的位置都没办法保留，可是心里却有隐隐的不甘心，或许知晓心意彼此会有另一番可能，抱着这样的侥幸，于是会选择在愚人节这样的日子里向他表明自己的心意，若被拒绝，还可以笑着说：你竟然当真了，我和你开玩笑呢，哈哈哈。然后转身后便是长久的失落。</p><p>　　你是否也是这样的人？那些倔强又违心的话，总是可以轻易地说出口，而那些甜言却总是找不到合适的机会表达，怕一片真心被辜负，怕得不到回应，反而会打破之前关系的平衡，于是很多真心话便以玩笑的口吻说出，一边期盼着他能够给予回应，一边又打着哈哈，我和你开玩笑呢，以此来掩盖心底的失落。</p><p>　　叶子是一个性格大大咧咧的姑娘，喜欢打打闹闹，喜欢开玩笑，身边有很多关系很好的哥们，文轩就是其中一个。已经记不清从什么时候开始，渐渐地对他产生了不一样的情愫，等自己意识到之时，早已深入骨髓。</p><p>　　因为彼此关系很亲近，所以什么样的玩笑都开过。曾说过如果找不到心仪之人，干脆俩人凑合过；也曾说过未来的那个他要按照文轩的标准找，一样的帅气逼人，一样的幽默风趣，一样地懂得照顾别人的情绪。或许从那个时候起自己便已经对他产生了超越朋友之间的感情吧。</p><p>　　后来的他们依然保持着之前的关系，一起吃喝玩乐，开各种各样的玩笑，可是叶子知道有些东西正在悄悄改变。</p><p>　　会更加关注他的一举一动，会忍不住地把目光停留在他身上，有女生的搭讪会忍不住试探。</p><p>　　也会想要找一个合适的机会表明自己的心意，可是却鼓不起勇气。</p><p>　　因为太过于熟悉，所以她一直都知道文轩喜欢的女子的类型，而自己大相径庭，她也一直知道文轩心中住着一个求而不得的人。她害怕自己的表白会让彼此间滋生间隙，再也回不到当初。</p><p>　　清醒的时候尚且能控制住自己的情感，但是酒醉之后却再也难以控制。</p><p>　　那天是自己的生日，然后一大群朋友为自己庆生。只记得喝了很多酒，记得说了很多话，然后便断片了。直到第二天听朋友说起，才知道原来所有的心事都已表明。</p><p>　　可是他们却认为你只是酒醉后的胡话。你鼓足勇气，决定彻底摊牌，你说如果我说的是真心话呢？他们却一致认为你还没有彻底清醒在开玩笑。看吧，习惯开玩笑的人，你唯一的一次认真都会被认为是玩笑。</p><p>　　一群人喧闹，然后你笑的比谁都大声，是啊，我真是喝糊涂了，我怎么会喜欢他呢，他长的那么丑，脾气又那么臭。哈哈哈，可是其实只有你自己知道这些才是违心的话。</p><p>　　你就是这样的一个人，习惯于伪装自己的情绪，装作一副百毒不侵的样子；</p><p>　　你会说很多狠话，很多违心的表达，却从来不肯透露自己的一点软弱；</p><p>　　你表面像一块石头一样坚硬，只是不希望把自己的悲伤暴露，宁愿自欺欺人，装作不在乎，也不愿承认(⊙o⊙)哦，我是真的喜欢。</p><p>　　所以你对酒精情有独钟，因为只有酒醉你才能把自己心底的秘密袒露出来，以不清醒的方式，以可以为自己留一丝防线的余地，即使失败，你也可以以醉后的胡言来掩饰；</p><p>　　所以你才会在很多次说过的话后加上一句：哈哈，你当真了，我是开玩笑的。只有这样，你才能够保有最后的一点尊严，不至于一败涂地。</p><p>　　你就是这样一个倔强的人，倔强地为自己构造一个屏障，可以随时随地保护自己。</p><p>　　可是也只有你自己知道那些玩笑的背后才是最真的表达，而很多时候你说的很多脱口而出的话：比如不喜欢，比如你走吧，比如不想念，这些可以轻易说出口的话却最违心。</p><p>　　我们总是这样，不善于表达情感中最柔软的部分，却可以把那些坚硬轻易地表达。我常常在想，如果我们都可以少一点口是心非，多一点坦诚相待，是不是我们会过的更简单一点？</p><p>　　其实我们终其一生也都在寻找那个懂你的人，他可以看透你所有的口是心非，也可以读懂你玩笑背后的表达。懂你的沉默不语，小心翼翼地呵护你的孩子气。</p><p>　　我希望这样的他早日来到你的身边，让你能够放下所有的伪装，无需再以玩笑来掩饰真心，无需借助酒精来表达感情，尽情地活出自己。</p><p><br/></p>', '玩笑', '最真的表达', '3', '23', '299', null, '满天星', '2018-02-28 15:41:57', '2018-04-28 16:36:21', '1', '1', '1');
INSERT INTO `article` VALUES ('4', '往事煮酒', '12', '往事', 'admin/Upload/20180301/010b39c65e46176d4c2cffcb9426133e.jpg', '<p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">&nbsp;&nbsp;&nbsp;我们都在学着去做一个成熟稳重的大人，遇到所有的事情都能做到不动声色，于是我们学会了掩饰和伪装，即使想哭也会笑着说出一些难过，用一种平淡或者遥远的语气，诉说着一件对别人不重要却对自己很刻骨铭心的事情。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　 【如果还有遇见】</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　 一个朋友跟我说，在感情里，信“总是深情留不住，偏偏套路得人心”的人，认为现在的痴情不值钱，是一种很白痴的表现，“认真了，就等于输了”，有技巧的撩妹子才是恋爱中的精髓，要懂得拿捏的准度，在这过程中，要知道你所“追求”的是什么。“追求”的时候要怎么说甜言蜜语，做事的时候该怎么有男人的霸道，吵架的时候要用什么方式去哄对方。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　 完了之后他笑着问我，听懂了吗？这就是你为什么在那次之后就一直没有找到新的追求，而且现在还发生了这么件事，说白了就是因为你在感情的游戏中陷得太深了。如果还有机会遇见她，你就应该像那首歌叫啥来着？“再见到你，我一定让自己假装很坚定”。说完就起身走开了，留下我一个人坐在马路边，身旁还剩下几个啤酒瓶……</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　 如果还有遇见，或许我还放不下，那还不如不见。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　 【爱你，就是在你需要的时候出现在你身边】</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　分手的时候我们任性的说了再见，赌气的跟对方歇斯底里的抱怨。你说我的懦弱和计较，我说你的自私和暧昧，于是说好这一生永不再见。你不知道的是，直到你真的开始了新的生活才是我真正和你告别的时刻。但是只要你需要，我依然会出现在你身边。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　那一天，疯子接到了前任的电话，有点诧异，因为分手这一年多里，一直没联系。电话拿起来还没来得及说话那头就传来了一阵哭声，她说：我跟他分手了，听到这个消息你高兴吗？</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　疯子没有说话，只是安静的听着。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　这一年你过的怎么样，一个人的生活有没有照顾好自己？我对不起你，当初不应该那样对你的，是我不好，对不起。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　疯子感觉事情有点不对劲，便问她是不是出了什么事？如果有什么事能帮到你的，你就说吧。当初不怪你，因为我也有错。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　她哽咽着，我生病了，而且是有可能一辈子治不好的病，也有可能是说好就好的病。我打电话给你，只是想跟你说句对不起，总感觉当初那样对你很不好。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　我陪你去治病吧，好了之后，回来我身边吧。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　好。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　年少的我们，总是有着一颗赤子之心，一颗不管世界如何变迁都不忘初衷的心，而这颗心只属于一个人，只是在经历了一次又一次的波折，被时间与命运的戏弄之后，渐渐的开始明白：在感情世界里并不是只有甜言和蜜语、浪漫与温馨，童话里的故事都是骗人的，那永远直只存在于书上和电视剧中，更多的故事到最后还是会分道扬镳、背道而驰，留下一些念想和回忆，给那个活在过去却又走不出来的人。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　几天后，疯子请假回了老家，因为前任现在呆在老家，精神时好时坏。她得是“焦虑和心理障碍并发症”，医学上也和精神病有点挂钩了，虽然不是很严重。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　之后陆陆续续请假了好几次回家去看望她，陪她说话，只因她的一句想见你。电话和微信也是消息不断，聊来聊去无非也就是安慰和鼓励她的话，有一次为了陪她去广州看病，甚至还把工作丢了，那时候疯子刚准备升职。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　 【不爱我，那么请把暧昧收回去】</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　有些人，不是真的自私自利、薄情寡义，只是受了伤，不知要该往何去，不甘心，不知要怎样去收回一切，才会戴上伪装的面具，行走在人来人往之中，继续在战场上摸爬打滚。只是，请不要伤了那些认真了的人。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　疯子最后一次和她发信息，是前几天的事了。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　她说：我很后悔跟你在一起过，和你三年时间的感情，就只换来了这几个月的陪伴，你的懦弱和计较，让我觉得别人好，所以我当初离开你是对的。你给我滚，滚的越远越好。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　她说的那个“他”，就是和疯子分手后的男朋友，也就是一开始她百般想要脱离疯子而选择的那个人。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　疯子淡淡的回了她一句：我们俩本就不应该再有联系，只因我心里对你的不舍和关心，才换来今天你再一次的无情，只是我现在后悔了，也来不及了，那么就这样吧。再见。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　我们不是不能忍受分别，只是受不了分别之后对方所留下的痕迹。我们都很好，只是时间不凑巧。差一点我就无法遇见你，差一点我就错过了你，差一点你就成为我的女人，差一点，还是差一点。如果当初不和你相遇，如果现在我才出现在你的身边，这样，是不是可以感动到你，是不是可以和你在一起。 去广州治病没效果，后来听说她哥哥在湖北有个教授级别的心理导师，于是便坐了20多个小时的火车，从深圳赶到了湖北。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　本来疯子又重新找了份差事，以为事情已经没什么转机了，也因为这件事又把工作给丢了。说来也好笑，一个男生愿意为一个女生这样付出，感觉也算挺痴情的，可是她似乎并不怎么领情，有时候总是有意无意的在疯子面前谈起她跟”他“的事情，说起“他”，她总是眉飞色舞。和“他”在一起的一年里，他们俩的事她全记得，而那三年里的事，她却一件也记不清了，不知道是故意避而不谈，还是真的忘记了。她不知道，每当提起“他”的时候，疯子的心就像被一把锋利的刀一下又一下的割着，旧伤口还没好，又被人在上面撒了一把盐，而那个人还是他最在乎的人。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　爱情最迷人的地方，就是你的一颦一笑总是牵扯着另一个人的心，明知道彼此毫无血缘关系。星座不同性格也有差异，但他好像很聪明，永远能猜中你的心。就好像当她在疯子面前提起”他“时心里在想什么，就好像当时她为何会故意躲着在背后和”他“聊天却把记录全删了，就好像此时疯子心里知道她为何会得这种病，因为，”他“的离开。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　 “他”的离开，让原本自尊心很强的她，垮了。离开了之后还欠了她不少钱，说好的海誓山盟也随着”他“的离去而崩解，可她心里却还是只有”他“。尽管”他“已经离她而去。尽管他们现在还有联系。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　湖北回来之后，她的病已经好的差不多了，本以为接下来的可以好好的，是的，以为，可以好好的……</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　还记得，分别之后的再次见面，她笑着对疯子说，你很好，遇见你是我这辈子最幸运的事，等我好了之后就踏踏实实的和你在一起，再也不分开了。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　那一晚，最后说完那句话，疯子就冲出家，喝了好多酒，一个人坐在马路边，对着公路失声喊道：失恋了就喝酒，闹够了就放手！如果不爱我，请你把暧昧收回去。谢谢。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　【我并不是你的谁，不可能必须对你好】</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　不是每个人都必须对自己好，也不是每个人都必须对自己有求必应、必须为你买单。你可以拥有你想要的东西，但希望你能好好想想你所拥有的是否是自己应得的。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　两个人的感情，一个人在不断的满足，一个人在不断的挥霍，总是觉得别人对你好是应当的，可是有没有想过，一个人对你好是一种福气，被人疼是一种运气，一个人可以随时随地对你好，他也可以随时对别人好，也可以随时把这些好收回来，不珍惜，换来的是别人的失望以及退后。等待是一种痛苦的煎熬。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　失望攒够了，那么就整理好心情，重新出发吧。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　那晚，她叫疯子去陪她，那时已经是晚上11点多钟了。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　疯子看了看时间，发了条信息过去：我今晚不去了，明天再过去陪你吧。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　那你别来了。真后悔当初会跟你在一起，三年的时间就换来你这几个月的陪伴，你给我滚，滚的越远越好，你的懦弱和计较，让我觉得别人对我好，当初离开你真是个正确的选择。如果能再给我选择一次，我宁愿不认识你，要不是我生病了我也不会再来找你，我可以过的更好，知道我为什么故意在你面前提起我跟“他&quot;的事吗？那是因为我想让你知难而退，况且我现在过的也挺好的，不用你瞎操心了。滚吧。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　疯子跑出了家门，喝了很多酒……</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　喂，我不是你的谁，我不可能必须对你好。这句话，对你对我都一样，因为至始至终，我们都没任何的关系，一切都是我在自作多情。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　【我有往事也有酒】</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　一个人，一辈子，谁没有爱过一两个错的人，谁又没有一两个故事，又有谁只有一两个故事？生活总在起落，没有谁是为了谁而活，没有谁会因为谁而过不下去。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　日子还是照样一天天的过着，偶尔疯子还会去看看她的朋友圈，知道她现在过的很好，仿佛心里被人砸了一下，疼，很疼，但是却又无能为力。可惜吗？不知道。说句不后悔是假的，说句后悔更假，这样，如果受伤的只是自己，那么就放心了，因为，我比你要坚强。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　该怎样去忘记一个人，需要一包烟还是一扎啤酒？都不是，而是不要见，不要再相见，分手后的不打扰，才是我们给彼此最好的结局。</p><p><br/></p>', '往事', '往事煮酒', '4', '21', '17', null, '满天星', '2018-02-28 15:41:57', '2018-04-28 16:03:15', '1', '1', '1');
INSERT INTO `article` VALUES ('5', '平淡的日子，依然诗意丛生', '12', '诗意', 'admin/Upload/20180301/aee1ad7fc4600a9516b0bce8ddc3bb64.jpg', '<p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">&nbsp;&nbsp;时光的流水，波澜不惊，穿过匆匆的脚步，淌过嘈杂的喧嚣，悄无声息从眼前流过。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　<a href=\"http://www.duwenzhang.com/huati/pingdan/index1.html\" style=\"color: rgb(51, 51, 51); text-decoration: none;\">平淡</a>的日子，好似一台老式的复印机，日复一日循环着，仿佛在翻印着单调。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　有人说：每天不过是两点一直线，家或者工作、学习的地方；也有人说：无所谓未来，早已是一眼到底。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　所谓诗和远方，那只是一个梦境；或者说，那只是属于展翅高飞之人。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　于是，有些人的远方，不过是休个假，到名胜古迹走一走，留下一组照片；有些人的诗意，不过也是听听音乐会或者开个相对高端的party。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　<a href=\"http://www.duwenzhang.com/wenzhang/shenghuosuibi/\" style=\"color: rgb(51, 51, 51); text-decoration: none;\">生活</a>，似乎更多是苟且，更多是没有任何变化的乏味。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　其实，远方不远，也许就在自己身边；诗意，无处不在，也许就在生活每一个不起眼的细节。当你，有一双善于发现的眼睛；当你，能够不断变换看待生活的视角。平淡的日子，依然诗意丛生。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　在柴米油盐酱醋茶中，或许会触动舌尖的味觉，在细品慢嚼中体悟到一种诗情画意；或许会想起苏东坡的“小饼如嚼月，中有酥和饴。”心中一动；或许还有很多很多的联想，令心中泛起层层诗意……</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　当匆匆的步履路过一个拐角，猛然发现一株从石缝中长出的不知名植物，看见一朵迎风摇曳，虽然孤零零却独自灿烂的小花，心中会猛一激灵，一幅独特的小小的风景，顷刻间唯美了<a href=\"http://www.duwenzhang.com/huati/xinling/index1.html\" style=\"color: rgb(51, 51, 51); text-decoration: none;\">心灵</a>，或许没有任何诗句，但心已落入诗的意境……</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　每一声鸟叫，每一声蛙鸣，每一片落叶，每一朵白云，每一次日升月落，每一次……其实，只要多用一点心，只要让心多一些柔软，就有一些诗一般的<a href=\"http://www.duwenzhang.com/huati/meihao/index1.html\" style=\"color: rgb(51, 51, 51); text-decoration: none;\">美好</a>充盈于心。哪怕行走于风雨中，也能看到风雨紧密相依、琴瑟共鸣的美好。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　只要有一点闲暇，只要能静下心，就能听到花与叶的呢喃，听到月亮与星星情的表达。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　远古的唐诗宋词，更是不经意间，以柔美的韵律，醉美了心境。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　坐在时光的渡口，看着春夏秋冬从身边缓缓走过，感受着季节里不一样的风情。那春花秋月，那夏风冬雪，哪个季节不蔓延着诗情？还有，小孩纯真的<a href=\"http://www.duwenzhang.com/huati/weixiao/index1.html\" style=\"color: rgb(51, 51, 51); text-decoration: none;\">微笑</a>，老人轻轻的叮咛，人间所有的暖心和美好，何处不会丰腴了诗歌？</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　<a href=\"http://www.duwenzhang.com/wenzhang/renshengzheli/\" style=\"color: rgb(51, 51, 51); text-decoration: none;\">人生</a>在世，心若向阳，一路<a href=\"http://www.duwenzhang.com/huati/wenxin/index1.html\" style=\"color: rgb(51, 51, 51); text-decoration: none;\">温馨</a>；心若向美，平淡的日子，也是诗意丛生……</p><p><br/></p>', '诗意', '平淡的日子，依然诗意丛生', '3', '21', '11', null, '满天星', '2018-02-28 15:41:57', '2018-04-29 12:15:44', '0', '1', '1');
INSERT INTO `article` VALUES ('6', 'My Father - 父爱无边', '12', '励志文章', 'admin/Upload/20180301/c9fe9f80af404b39dd87f7834dc2e6ab.jpg', '<table width=\"100%\" align=\"left\"><tbody><tr class=\"firstRow\"><td width=\"63%\" style=\"font-size: 13px; line-height: 1.5;\"><p>My father was a self-taught mandolin player. He was one of the best string instrument players in our town. He could not read music, but if he heard a tune a few times, he could play it. When he was younger, he was a member of a small country music band. They would play at local dances and on a few occasions would play for the local radio station. He often told us how he had auditioned and earned a position in a band that featured Patsy Cline as their lead singer. He told the family that after he was hired he never went back. Dad was a very religious man. He stated that there was a lot of drinking and cursing the day of his audition and he did not want to be around that type of environment.</p><p>　　Occasionally, Dad would get out his mandolin and play for the family. We three children: Trisha, Monte and I, George Jr., would often sing along. Songs such as the Tennessee Waltz, Harbor Lights and around Christmas time, the well-known rendition of Silver Bells. &quot;Silver Bells, Silver Bells, its Christmas time in the city&quot; would ring throughout the house. One of Dad&#39;s favorite hymns was &quot;The Old Rugged Cross&quot;. We learned the words to the hymn when we were very young, and would sing it with Dad when he would play and sing. Another song that was often shared in our house was a song that accompanied the Walt Disney series: Davey Crockett. Dad only had to hear the song twice before he learned it well enough to play it. &quot;Davey, Davey Crockett, King of the Wild Frontier&quot; was a favorite song for the family. He knew we enjoyed the song and the program and would often get out the mandolin after the program was over. I could never get over how he could play the songs so well after only hearing them a few times. I loved to sing, but I never learned how to play the mandolin. This is something I regret to this day.</p><p>　　Dad loved to play the mandolin for his family he knew we enjoyed singing, and hearing him play. He was like that. If he could give pleasure to others, he would, especially his family. He was always there, sacrificing his time and efforts to see that his family had enough in their life. I had to mature into a man and have children of my own before I realized how much he had sacrificed.</p><p>　　I joined the United States Air Force in January of 1962. Whenever I would come home on leave, I would ask Dad to play the mandolin. Nobody played the mandolin like my father. He could touch your soul with the tones that came out of that old mandolin. He seemed to shine when he was playing. You could see his pride in his ability to play so well for his family.</p><p>　　When Dad was younger, he worked for his father on the farm. His father was a farmer and sharecropped a farm for the man who owned the property. In 1950, our family moved from the farm. Dad had gained employment at the local limestone quarry. When the quarry closed in August of 1957, he had to seek other employment. He worked for Owens Yacht Company in Dundalk, Maryland and for Todd Steel in Point of Rocks, Maryland. While working at Todd Steel, he was involved in an accident. His job was to roll angle iron onto a conveyor so that the welders farther up the production line would have it to complete their job. On this particular day Dad got the third index finger of his left hand mashed between two pieces of steel. The doctor who operated on the finger could not save it, and Dad ended up having the tip of the finger amputated. He didn&#39;t lose enough of the finger where it would stop him picking up anything, but it did impact his ability to play the mandolin.</p><p>　　After the accident, Dad was reluctant to play the mandolin. He felt that he could not play as well as he had before the accident. When I came home on leave and asked him to play he would make excuses for why he couldn&#39;t play. Eventually, we would wear him down and he would say &quot;Okay, but remember, I can&#39;t hold down on the strings the way I used to&quot; or &quot;Since the accident to this finger I can&#39;t play as good&quot;. For the family it didn&#39;t make any difference that Dad couldn&#39;t play as well. We were just glad that he would play. When he played the old mandolin it would carry us back to a cheerful, happier time in our lives. &quot;Davey, Davey Crockett, King of the Wild Frontier&quot;, would again be heard in the little town of Bakerton, West Virginia.</p><p>　　In August of 1993 my father was diagnosed with inoperable lung cancer. He chose not to receive chemotherapy treatments so that he could live out the rest of his life in dignity. About a week before his death, we asked Dad if he would play the mandolin for us. He made excuses but said &quot;okay&quot;. He knew it would probably be the last time he would play for us. He tuned up the old mandolin and played a few notes. When I looked around, there was not a dry eye in the family. We saw before us a quiet humble man with an inner strength that comes from knowing God, and living with him in one&#39;s life. Dad would never play the mandolin for us again. We felt at the time that he wouldn&#39;t have enough strength to play, and that makes the memory of that day even stronger. Dad was doing something he had done all his life, giving. As sick as he was, he was still pleasing others. Dad sure could play that Mandolin!</p></td><td width=\"37%\" style=\"font-size: 13px; line-height: 1.5;\">父爱<p>　　我父亲是个自学成才的曼陀林琴手，他是我们镇最优秀的弦乐演奏者之一。他看不懂乐谱，但是如果听几次曲子，他就能演奏出来。当他年轻一点的时候，他是一个小乡村乐队的成员。他们在当地舞厅演奏，有几次还为当地广播电台演奏。他经常告诉我们，自己如何试演，如何在佩茜?克莱恩作为主唱的乐队里占一席之位。他告诉家人，一旦被聘用就永不回头。父亲是一个很严谨的人，他讲述了他试演的那天，很多人在喝酒，咒骂，他不想呆在那种环境里。</p><p>　　有时候，父亲会拿出曼陀林，为家人弹奏。我们三个小孩：翠莎、蒙蒂和我，还有乔治通常会伴唱。唱的有：《田纳西华尔兹》和《海港之光》，到了圣诞节，就唱脍炙人口的《银铃》：&quot;银铃，银铃，城里来了圣诞节。&quot;歌声充满了整个房子。父亲最爱的其中一首赞歌是《古老的十字架》。我们很小的时候就学会歌词了，而且在父亲弹唱的时候，我们也跟着唱。我们经常一起唱的另外一首歌来自沃特?迪斯尼的系列片：《戴维?克罗克特》。父亲只要听了两遍就弹起来了，&quot;戴维，戴维?克罗克特，荒野边疆的国王。&quot;那是我们家最喜欢的歌曲。他知道我们喜欢那首歌和那个节目，所以每次节目结束后，他就拿出曼陀林弹奏。我永远不能明白他如何能听完几遍后就能把一首曲子弹得那么好。我热爱唱歌，但我没有学会如何弹奏曼陀林，这是我遗憾至今的事情。</p><p>　　父亲喜欢为家人弹奏曼陀林，他知道我们喜欢唱歌，喜欢听他弹奏。他就是那样，如果他能把快乐奉献给别人，他从不吝啬，尤其是对他的家人。他总是那样，牺牲自己的时间和精力让家人生活得满足。父亲的这种付出是只有当我长大成人，而且是有了自己的孩子后才能体会到的。</p><p>　　我在1962年1月加入了美国空军基地。每当我休假回家，我都请求父亲弹奏曼陀林。没有人弹奏曼陀林能达到像我父亲那样的境界，他在那古老的曼陀林上抚出的旋律能够触及你的灵魂。他弹奏的时候，身上似乎能发出四射的光芒。你可以看出，父亲为能给家人弹奏出如此美妙的旋律，他是多么的自豪。</p><p>　　父亲年轻的时候，曾在农场为爷爷工作。爷爷是农场使用者，要向农场所有人交纳谷物抵租。1950年，我们全家搬离农场，父亲在当地石灰石采石场谋得职位。采石场在1957年倒闭，他只好另觅工作。他曾在马里兰州登多克的欧文斯游艇公司上班，还在马里兰州的洛斯的托德钢铁公司上过班。在托德钢铁公司上班期间，他遇到了意外。他的工作是把有棱角的铁滚到搬运台上，这样焊接工才能作进一步加工来完成整个工序。在那个特殊的日子里，父亲的</p><p>　　左手第三个手指被缠在两片钢铁中。医生对手指施手术，但未能保住那只手指，最后父亲只好让医生把那手指的指尖给切除了。那个手指并没有完全丧失拿东西的能力，但是却影响了他弹奏曼陀林的能力。</p><p>　　事故后，父亲不太愿意弹奏曼陀林了，他觉得再也不能像以前弹得那么好了。我休假回家请求他弹奏曼陀林，他以种种借口解释不能弹奏的原因。最后，我们软硬兼施逼他就范，他终于说：&quot;好吧，但是记住，我拨弦再也不能像过去一样了。&quot;或者会说：&quot;这个手指出意外后，我再也不能弹得像过去那样好了。&quot;对于家人来说，父亲弹得好不好并没有分别，我们很高兴他终于弹奏了。当他弹起那把陈旧的曼陀林，就会把我们带回昔日那些无忧无虑的幸福时光。&quot;戴维，戴维?克罗克特，荒野边疆的国王&quot;就会再次响彻西弗吉尼亚州的贝克顿小镇。</p><p>　　1993年8月，父亲诊断得了不宜动手术的肺癌。他不想接受化疗，因为他想体面地过完他生命最后的时光。大约在父亲去世的一周前，我们请求他能否为我们弹奏曼陀林，他说了很多借口，最后还是答应了。他知道这可能是他最后一次为我们弹奏了，他为老曼陀林调弦，弹了几个音。我环顾四周，家人个个都泪水满眶。我们看见在我们面前是一个安静的、谦虚的人，以生命最后的力量，用爱的力量支撑着。父亲再也没有足够的力量弹奏，这使我们对那天的记忆更加强烈。父亲做着他一生都在做的事情：奉献。即使生命已走到了尽头，他却仍尽力为他人创造欢乐。没错，父亲一定还能弹奏曼陀林的。</p></td></tr></tbody></table><p><br/></p>', '父爱', 'My Father - 父爱无边', '3', '20', '8', null, '满天星', '2018-02-28 15:41:57', '2018-04-28 16:03:15', '0', '1', '1');
INSERT INTO `article` VALUES ('7', '经典语句－中英对照', '8', '经典', 'admin/Upload/20180418/67d9561574e2e906005946b9eaa86527.jpg', '<p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">&nbsp;&nbsp;&nbsp;&nbsp;Love your parents. We are too busy growing up yet we forget that they are already growing old——多关心一下自己的父母吧，我们总忙着自己成长，却忘了他们也在变老。&nbsp;<br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;The moment you think about giving up,think of the reason why you held on so long——每当你想放弃的时候，想一想是什么支撑着你一路坚持。&nbsp;<br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;I don&#39;t wanna be your &#39;number one&#39; that implies there are a number two and maybe a number three. I want to be your only one——我不想做你的“第一”，因为那就意味着还有第二，第三……我只想做你的“唯一”。&nbsp;<br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;Total umbrella for someone else if he, you&#39;re just not for him in the rain.——如果他总为别人撑伞，你又何苦非为他等在雨中。&nbsp;<br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;Hold my hand,you won&#39;t get lost even with eyes closed. ——握住我的手，即使你閉上眼睛也不會迷路。&nbsp;<br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;We never really grow up. We only learn how to act in public. ——我们从未真正长大，我们只是在别人面前学会了假装。&nbsp;<br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;Each trauma, is another kind of maturity. ——每一种创伤，都是另一种成熟。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">&nbsp;&nbsp;&nbsp;&nbsp;Fortune favours the brave——运气往往眷顾勇敢的人。&nbsp;<br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;You keep on concentrating on the things you wish you had or things you wish you didn’t have and you sort of forget what you do have.-Nick Vujicic——如果你不停的纠结于你所没有的或者不想要的，你就会忘记你真正拥有的。—尼克•胡哲&nbsp;<br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;Never put your happiness in someone else’s hands.—— 永远不要把自己的幸福放在别人的手里。&nbsp;<br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;Sometimes you have to give up on someone in order to respect yourself——有时候我们必须放弃一些人，来成全自己的自尊。&nbsp;<br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;There is a time in life that is full of uneasiness.We have no other choice but to face it.——&nbsp;生命中总有那么一段时光，充满不安，可是除了勇敢面对，我们别无选择。&nbsp;<br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;Being singl means you&#39;re strong and patient enough to wait for someone who deserves your worth. ——单身意味着你足够坚强，有足够耐心去等待那个值得拥有你的人。&nbsp;<br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;The more you care, the more you have to lose.——越在意，失去的就越多。&nbsp;<br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;One of the best things in life is seeing a smile on a person’s face and knowing that you put it there——生活中最美好的一件事情是，因为你，某个人脸上洋溢着微笑。&nbsp;<br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;No matter how bad your life may seem in the moment, it will always get better——哪怕此刻的生活有多糟糕，也总会有好转的一天。&nbsp;<br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;Sometimes, it is better to turn around and leave than to insist on and pretend to be well. ——有时候，转身离开要好过假装若无其事的坚持。&nbsp;<br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;Patience with family is love, Patience with others is respect, Patience with self is confidence. ——对家里人充满耐心是爱的表现， 对别人充满耐心是种尊敬， 对自已充满耐心是种自信。--锡吕．玛塔吉&nbsp;<br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;Sometimes, the most painful not lose, but get later not happy. ——有时候，最痛苦的不是失去，而是得到以后不快乐.&nbsp;<br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;For those things i don&#39;t talk about,it does not mean i don&#39;t know.——有些事不说，不代表不知道。&nbsp;<br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;We both have no idea if we are gonna be together in the end. But one thing&#39;s for sure that I&#39;ll do everything I can to make it happen ——我们都不能确定最终能否在一起，但能确定的是，为了在一起，我会拼尽全力。</p>', '经典', '经典', '0', '21', '14', null, '满天星', '2018-02-06 16:52:22', '2018-04-28 16:03:15', '0', '1', '1');
INSERT INTO `article` VALUES ('8', '只道情深，奈何缘浅', '8', '情深', 'admin/Upload/20180418/9f2b0ff9e0c4c930a23a80fc398214cc.jpg', '<p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">&nbsp;&nbsp;只道情深，奈何缘浅</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　“人生若只如初见，何事秋分悲画扇。”第一次见到这句诗是上课的时候，我坐在他身后，从他的书中飘出来一张白纸落到我的脚边，那是我看过写的最好看的字，工整又不失张扬，给人一种特别舒适的感觉。有一种不舍得还给他的感觉，我轻轻的拍了一下他：“同学，这个是你的吧？”他回头盯着我手里的纸片，那张整齐排列的五官映入我的眼帘，他反映了一会儿，“啊！是的，谢谢！”他继续听课了，我很庆幸他并没有发现我的脸涨得通红。后来听宿舍里的人说他叫张扬，爱打篮球，是个大才子。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　“阿嚏，阿嚏。”一向有鼻炎的我坐在教室里吊扇的正下方，吹的时间久了，自然有点吃不消。“给你。”旁边一个长相乖巧的男生递过来一包纸巾，“我叫李想，来自安徽芜湖，我从班长那儿看了我们班花名册，就咱俩是同乡，你叫许紫菲对吧？”“嗯嗯，我们家是安徽亳州。”我特别激动，初到这个新环境，竟然有人已经对我有所耳闻，还是有些小得意的。然后我们俩就像是给家乡搞宣传一样，把我们那儿，所有的好吃的好玩的都聊了个遍，感觉很过瘾。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　刚入学，对其他人也不太认识，我一进教室李想就向我招招手，他已经给我占好了桌位。有一次起床晚了忘记吃早餐，然而我那脆弱的小胃可受不了啊！痛得我，趴在桌上捂着肚子脸色苍白，连说话的力气都没有。吓得李想从那以后每天都给我准备一份早餐，我都不好意思了。一天早上我又踏着上课铃声走进教室坐下，“快吃早餐吧！一会儿该凉了！”李想说。我从钱包里拿出一百块钱塞给他给他，“来，这两个月的早餐钱。”他不肯收，“你要不收，今后就不要给我买早餐了。”他没有收。从那以后李想没有再给我带早餐了，我也每天尽量早起五分钟去买早餐，但是李想的包里总是不断零食。每到快放学的时候，李想就特别屁颠的从书包里拿出面包，饼干之类的零食说：“来，紫菲，肯定饿了吧？”然后我们就像两只老鼠一样，把头塞进桌兜里偷吃。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　其实，我想说，我们宿舍里都是女汉子。王艳拉着我说走篮球社纳新呢，我们去报名，学长可都是又高又帅又有型的呢。我很不屑的用食指点着王艳的脑袋说：“你个大花痴！”转眼我们就到了，王艳毫不犹豫的报了名，她签名字的时候，我看见我又看见了那工整又跋扈的两个字“张扬”，他也报名了。“给”王艳写完名字把笔递给我，我犹豫了一下，“哎呀！你就当是陪我去嘛！”王艳撒起了娇。“好吧！”然后我也报了名。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　“紫菲，你快点，一会我们俩该去迟到了”王艳催我。今天篮球社第一次召集大家训练，我特别熟练的把长发挽起来，显得很清爽。然后我们就向体育场飞奔过去。我们去到，还没开始训练，男生们在一块打球。张扬穿着那件黄色的球衣特别显眼，他就钻进我的眼里出不来了。每一个投篮都是那么帅气，我在心里为他鼓掌呐喊。突然张扬手里的球向我飞奔过来，砸到我的头上，我感觉头很懵，就蹲下了，张扬跑过来关切地说：“美女，对不起啊，没事儿吧？”我心里窃喜，他叫我们美女耶！“奥，没事没事。”</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　刚开学大家总归是客客气气的，过了一段时间大家都玩得很熟了。眼看张扬一个帅气的三步上篮，运球，跳，准备投的时候被一个跳起的瘦高个挡了一下，投偏了，没有进。“张扬，你是猪吗？看不见他从这边拦你啊？”我大声嚷着。“许紫菲，你懂个毛线，闭嘴！”张扬大汗淋漓，晚霞的余光照到他身上感觉好像他整个人都在发光。他掀起黄色球衣一次性盖在脸上擦掉脸上汗珠，我就默默的关注着他，做他的小粉丝，只有我自己清楚我有多么崇拜他。我喜欢他的霸道，喜欢他和我斗嘴。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　这时我的电话响了，是李想，我们说好晚上一块去补作业，因为明天要交了，其实也就是我拿他的作业copy一份。“嗯，我这边就要结束了，我在这边等你。”来到自习室，我就拿起作业狂抄，“你有没有不懂的？我来给你讲讲。”“谢谢我们的大学霸，这些我都会做只是懒得耗费时间罢了。”我三下五除二写完了。如释重负的说“走吧！”“现在就走？”他有些疑惑，“不然呆在这里干什么？”“那我送你回去吧！”他有些不舍。回宿舍的路上，他一直问：“紫菲，你要不要吃这个？紫菲要不我给你买个冰激凌吃吧？”我们就一人舔着一个冰激凌走向宿舍。“我是第一次有男生送我感觉怪怪的。”“那今后我每天都送你好不好？”他认真的看着我。“哎呀！干嘛？我长这么安全，根本不用送好吗？”我装傻的说。我们一直没有说话，到了楼下，我说：“我上去了啊，谢谢你的冰激凌。”</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　从那以后，每天篮球训练，李想也都会去捧场，他总是拎一大兜矿泉水，等大家训练累了休息的时候分给大家喝，“给，张扬”李想扔过去一瓶矿泉水，张扬接住，他们俩是一个宿舍的，玩得很铁。我兴冲冲跑去拿一瓶正准备拧开。“紫菲，这个是给你准备的。”李想把手中已经拧松的脉动递给我。“哎，李想，大家待遇怎么不一样啊？”王艳一脸坏笑的看着我。我使劲掐了她一下：“死丫头，来来来，咱俩换换。”我抢过她手中的矿泉水。我注意到张扬的脸铁青着，不说话，拿一瓶矿泉水浇到头上。王艳拿着脉动很得意的递到张扬面前说：“来，张队长辛苦，给您！”张扬很用力的推开王艳“我不喝！”然后就大步走开了。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　回去的路上王艳一脸坏笑说：“紫菲，你说我们每天形影不离的，你跟李想什么时候好的，我怎么一开始没看出来啊！快给我招了。”然后就开始挠我痒痒。“哎呀，艳子，你别闹了。我们俩是老乡，你不要乱说，李想他个老好人对谁都好。”“那他怎么不给我买脉动啊？”王艳有点吃醋的说。“好，那我就你们牵牵牵线让他每天都给我们王大小姐买脉动好不好？”“我才不要呢，我已经有我们家张扬张大队长了，你们家脉动还是留着自己喝吧！”王艳脸上洋溢着幸福和憧憬。我的心里一紧张，有点支支吾吾的说“你喜欢张扬啊？”“对啊，紫菲，你不感觉队长特别帅吗？每一个投篮，运球，啊！真是太帅了。”王艳两眼放光的样子看着远方说。我敲了她脑袋一下，“看你一副流口水的样子，好像要吃了让人家张扬一样。”王艳一路上都在给我讲张扬怎样怎样的令她神魂颠倒，我没有听进去。我内心一阵悸动，怎么可以这样，我最好的闺蜜竟然喜欢我心底的那个小心翼翼珍藏的他。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　张扬打完球，一边察汗一边冲我走过来，小心脏在砰砰狂跳，我在想要怎样跟他打招呼呢！“给你，张扬。”艳子递给张扬一瓶脉动。“谢谢！”张扬冲艳子笑笑，艳子害羞的低下了头。我就站在旁边看着他们，艳子回过神来说：“来来来，紫菲，我给你买了你最喜欢的果粒奶优。”我接过来，没有说话。燕子是一个勇敢大方奔放的女生，自从那次他给我说他喜欢张扬，现在，每次训练完，她都热情跑到小卖铺去买水。“张队长，那个三步上篮我怎么都学不会，不是多一步就是少一步，一会儿你再教教我吧？”王艳一脸期待的看着张扬。“嗯，好！”“那个，艳子，你跟着张扬好好练，我就不等你了，我先回去了。”我看出了艳子的小计谋。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　从那以后我就找各种理由请病假不去参加篮球培训，也不去上课，就那样蜗在宿舍里。李想给我打电话问我是不是病了，说要来带我去医院，我说没事，就是懒得上课。李想托艳子给我拎了一大兜苹果，还有各种感冒药。过了几天，李想问我感冒好了没有，我说好了好了，怕他又给我买药。好了就要多出来走走，不能老是蜗在宿舍里。我们去了图书馆，我一头扎进书堆里，各种类型的小说书一本接着一本，不停地看，像是疯了一样。李想摸摸我的头说，你这脑子不会烧坏了吧？</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　到了吃饭时间，我也不愿意去，李想说你想吃什么，我去给你买回来。我说随便吧！我又继续钻进我的小说世界里去了，“嘿”有人突然跳出来重重的拍了我一下，吓得我“啊！”一声大叫出来，周围其他人都分分抬头看我，我脸刷的一下红了。张扬特别得意：“怎么样，许爱妃，你的病好了没？”他给我起的外号，他说我的名字，许紫菲，倒过来念是许妃子，今后就叫我许爱妃了。“张扬，你有病啊？吓死我了。”在其他人面前他是一个酷酷的队长，在我面前他像是一个赖皮一样，总是爱捉弄我，并引以为乐。“怎么样啊！最近都不来训练，本来就底子差还不好好训练。”“哼……本小姐就是不用训练也照样虐你。”我夸下海口。“唉吆喂，走吧！练练去。”张扬晃手中的篮球向我挑衅。我不甘示弱，放下书，跟着他来到篮球场。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　我抢过球，在篮下不停的投，不停地捡球，再投，完全不顾张扬。我浑身出满了汗，但是仍然在继续投。“紫菲，你怎么了？累了就歇一会。”张扬抢过篮球关切地说，他从没见过我这样，像脱缰的野马一样，拼命投球。“不累”我去抢张扬手里的球，他的手臂很长，他双手举起球，右手一勾，球进了。他抱住我，“到底怎么了？紫菲，你这样对自己我很心疼。”我在他怀里挣扎，他抱得更紧了，“有什么事你给我说啊！”你知道喜欢的人不能喜欢是什么感觉吗？”他在我额头上深深的亲了一下，靠在他宽大的怀抱中，我感觉特别踏实，我真想时光能够停留在那里。“我不管你喜欢的是谁，但今后只能喜欢我，因为我喜欢你。”这是我听过最霸道的告白。他送我回宿舍，半路他的大手主动来握住我的小手，很温暖，但是我甩开了。我们就这样左右并排走着，离得很远，我扭捏着有点不好意思。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　他跑去给我买了粥和饼，“赶快吃吧！肯定饿坏了！”他关切的看着我。我真的感觉像是做梦一样，回到宿舍，艳子跑过来关切地说：“怎么现在才吃晚饭？一天没有见你，想死你了。我要抱抱！”我的心里感觉像是做了亏心事，和艳子一直像是亲姐妹一样，我们无话不说，性格相仿的我们很合得来。我从刚才那个甜蜜的梦中醒来，心里像是压一块大石头一样，堵得慌。左手是友情，右手是爱情。洗刷完躺到床上，李想和我聊天，李想是一个特别特别好的人，无可挑剔，对我关心之至，大暖男一枚。聊了好久，我的心也不堵了，就睡觉了。梦中，我们四个人都是特别亲密的好朋友，我们纯洁透明的友谊不参杂任何杂质。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　往后的日子里我特别乖，每天按时去上课，乖乖的坐在艳子和李想中间认真听课，然后去图书馆认真完成作业。只是没有再去篮球社团打球了，见着张扬也是绕道走像是没看见一样。我已经决定为了艳子我要灭了心中那窜类似爱情的小火苗。下课了，张扬就在门口堵着我，“张扬”艳子兴冲冲的跑上去，“陈紫菲，你过来一下！”张扬有点严肃。“艳子，你先去等我一会，估计是我这几天没有去篮球社训练，也没有请假，惹队长不高兴了。”我趴到艳子耳边悄悄的跟她说。艳子点点头先走了。“那我也先走了”李想感觉自己有点多余识趣地走了。“你这几天为什么躲着我？”张扬还是特别严肃。“我没有啊，咱俩以前交集也不是很多，也没怎么说话啊！”我装作无所谓的样子看着地面说。“好，那你为什么不去参加篮球培训，而且还不请假？”张扬更加愤怒了。“姐不想去了，行不行，我要退了篮球社。”我有点恶狠狠的又有点不忍心伤害张扬。“好，我再问你最后一个问题，你那天晚上有没有接受我？”张扬抱着最后一丝希望，眼睛特别渴望的看着我。“没有啊！怕你太伤心，安慰你的。”我眼神飘忽，没有敢直视他。张扬愣了一会，平静下来问我：“那你喜欢的人是李想对不对？”“对”说完我面无表情的转身走了，可是转过身的我却已泪流满面。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　我在心里安慰自己，如果我和张扬在一块了，那么根据李想的性格，一定会恨死张扬，他俩就不能做好哥们了，而且我也没有脸见艳子了。如果燕子和张扬在一块的话，那么大家都还是好朋友。上课的时候，我依然和李想艳子坐一块，我知道张扬在暗中监视我，所以我就和李想装作很亲密。而且果然奏效，很快张扬就对外声称艳子是他女朋友了。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　我像平时一样和李想一块去食堂吃饭，他像平时一样细心体贴的给我剥鸡蛋。吃过饭我们准备返回图书馆，我怕自己的心静下来，会感觉特别空虚和委屈，最近就一只躲在图书馆看书。李想最近和我说话我总是能够听出一种暧昧的感觉。回图书馆的路上，他竟然拉起我的手，“紫菲，我喜欢你，你可以做我女朋友吗？我听张扬说你也喜欢我，是真的吗？”我的脸上一阵发烫，没有回答他，也没有甩开他的手。当时就是，不想让李想伤心难过。就这样，李想每天都约我或者是去图书馆，或者是去公园。他以为我是默认了，善良单纯的我只是不懂得拒绝。有时候我感觉李想大多是一个一块玩耍的好伙伴，而不像是男朋友，他真的是各方面都很好，对我的好也是无可挑剔，我总是觉得我对他缺一种感觉。我们在一块玩的时候还挺愉快的，双子座和双鱼座是很好玩伴，但是恋人指数偏低。当时感觉傻乎乎的，懵懵懂懂的，上课不能做亲密动作，只要有熟人在就不能拉手。为此我们还立下字句，双方还摁手印签名了呢！两个人凑一块就变成了两个逗比。艳子每天不在宿舍，晚上回来的时候就跟拉着我说：“紫菲，你知道吗，今天我和张扬一块去坐摩天轮了呢，给你看我们拍的照片。”我看见照片上他们俩笑得都很开心。心里有一种莫名的悲伤。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　放暑假了，我是很期待这个暑假的，在学校里待的够了，想换个环境。李想去火车站送我上车，一路上特别的不舍，我说没事的我们可以打电话，聊QQ。回家之后，我们每天晚上都打电话，聊的今天干什么了，聊到没有话了，还是不挂电话。我给我妈妈说，有个男孩子追我，我不知道怎么拒绝。妈妈说：“那你要知道自己到底喜不喜欢他，现在都上大学了，妈妈也不管你了，可是你要跟着自己的心走，既然都说不知道如何拒绝，那你肯定是不那么喜欢他了？”“他的人特别好，对所有人都好。”我说：“傻孩子，世界上好人多了，他们要是都喜欢你，你该怎么办呢？喜欢一个人的感觉就是就算全世界都不喜欢他，你也会站在他的身后崇拜他。在你的心中他是一个太阳般的人，是最完美的人。”妈妈说完，我的脑海里浮现了李想投中球之后嘚瑟的冲我一笑的场景。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　李想再给我打电话，我们总是说不几句，我就想各种借口，我先洗澡，我妈叫我刷碗呢，然后就挂了，再打过来我就说我有一点困了。李想是那种特别迁就我，体贴的人。并不是妈妈那样说我才对李想疏远的，而是每次打电话的话题总是千篇一律，让我感觉如同爵蜡。我用我的小技俩推脱了有一个礼拜，每次打电话就只说了几句。李想问我：“紫菲，你是不是想和我分手了？你说为什么？”我没有说话，“你不想说话，好，你如果是想和我分手，就直接回答我：是 就行了。”我犹豫了，他也不说话，就这样静静的，过了好久，我小声的“嗯”了一声。然后他就默默的挂了电话，我再也打不通。他总是这样，给我想要的，哪怕我想要跟他分开，他也会撕心裂肺的满足我。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　开学了，今年我们大三了，时间过得真快。回到学校，进教室的时候我的心里砰砰砰的，好像是做了亏心事一样既害怕见到李想，又有点想看到他。我偷偷地看着他，他瘦了许多，整个人也都像霜打的茄子一样，萎靡不振的。他找过我一次，“紫菲，我们不闹了好不好？”我不知道怎么回答他，他看出了我的答案。走的时候给我留下了一大堆好吃的，回到宿舍，翻看他买的零食，我哭了，全都是我爱吃的。他没有再来找过我，他好像消失了一样，我知道自己有多么残忍，自责也无济于事。从此他对我就就鸡犬之声相闻，老死不肯往来。开学之后，艳子每次回宿舍总是眼睛红红的，看样子张扬又惹她生气了。没多久他俩也分手了。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　时间过得不急不慢，百无聊赖的我喜欢泡图书馆，一泡就是一整天，大学，我可没少啃图书馆的书。上课的时候，李想总是故意躲着我。张扬喜欢盯着我看，碍于艳子，我也总是避着他。快毕业的我们还是比较忙的，写完论文，答辩过后，我们就要毕业了。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　有些人，一辈子都不可能会在一起，但可以在心里藏一辈子。毕业那天晚上你问“如果我们能在恰当时间，恰当的地点，恰当的场合，相遇在一起，会不会在一起相恋？”</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　“时光恍若隔世，或许会吧，只是没有如果！”我抬起头，仰望着星空说。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　泪光滑过你的脸颊，在夜空发黄的灯光照耀下，显得晶莹剔透，“我懂了！”你转过身，背向着我说。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　“人生若之如初见，何处秋风悲画屏。”彼岸豆蔻，谁许谁地老天荒？此去经年，谁伴谁天涯海角？只道是情深似海，奈何缘浅于此。你说“我们在最美的季节相遇，却没有在最美的季节相恋。”我说“情深与此，奈何缘浅！”只道一声君安，勿忘！　</p><p><br/></p>', '情深', '情深', '0', '20', '10', null, '满天星', '2018-02-28 15:40:02', '2018-04-28 16:03:15', '0', '1', '1');
INSERT INTO `article` VALUES ('9', '快乐的人生，少一分在意，多一份宽恕', '7', '快乐', 'admin/Upload/20180418/a8c23dcaca776e2cc5d8fa969f4f7162.jpg', '<p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　 &nbsp;生活中，每个人都有自己在意的东西，或大或小，或人或物，亦或一些事情。记忆，是每个人与生俱来的本能，美好会记着，不好的更会记得。所以，很多人觉得自己不快乐，是因为自己记得的东西太多了，而那些感觉快乐的人，不是没有记忆，而是选择性记忆。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　其实，记忆本身于人，是一种馈赠。心胸宽广的人，用它来记录人生的美好，慰藉自己；狭隘计较的人，却用来怨怼恩仇，惩罚自己。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　人活这一世，谁能不遇到委屈，又有谁能不经历艰难；人生怎能无缺憾，生命怎会无过错。如果把所有的事情都缠绕在心上，耿耿于怀，那么生活必然一片沉重颓然。有限的生命，谁都不易，何苦那般为难自己？与其将那些繁杂琐事纠结于心，不如看淡，看轻。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　生活的真谛就在于宽恕与忘记。宽恕那些曾经伤害过我们的人和事，相信没有谁有意伤害，只不过难言苦衷与无奈。宽恕别人，也是善待了自己；忘记那些不值得铭记的东西，生活的垃圾尚且需要我们及时清理，情感与心情的垃圾，也无需抱残守缺。心若明朗，生活处处皆暖阳。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　人生最忌讳的，就是凡事太过在意。就算在意到为其舍生忘死，一命归西，又能如何？</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　人生的舞台上，谁没有得与失，成与败？太在意，只会让自己迷失方向，更多失意。人生不过匆匆百年，当你想到，匆忙流淌的时光，有一天终是将生命推至尽头，再无回首，你还会觉得来日方长，让你学会遗忘和释然吗？</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　最是生命经不起等待，何不如就在此刻，让那些盘桓于心间已久的烦恼与忧愁，随着天空上那一抹恬淡的白云，渐渐飘远，散去，最后消失在那漫无边际的蓝天之下，还内心一片湛蓝，一片澄明。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　这世上，没有跨不过的河，迈不过的沟。很多时候，能不能想得开，能否放得下，都得看自己是否想得开。生命这一程，总是交织着风霜雨雪，夹杂着泥泞坎坷。面对顺境不要沾沾自喜，面对逆境也莫要怨天尤人。生命的列车，总是要连同荆棘沼泽一同走过，才算完满。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　无论是顺境还是逆境，终归是要过去。倘若你强行将自己困在回忆之中，只会让自己备受煎熬。人生如白驹过隙，又何必将自己置若伤情执迷不悟？学会释然，尝试放下，以一颗洒脱的心，不负似水年华。无论明天会怎样，只要心存美好的期待，太阳终会升起，阳光依旧温暖。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　人生如一杯清茶，除杂才得其精华，洗尘才见其纯澈；舍得才知其清甜，放下才闻其香郁。放飞心中的尘埃，让其像腾空而起的气球一样，放手才知其自由；放下才感其奔放。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　与其内疚于心，不如尽力补救；与其埋怨气愤，不如自省宽恕。就算过往已无可挽回，但至少今后可以做的更好。少一分在意，多一分宽恕；少一分计较，多一分谅解。适当的忘记，是品质的提升，是心态的调和，更是生命的沉淀。善待别人，也是善待自己，更是善待了所有爱你与在乎你的家人朋友。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　若是不在意，便不会失意。以一颗平和的心，面对这人生，这大千世界，相信淡然得自在，自会走出一番属于自己的精彩。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　作者简介：</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　心柔，原名谭成妍，中国散文学会会员，中国西部散文学会会员，内蒙古作家协会会员。作品见《青年文学家》《西部散文选刊》《读者文摘》《北方农村报》《河北农民报》《库尔勒晚报》《重庆日报》《祁东新闻》《搜狐新闻》等媒体报刊杂志及各大文学网站。散文在“中国之声”之《千里共良宵》栏目诵读播出。出版合集《散文经典选藏》，散文集《心柔若水》微信尾单签售中，最新文集《岁月很长，不必慌张》已公费出版，全国发行。</p><p><br/></p>', '快乐', '快乐', '0', '20', '17', null, '满天星', '2018-02-28 15:41:57', '2018-04-28 16:03:16', '0', '1', '1');
INSERT INTO `article` VALUES ('10', '人生苦短，别懂得太晚……', '7', '人生', 'admin/Upload/20180301/010b39c65e46176d4c2cffcb9426133e.jpg', '<p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">&nbsp;&nbsp;不要等到生病住院了，才明白健康是多么的重要。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　身体是自己的，不论好坏都得自己承载。健康的身体让我们如鱼得水，游山玩水，不健康的身体可以让我们度日如年，甚至于拖垮一个家庭。生活要注重细节，平时要善待自己，克制自己，健康都是从一点一滴的好习惯中培养出来的。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　不能等到年老无助了，才明白时间是那么的重要。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　随心而动，想做的事情就抓紧时间去实现吧。一眨眼，我们就都要面对现实苍老了。喜欢的东西，自己动手用脑努力赚钱去买；喜欢的人，学会珍惜拥有，用心经营好彼此之间的情谊。别等到老了，心有余力而不足的时候，才遗憾时间都去哪儿了。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　不要等到人心凉透了，才明白原来没有学会珍惜。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　往往摆在我们眼前的，现实的版本，我们总是不以为然，仿佛别人对我们的好都是理所当然，不懂得感恩。其实平凡的陪伴弥足珍贵，无声无息的默默付出最伟大。我们的父母，我们的爱人，我们的孩子，我们的知已朋友，都是日复一日，年复一年的施恩于我们，全心全意不求回报。所以，我们应该学会珍惜，回赠给他们同样的关心和温暖…。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　不要等到落魄、处处碰壁的时候，才明白谁是真正的朋友。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　人与人之间相互交往，不但要用眼睛辨别是非，还要更加用心的去体会，谁才是我们身边真正的朋友，并不是每天陪着我们胡吃海喝的朋友就是真朋友，也许在你张扬、辉煌的时候，他们像苍蝇一样围着你转，当有一天，你落魄不堪的时候，他们就离你远去，形同陌路。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　人生苦短，我们伤不起、赌不起、输不起、也等不起。能真正争取到手里的东西举止可数，所以，我们一定要努力去争取，做自己想做的事情，哪怕最终的结果是失败，至少我们尽力了，试过了，不要连尝试去做的勇气都没有。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　人生在世，有多少人都输在一个“等”字上，总以为很多人，很多事都可以等到，等自己有能力，等自己有机会，等自己长大，等自己有钱，等自己有时间，等自己有工作，等自己退休后……。然而，握在自己手里的幸福是经不起等待的。等没了青春，等没了健康，等来的是无穷无尽的悔恨和遗憾。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　人这一辈子，真的很短暂，千万别懂得太晚！太晚！太晚！重要的事情说三遍！</p><p><br/></p>', '人生', '人生', '0', '20', '13', null, '满天星', '2018-02-28 15:41:57', '2018-04-28 16:03:16', '0', '1', '1');
INSERT INTO `article` VALUES ('11', '今年初夏，曲终人散', '12', '曲终人散', 'admin/Upload/20180301/aee1ad7fc4600a9516b0bce8ddc3bb64.jpg', '<p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">&nbsp;&nbsp;今年初夏，曲终人散</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　——写给四年同窗好友</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　轻声细语缠绵了时光，轻弹浅唱黯淡了流年。那些明灭相随的离合悲欢，都在这一季里化作一纸云烟。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　不知不觉，已到了初夏。伴随着惊雷和雨滴的声音，才想起当时是自己把时间想的过于仁慈，其实，不然。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　突然想要写点什么，来纪念自己即将逝去的大学四年时光，借机过度一下自己此时此刻莫可名状的心情；突然想要说点什么，来缅怀一下自己当初万丈豪情遗失的时光和所谓的青春年华。不知道是骨子里的性情使然，还是内心深处想要特意地煽情？</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　毕业论文完成了心也空了，毕业照照了笑了也哭了，毕业聚餐聚了也散了，课程再没有了。想要再安安静静地坐在教室里听一堂课，也许已不再可能，室友同窗一个个整理好行囊离校而去，为了工作，为了生活，为了自己的将来各奔天涯，各奔前程。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　想起四年里，我们闹过，笑过，憋屈过的点点滴滴，原来那是我们美好的时光，一切恍若人世，顷刻间涌到眼前而又顷刻间飘远，来回往复。曾经不太在乎的人和事，即使当初彼此没说过多少话，没有多少相同的言语，甚至没有打过照面，没有同时毕业，可一切都在一瞬间让我们感到弥足珍贵，毕竟我们已有四年的同窗之谊和离校后的校友之情。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　一个一个地送他们离去，一路无话，一路沉默，一路看着，一路听着，半抹余阳，心照不宣。周围的热闹与喧嚣，更突兀地让心里莫名地徒增伤感，心里的落差，天南地北难以言说，只能硬生生地挤出几抹别扭的微笑挂在脸庞，握手拥抱，互祝“安好”，可看着他们远去的背影，车窗里彼此眼里噙着的泪水，强忍着就差冲破心底最后的一道防线夺眶而出，招手作别，列车离去，心情终究还是崩溃了。也许，三五年我们再相聚，也许有的人我们天涯两隔一生从此难再相见。想到这里，心底莫名地抽痛起来。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　送走他们，独自一人默默地往回走，身体与灵魂已不在同一防线，内心的凄楚与离愁一点一点地跃上心头。拖着落寞的躯壳独自一人回到曾经熟悉的校园，校园里，学弟学妹照常热闹着，照常忙碌着，这一切都是毕业前的狂欢，一如昨天对未来不太着急的我们自己，我看到了那时我们的影子就在他们的身上若隐若现，一切仿佛什么都从未发生过一样，可一切确切已然发生，我们已然疼痛过，这一切真实地在我们的心底深处留下鲜红的痕迹。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　深一脚浅一脚地挪回曾经温馨的寝室，熟悉的人却已不再，曾经热闹的空间早已被冷清覆盖得令人胆寒，那些收拾得干干净净的空床位，赫然醒目，心底最后的一丝坚强落荒而逃。忆起往昔的点点滴滴，孤独和冷清的气息侵蚀着身体的每一根神经和每一个毛细血孔，悲怆之感不言而喻，满心的离愁别绪在所难逃，有同学朋友离去的不舍之情，也有为自己明天四处奔波为了生活找寻工作的压迫之感和深深的担忧之愁。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　空荡荡的房间，一如自己空空如也的心房，平时热闹惯了，没觉得人生有多么的突兀和不寻常，所有人真正地离开，才觉得原来我们在一起已经四年，时光过得真快，转眼四年已如指尖沙，随风扬灭，或许是因为之前看不到离别，连同时光也走的慢慢悠悠。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　成长带给我们的疼痛越来越清晰可见，离别不仅仅是说说而已，有时足以让人撕心裂肺。忧思在我的心里慢慢地平静下去，正如暮色降临在寂静的山林之中。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　守着夜，一直想找个时间，好好看看所谓的花开不败和一起度过的锦绣年华，搁浅在时空里的怀念，累积成了今日今时的匆匆别离，淡化成了一纸云烟扶摇直上万里长空。未断的思念焚烧了离别的愁绪，夜来的太仓促，街头的风来来往往，恍恍惚惚就无声无息地刮过了四年大学时光。</p><p style=\"font-family: Simsun; font-size: 14px; line-height: 21px; white-space: normal; widows: 1;\">　　曲水流觞，沧海踏歌，唱一段一羽清尘，直到曲终人散。站在街头迎风的位置，我感到怅然若失，希望离别后的我们一切安好！</p><p><br/></p>', '曲终人散', '曲终人散', '0', '20', '12', null, '满天星', '2018-02-28 15:41:57', '2018-04-28 16:03:16', '0', '1', '1');
INSERT INTO `article` VALUES ('12', '凡事何必都看淡，自己理解就很好', '7', '凡事', 'admin/Upload/20180301/c9fe9f80af404b39dd87f7834dc2e6ab.jpg', '<p>&nbsp;&nbsp;凡事何必都看淡，自己理解就很好，决定我们一生的，是我们的选择。我们时刻都在失去，我们努力的想去拥有更多的精彩，可惜只有两只手，所以必须学会选择，学会放弃。要清楚哪些是我们不需要的，如果心的欲望太大，什么都想抓，可能最后什么也抓不牢。只有学会放弃，才能更好地持有。无须强求，最美好的总会在不经意间出现。每天都是改变生活的机会。努力经营，往渴望的生活方向前进。为自己相信的事情，艰苦奋斗；心怀远大，藐视忧虑。量力而行，不用超负荷；只要每天都有进步。还有，在没有做之前，你要决定是否去做，而不是找借口，并且要学习新知识，越来越接近自己的目标。你看到什么样的世界，就拥有什么样的内心。岁月弄人，年轻时，有些事你无法懂；懂得时，年轻已是不复来。</p><p>　　昨天若是都过去了，那还有什么过不去的明天。有人说，生活是一种享受；有人说，生活是一种无奈。其实，生活有享受也有无奈，有欣慰也有困惑。生活就象一枚青果，你含在嘴里慢慢品，细细嚼，便有诸多滋味在你舌尖蔓延，也甜，也酸，也苦，也涩，人生有两种境界，一种是痛而不言，另一种是笑而不语。当我们习惯沉默，原来是为了躲避失望。放弃可以说是件痛苦的事，因为对生活寄予太多的渴望，对未来秉持长久的梦想，许多东西不能割舍。但是，生活有时是低谷，深渊，过多的负重，可能加速坠落，有些人生与自我，既可用来战胜，更是用来相处的，命运的精彩，不只在轰烈之间，那些平淡与安静，亦是心灵的另一种力量。此生不长，唯有珍爱，才不会错过那些美与可爱，让遗憾尽量少些。</p><p>　　青春，注定了要颠簸，要有眼泪和汗水，有委屈、不甘和失败。我为自己这样感到委屈，但是看看周围，想想，也许这正是最正确的生活，这样走的才会踏实。无论是鸡飞狗跳，还是躁动不安，青春都不属于安逸，安逸是一潭死水，跳进去就出不来。总有些事，管你愿不愿意，它都要发生，人之所以会烦恼，就是记性太好。人之所以会痛苦，就是追求的太多。人之所以不快乐，就是计较的太多。人之所以不幸福，就是没有知足心。人之所以活的累，就是想的太多。人，永远是矛盾的主体，经常处在犹豫和憧憬的困惑中，夹在世俗的单行道上，走不远，也回不去。面对恐惧，擦干眼泪，每一天醒来都要比昨天更坚强。有些事，实在办不到，就算了吧，千万别为难自己。忍一忍风平浪静、退一步海阔天空。</p><p>　　人要知足就快乐，心要简单就幸福，生活有时会是困苦的，但也一定要高扬起头，逃避的不一定躲的过，面对的不一定最难过，孤单的不一定不快乐，得到的不一定能长久，失去的不一定不再拥有。人越往上走，心应该越能往下沉，心里踏实了，脚下的路才能走得安稳。每天给自己一个希望，试着不为明天而烦恼，不为昨天而叹息，只为今天更美好。幸福不是给别人看的，与别人怎样说无关，重要的是你自己心中充满快乐的阳光，幸福掌握在自己手中，而不是在别人眼中幸福是一种感觉，这种感觉应该是愉快的，使人心情舒畅，甜蜜快乐的。原谅自己的不足，原谅自己犯下的错，重新出发吧，往前走的不仅是时间，也该是我们自己。人没有完美，幸福没有一百分，不能拥有那么多，你又何必要求那么多。</p><p>　　岁月悠悠过一生，红尘淡淡又一年，人的一生，其实所需要的并不多，稳稳的心安，稳稳的幸福，就是满足。人生在世都不容易，懂得珍惜该珍惜的一切，懂得放弃一切应该放弃的，懂得把握一切应该把握的，懂得该享受享受的一切。让自己这辈子，过得充实，快乐，健康。珍惜自己身边的一切吧！人生短短几十年，时间很快就过去了，不要错过后，再来后悔；不要失去后，才知道要珍惜；不要让自己的一生，过得充满遗憾！人的一生，难免都会有遗憾。其实没有遗憾的人生，就不叫人生。只要脚还站在地上，就别把自己看得太轻；只要你还活在世上，就别把自己看得太重。只有放得下，才能拿得起。多一些宽容，多一些大度，挥挥手，笑一笑，一切的不愉快都会成为过去，幸福在与简单，生活就会自足。</p><p>　　生活，那就是面对现实，人生，就是越过障碍，自己身上有一架天平，在那上面衡量善与恶。生活，就是有正义感有真理有理智，就是始终不渝诚实不欺表里如一心智纯正。路是自己走出来的，没有什么后悔的理由。每个人在成长中都会受到很多伤，会哭泣，会悲伤，会觉得疼痛。而疼过之后，你就是一个全新的自己了。你疼过，便懂得了；你跨越过，便成熟了；总是要失去了什么，才学会珍惜什么；才会学会改变什么，放弃什么。生活都是有喜有悲的，没有必要羡慕别人而增添自己的烦恼。不是不追求，只是不强求。淡然过着自己的生活，不要轰轰烈烈，只求安安心心。多一点快乐，少一点烦恼。你不论钱多钱少，只要每天开心就好，人生百味有品味，百味人生事事真，凡事何必都看淡，自己开心就很好。</p><p><br/></p>', '凡事', '凡事', '0', '20', '13', null, '满天星', '2018-02-28 15:41:57', '2018-04-28 16:03:16', '0', '1', '1');
INSERT INTO `article` VALUES ('13', '浮生若梦一场', '8', '励志文章', 'admin/Upload/20180329/2be6e025167f9541bb53dc28f6f73d92.jpg', '<ul style=\"padding: 0px 0px 20px; overflow: hidden; color: rgb(102, 102, 102); font-family: &quot;Microsoft Yahei&quot;; font-size: 18px; white-space: normal;\" class=\" list-paddingleft-2\"><li><p style=\"text-align: center;\">浮生若梦一场</p></li><li><p style=\"text-align: center;\">念你成了忆时的泪</p></li><li><p style=\"text-align: center;\">在心灵种花，人生才不会荒芜</p></li><li><p style=\"text-align: center;\">春</p></li><li><p style=\"text-align: center;\">期待春天</p></li><li><p style=\"text-align: center;\">只有一个人的夕阳</p></li><li><p style=\"text-align: center;\">春风十里杏花新</p></li><li><p style=\"text-align: center;\">蓦然间，与春天撞个满怀</p></li><li><p style=\"text-align: center;\">做自己的朝圣者，仰望月亮</p></li><li><p style=\"text-align: center;\">从诗歌中走出来的民族魂</p></li><li><p style=\"text-align: center;\">讽刺的时代</p></li><li><p style=\"text-align: center;\">有缘再见，知心人</p></li><li><p style=\"text-align: center;\">让我数数你的好</p></li><li><p style=\"text-align: center;\">一念春风，一念阳光</p></li><li><p style=\"text-align: center;\">时光知味</p></li><li><p style=\"text-align: center;\">好想好想</p></li><li><p style=\"text-align: center;\">为君叶叶起清风</p></li><li><p style=\"text-align: center;\">站在山尖上</p></li><li><p style=\"text-align: center;\">若爱只是为了离别</p></li><li><p style=\"text-align: center;\">人生自是多情苦</p></li><li><p style=\"text-align: center;\">长安花开千万树，不解相思几多愁</p></li><li><p style=\"text-align: center;\">人生的院墙</p></li><li><p style=\"text-align: center;\">坐在黄河边</p></li><li><p style=\"text-align: center;\">烟雨三月觅春归</p></li><li><p style=\"text-align: center;\">仰望星空</p></li><li><p style=\"text-align: center;\">一个男人的宣言</p></li><li><p style=\"text-align: center;\">生命中的一株树</p></li><li><p style=\"text-align: center;\">最大的宽慰，莫过于善良</p></li><li><p style=\"text-align: center;\">生命的风雨都是掌声</p></li><li><p style=\"text-align: center;\">遥望来时路漫漫，他乡归来是何时</p></li><li><p style=\"text-align: center;\">走进中国文学史（一）</p></li><li><p style=\"text-align: center;\">记忆的芬芳</p></li><li><p style=\"text-align: center;\">小小的年纪，小小的幸福</p></li><li><p style=\"text-align: center;\">再见，青春</p></li><li><p style=\"text-align: center;\">命里有时终须有，无时莫强求</p></li><li><p style=\"text-align: center;\">未知的未来</p></li><li><p style=\"text-align: center;\">微笑向暖，安之若素</p></li><li><p style=\"text-align: center;\">云龙湖初春行</p></li><li><p style=\"text-align: center;\">给我一场痛彻心扉的深醉</p></li><li><p style=\"text-align: center;\">心城</p></li><li><p style=\"text-align: center;\">每天的阅读和写作，让我与自己的世界和解</p></li><li><p style=\"text-align: center;\">把鲜花送给自己</p></li><li><p style=\"text-align: center;\">春恋</p></li><li><p style=\"text-align: center;\">外面的世界刚刚好</p></li><li><p style=\"text-align: center;\">守望者</p></li><li><p style=\"text-align: center;\">一灵魂一世界</p></li><li><p style=\"text-align: center;\">飞翔的雏鸟</p></li><li><p style=\"text-align: center;\">春暖心，心海无限</p></li><li><p style=\"text-align: center;\">一别两宽，各生欢喜</p></li><li><p style=\"text-align: center;\">树有年轮，人要心善</p></li><li><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20180329/1522288644158486.jpg\" title=\"1522288644158486.jpg\"/></p></li><li><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20180329/1522288644106977.jpg\" title=\"1522288644106977.jpg\"/></p></li><li><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20180329/1522288644737822.jpg\" title=\"1522288644737822.jpg\"/></p></li><li><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20180329/1522288644126719.jpg\" title=\"1522288644126719.jpg\"/></p></li><li><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20180329/1522288644995755.jpg\" title=\"1522288644995755.jpg\"/></p></li></ul>', '梦', '', '0', '20', '62', null, '满天星', '2018-03-27 17:17:17', '2018-04-28 16:03:16', '1', '1', '1');
INSERT INTO `article` VALUES ('14', '与其嫉妒别人，不如经营自己', '12', '经营', 'admin/Upload/20180418/1b7c5b6f38df76421c2abc48e1c54a44.jpg', '<p>&nbsp;&nbsp;宇宙无边无际，世界瞬息万变，无时无刻不在演绎着天外有天，山外有山，人外有人的强弱变更法则。由此，产生了人与人之间的嫉妒。“同行如敌国” 、“文人相轻” 正是这一现象的诠释。</p><p>　　嫉妒，是人与生俱来的一种心理本能。当人们拥有相似的生活背景，教育背景，社会背景时，就容易产生同等水平必须有同等运势、同等成就的逻辑思维。而现实却并非如此。</p><p>　　我们常常因为看到他人的外貌、才气、家境、荣誉、地位等因素都处在高于自己的优势时，感到烦躁不安，怨天尤人，甚至心生忌恨，用一句流行语来说，叫“羡慕嫉妒恨”。羡慕：看到别人有某种特长或好处，也希望自己拥有； 嫉妒：对才能、名誉、地位或境遇比自己好的人，心怀怨气；恨：仇视、敌对。这是一个循序渐进的过程。因比，嫉妒可分为良性嫉妒和恶性嫉妒两种。</p><p>　　心胸宽阔的、有自知之明的人，会理性地控制自己的情绪，从别人的优秀中反思自己的不足或差距，并以此作为提高自己的动力，为自己的人生画卷添上一笔自尊自强的色彩。此为良性嫉妒，于人于己都有好处。从另一个角度看，良性嫉妒还能促进良性的竞争，推动社会不断进步。</p><p>　　心胸狭隘的、迷失自我的人，往往用主观臆断去评判客观事实。他只看到别人头上的光环，却看不到别人为之付出的努力。所以，他一味地怨天公不公平，恨神灵不相助，心里总有一个过不去的坎：为啥我就不如他？如此这般，扰乱了自己的心智，挫伤了自己的意志，还有可能做出极端行为伤害他人。此为恶性嫉妒，于人于己都不利。</p><p>　　恶性嫉妒的人是可怜可悲的，因为他在嫉妒别人的同时也在伤害着自己。他的内心长期处于焦虑、猜疑、自卑、怨恨、敌意的黑暗中，没有阳光照耀，没有心灵快乐。更糟糕的是，心理疾病会导致身体疾病，身体疾病有可能让人一切都归于零。真是得不偿失。</p><p>　　古人曰：“临渊羡鱼，不如退而结网。” 引伸到我们面对别人拥有的各方面条件都比自己优越时，所产生的心理不平衡，应该采取的态度是：与其嫉妒怨恨别人，不如认真经营自己。</p><p>　　那么，如何经营自己呢？</p><p>　　一、阅读好书。无论是纸质书，还是电子书都要读。</p><p>　　书中凝聚了前人的智慧与思想，我们可以从中获取人生的营养和力量，由此确立自己的人生目标，让自己的努力有方向，有路径，在人生途中开阔眼界，拓宽胸襟，学会反省，不再纠缠于嫉妒别人，而是致力于提升自己的才能，活出快乐的、真实的自己。</p><p>　　二、在相遇的人中寻找榜样，取人之长，补己之短。</p><p>　　他们可能是你的长辈，可能是你的同辈，也可能是你的晚辈，不同的人，有不同的优点，有不同的工作经验和生活经验。虚心学习别人的长处，借鉴别人的经验，有助于你的思想成熟和工作能力提高。</p><p>　　三、在大千世界中找到自已的合适定位。</p><p>　　面对人际关系，要学会沟通，学会换位思考，学会包容，学会做事。</p><p>　　面对自己的成长，要学会感恩，学会谦和，学会隐忍，学会敬畏。要善于发现真实的本我，要勇敢接纳挫折与失败。</p><p>　　首先，要知道上天赋予我们每个人的特质是不尽相同的，人与人之间的得失差异也是正常的。</p><p>　　我曾经与一位车贷公司的总经理聊天，他说到了员工进出跳槽的问题。</p><p>　　我说：“你们公司的福利待遇这么好，出去的人是不是很傻？”</p><p>　　“出去的人不傻，留下的人也不傻，只是兴趣爱好不同，择业观念不同而已。”他笑着说。</p><p>　　能这样看问题，足以证明了他的悟性。十多年前，他就进入了这个行业，因为爱好，干得开心，才有了他对工作的坚持； 因为坚持和能力配位，才有了他今天的职位。</p><p>　　这位总经理的经历是否可以给我们这么一个启示：我们在追逐目标的过程中，不仅要有努力奋斗的精神，还要有善于发现自己长处和短处的明智一一我的擅长是什么，适合干什么？我的短板是什么，不易干什么？做到心中有数，在职场上就可以扬长避短，充分发挥自己的优势潜能，工作效率自然事半功倍。</p><p>　　其次，人在一生的拼搏过程中，总会遇到比自己优秀的对手。此时，你应该告诉自己：不要羡慕别人的好运，因为你不知道别人前世修了多少行，积了多少德； 你也不知道别人今生做了多少不为人知，只有天知的善事； 你更不知道别人为了追求梦想，经历了多少艰难困苦。明白了这些，你就不会被嫉火焚烧，而是继续按自己的既定目标前进。</p><p>　　最后，在遭受挫败与打击之后，请不要抱怨自己怀才不遇，不要叹息自己生不逢时。崎岖人生路，谁都有跌入低谷的痛苦，而且不止一次。只要你坚守自己的目标，不气馁，不放弃，心无旁骛地继续奔驰向前，识才的伯乐就会发现你，好运就会与你不期而遇。</p><p>　　人的一生，说长不长，说短不短，只是路途充满着坎坷与荆棘，充满着我们无法预测的未知，所以，每个阶段该干什么，都应该有自己的设想。</p><p>　　人在成长初期，家庭长辈，学校师长会对自己有较大的影响，但最终决定自己人生走向的仍然是自己。</p><p>　　盛年不重来，一日难再晨。青春期，正是学习知识、积累人生经验的最佳时期，请年青朋友们不要沉溺于吃喝玩乐，也不要与别人攀比，而应该把握好自己的优势能力，去努力，去奋斗，去获取生命的精彩。</p><p><br/></p>', '经营', '经营', '0', '20', '14', null, '满天星', '2018-02-28 15:40:02', '2018-04-28 16:03:16', '0', '1', '1');
INSERT INTO `article` VALUES ('15', '从前', '12', '从前', 'admin/Upload/20180418/0d329bd13c22f6577b9f0a4bcfedb093.jpg', '<p>&nbsp;&nbsp;某年、某月、某日，晴，我在从前等你……</p><p>　　时光如梦，恍然离下；朝花夕拾，尽是枯萎。从前，我爱你！</p><p>　　庄严的殿堂，洁白的婚纱，新郎绅士的将戒指戴在了新娘的无名指上，俯身亲吻了新娘的额头，随后道出一句：我爱你。新娘脸上幸福洋溢，露出两个浅浅的酒窝。这个画面经常出现在我的梦里。</p><p>　　那年那个酷热的夏天，我考上了省城的高中，家里父母忙于工作，抽不出时间送我到学校，我自己一人提着两大包行李坐车来到学校门口，当我把行李提下车时，我才发现自己第一次来这，根本不知道要去哪，而且还有两包行李，顿时让我有些不知所措。</p><p>　　“喂！同学，需要帮忙么？”。阳光下一张帅气的面孔进入了我的视线</p><p>　　在一个陌生的地方，遇到了一个陌生的男孩，我本应该警惕的，但看到他身上的校服，让我立即消除的警惕出于礼貌，我屡了屡额前的刘海回答道：“我是新生，第一次来这里，请问我该去哪里报道？”</p><p>　　他提起我的两包行李，走吧，跟着我，带你去报道，顺便在帮你把行李送到宿舍。</p><p>　　之后他带着我报道，然后帮我把行李提到宿舍楼下。“你自己提上去吧，男生不能进女生宿舍”。他有些不好意思道。</p><p>　　这是我们第一次见面，我们交往后，我还问过他，当时为什么会来帮我。</p><p>　　那年那个酷热的夏天，马路旁站着一个身穿白色连衣裙的女孩，精致的花边衬出白皙的双腿，修长挺拔，玲珑的曲线完完全全的勾勒了出来，女孩一脸无助，这一幕出现在他眼里，他便忍不住想要上去搭讪，可是却没有什么借口，当看到女孩身旁的行李时，他便直直的朝女孩走去。</p><p>　　喂！同学，需要帮忙么……</p><p>　　时隔多年，每当我想起那个夏天，嘴角都洋溢起幸福的微笑。</p><p>　　我们两个在一所学校，虽然不在一个班，但是我们每天都能遇到。不知是他有意还是无意，每天放学我总是在同样的地方看到他，他看到我总会给我一个特别阳光的微笑，同样，我也会给还他一个灿烂的微笑。时间久了，我也养成了习惯，每天放学，就算不用从那条路走，我也会绕路过去，同样，每次过去都能得到他一个特别阳光的微笑。</p><p>　　就这样，不知不觉我们都上了高三，没有了高一高二那种轻松的学习环境，还有几个月就要面临高考，所有人都开始奋发图强。放学，几乎所有人都是拿起书，飞快的冲回宿舍继续学习。而我，仍然每天都会绕路过去跟他见面，几年不变的是他还是会给我一个特别阳光的微笑，同样，我会还他一个灿烂的微笑，每次除了远远地微笑，我们不会说一句话。有几次我想上去跟他说话，可他冲我微笑后，就转身离开了。</p><p>　　突然，有一天放学，我还是照常去与他碰面，可是却没看到他，我以为他有什么重要的事，可能迟到了，可是我等到宿舍快要关门了，他还是没有出现，那天我特别失落的回到宿舍。</p><p>　　接下来一个多星期，我都没有再看到他，仿佛他突然就人间蒸发了，最后我开始慌了，终于我忍不住去他班里问他同学。得到的却是他一个多星期前就请假了，再也没有其他消息。</p><p>　　高三紧张的学习氛围让我无法抽出时间去想他，可是每天放学，我还是会去那条路等他。</p><p>　　高考前一个月，还是那条路，我看到了那个熟悉的身影，我再也忍不住跑了过去，责怪的问道：“这么久你去哪了，我每天可都在这里等你。”</p><p>　　“对不起！”他开口道：“做我女朋友吧？”</p><p>　　我想也没想便答：“好啊！”</p><p>　　他突然伸手捧着我的脸俯身吻在我的额头上，我出奇的没有阻止他。</p><p>　　那年，我们18岁。</p><p>　　高考过后，我如愿考上了自己理想的大学，而他却连最普通的大学也没考上。</p><p>　　去大学的那天，他帮我提着行李，送我到车站，上车前，他猛然抱住我，我也紧紧抱着他。</p><p>　　汽车发动，夕阳下，他冲着车内的我又发出了一个特别阳光的微笑，同时，我也冲塔发出了一个灿烂的微笑。他猛然喊道：“等我一个月，一个月后，我去找你！”</p><p>　　我听到后，眼角忍不住划过一丝眼泪。</p><p>　　一个月后的一天，我因为要出校办点事，刚刚走到大门口，便看到屹立在校门外马路边那道熟悉的身影，我迅速朝他跑去，狠狠抱住他。</p><p>　　“傻瓜，抱这么紧，还怕我跑掉啊！”他轻轻在我耳边说道</p><p>　　“笨蛋，我想你了。”我不争气的眼泪再次划过脸颊。</p><p>　　“嫁给我，你愿意吗？”他认真的问道。</p><p>　　“嗯，好！”我擦了擦眼角的泪水含糊不清的道出两个字。</p><p>　　那年，我们19岁。</p><p>　　不久后，他在我学校附近找了个工作，还租了个房子，时不时的做些好吃的送到学校给我吃，朋友们都特别羡慕我有一个这么好的男朋友。还让我看紧点，不然小心被她们抢走，为此我还担心了一阵子，可发现，每次他到学校来给自己送好吃的时候，从来不会看别的女生一眼，只是认真的看着我吃完，然后跟我聊聊天，就会离开学校回到他的出租屋。我就慢慢放心了。</p><p>　　两年后，我上大三了，也开始在外面实习，本来准备自己租个房子，可是他告诉我让我去他那里住，我想了一下，便欣然答应。</p><p>　　一间简易的屋子，两个房间，一张桌子，两把椅子，一台旧电视机和一些生活用具。本以为男生的房间都很乱，本来简易的屋子里却收拾的干干净净。我跟他一人一个房间，每天他出去工作，我出去实习，晚上回来，他就会给我做些好吃的。生活虽然简单，但却无比幸福。</p><p>　　一天傍晚，下着大雨，电闪雷鸣，我实习完回到出租屋，刚刚打开门一阵浓密酒气扑面而来，我差点被熏晕，往日干净整洁的屋里，到处都是啤酒易拉罐，看着他房门开着，不断有酒气传出，我慌忙的跑进他的房间，眼前的一幕，让我伤心，看着往日那个阳光帅气的大男孩，现在满身酒气，躺在满是易拉罐的地上，帅气的面孔上沾满了泪水和酒水，他为什么会如此痛苦？当我看到他床头上的一张黑白照片，照片中一男一女，男的跟他长的很像，照片还放着一份遗产证明书。</p><p>　　我总算知道，高考前，他为什么会突然消失。那一天，他父母开车出差，路上遇到车祸，双双遇难。</p><p>　　我突然感觉很心碎，发生这样的事，他竟然一直没告诉我，一直一个人默默的承受着。看着此时他，我心里很生气同时也非常担心。我想将他扶到床上，他也很配合地让我把他扶到床上。</p><p>　　我将满屋子的易拉罐收拾干净后，打了一盆热水，认真地擦着他脸上的泪痕，他全程一直默默地看着我，将他脸擦干净后，我转身准备离开，他一把拉住我，然后他的嘴狠狠的堵住了我的嘴。我稍作挣扎，便也任由他去。</p><p>　　外面下着大雨，电闪雷鸣，出租屋内，我们相互索取着……</p><p>　　那年，我们21岁。</p><p>　　从那以后，我们就住到了一间房，每天他出去工作，我出去实习，生活照旧，每天我们都很幸福。</p><p>　　一年后，我工作稳定，但有一点不好，就是我被调到别的城市里去工作。</p><p>　　离开的时候，还是他送我到车站，同样拥抱，亲吻，我上车后，他给我一个特别阳光的微笑，同时，我也还给他一个灿烂的微笑。汽车启动后，他冲我大喊道：“毕业了，我就娶你。”我听到后泪流满面。</p><p>　　现在我怎么都想不到这一个微笑一个承诺对我来说将会是永恒的。</p><p>　　分隔两地，我们每天都会通电话，电话里大部分都是我在讲，他在听，他时不时会嘱咐我，让我好好照顾他未来的媳妇儿。为此，我也感觉特别幸福。</p><p>　　几个月后的一天，我跟他打电话，没人接，我以为他有事，便也不想扰打他，想想他事他应该会给我回电话的。可是我等到晚上，他却仍然没给我回电话，我有点气他的气了，便打算，他就算给自己回电话，自己也不接了。当做给他的惩罚。</p><p>　　几天后，我发现他居然这几天没有给我打电话，最终还是我忍不住再次拨打了他的电话。可是电话里面传出的却出你拨打的电话已停机。这时我心里面急坏了。</p><p>　　回到公司，我马上向领导请假，准备去一趟他所在的城市，但是领导说公司现在正是缺人的时候，一个月后才能准我的假。</p><p>　　十几天后，公司的会议上，一个陌生人的号打到我的手机上，在开会，我准备挂掉时，突然发现是他那个城市的号，我立马接听，然后向领导说了声：“对不起。”然后走出会议室。</p><p>　　“喂，请问你是哪位？”我心中有些激动</p><p>　　“喂”电话那边传出一个熟悉的声音。</p><p>　　我瞬间知道是他，“你什么情况，为什么一直不打我电话。”我大声说道。</p><p>　　“我们分手吧！”他沉声道。</p><p>　　“你说什么？”我感觉我听错了。</p><p>　　“我们分手吧！”他仍然沉声道。</p><p>　　“是不是，因为我工作的原因，那样我可以辞职，现在立马回去跟你结婚。”感觉他是认真的，我有些急了，眼中突然充满泪水。</p><p>　　“……记得照顾好自己”</p><p>　　我已经忘记了说话。</p><p>　　“你会一直想我吗？”</p><p>　　我还没说话便听到……嘟……嘟……电话那边传来了挂断的声音。</p><p>　　一瞬间，我的眼泪再也制止不住，花花的流了下来，我回到这边公司分的房子里，关上门，大声的哭了出来，算上今年，七年了，七年的感情，就这么破碎了，我不甘心，我要去找他。我要当面问清楚。</p><p>　　一个月后，公司准了我的假，我简单的收拾了一下，便踏上他所在的城市。</p><p>　　几天后，我到了他工作的地方，可是他已经不在这了，他的老板说，他早就辞职了，说是要回家准备结婚。</p><p>　　过了很久，我依然没有他的一点消息。直到有一天，又是哪个陌生的电话打来，我迅速接听，电话里传来一个妇人的声音。</p><p>　　妇人：“你是他的女朋友？”</p><p>　　我：“对，我是他女朋友”</p><p>　　“他在我这，你过来看看吧，唉！”妇人叹了口气报出了一个地址。</p><p>　　我发现那个地址就在我工作的城市，我迅速下楼，拦了一辆出租车，跟司机说了下位置。</p><p>　　几小时后，出租车，开到了一个小型公寓楼下，我下车，地址上说是三楼，我直接走进电梯，按了下‘三’。电梯缓缓上升。</p><p>　　‘咛’我走出电梯，迫不及待的朝一扇门走去。按了下门铃。</p><p>　　不久，一个中年妇人将门打开将我迎了进去。</p><p>　　进来后，我看到一个小型祭祀台，上面摆着水果，祭祀台上挂着一张黑白照片，照片里面的人脸上挂着特别阳光的微笑，而我盯着照片眼睛却再也无法移动。</p><p>　　孩子命苦，爸妈走的早，现在自己也走了。我翻看他遗物的时候，发现他手机里面只有一个电话号码，打电话，接电话，基本全是你。我是他的亲戚。中年妇人道。</p><p>　　“喂！同学，需要帮忙么？”。阳光下一张帅气的面孔进入了我的视线。</p><p>　　我屡了屡额前的刘海回答道：“我是新生，第一次来这里，请问我该去哪里报道？”</p><p>　　“对不起！”他开口道：“做我女朋友吧？”</p><p>　　我想也没想便答：“好啊！”</p><p>　　他突然伸手捧着我的脸俯身吻在我的额头上，我出奇的没有阻止他……，曾经的一幕幕全都出现在我眼前。这次我却没有哭。</p><p>　　“姑娘，来了，就在这吃个便饭吧。”中年妇人端上来三个小菜，两碗米饭对我道。</p><p>　　我盯着照片出神，突然有人跟我说话，我回过神来，见中年妇人饭菜都已经端上来了，我也没有拒绝。</p><p>　　跟中年妇人坐下后，一边吃一边把我跟他发生的事一件一件讲出来。全程我没有掉一滴眼泪。</p><p>　　饭后，我想中年妇人辞行。中年妇人将一个盒子递给我，说这里是他的遗物，让我好好保管。</p><p>　　下楼后，我打开盒子，看到里面厚厚的病例表，再也忍不住，眼泪破框而出，哭声撕心裂肺。</p><p>　　原来他……</p><p>　　我爱你，但我却不敢说，我说了怕我立马就会死去，我死后，我怕没有会比我更爱你。</p><p>　　某年、某月、某日，晴，我在从前等你……你在哪？</p><p><br/></p>', '从前', '从前', '0', '20', '15', null, '满天星', '2018-02-28 15:41:57', '2018-04-28 16:03:16', '0', '1', '1');
INSERT INTO `article` VALUES ('16', '等车', '12', '等车', 'admin/Upload/20180301/010b39c65e46176d4c2cffcb9426133e.jpg', '<p>&nbsp; &nbsp;等待永远是漫长的，看不到时间，看不清即将到来，在等着，等到焦灼。甚至有时自己都会恍惚，是在等即将到来，还是等到来时的那一刻欣喜，这份欣喜里藏着那不言而喻的激动与渴望。</p><p>　　 等待是不可思议的艺术。等车时，你能看到每个人最真切的表情，如果你不那么急切，你会发现，你像是一位简单的被心理学家。被心理学家是一件很有趣的现象，别人的言语，行为，表情，甚至是语气等，都会让你察觉到他们的心理活动。有不急不躁低头刷着手机或者和身边的人谈笑风生的，这些人一般是早到的，离出发时间还有一段时间的。也有撒腿狂跑或者步伐很急，言语催促的，其实我是比较喜欢这类人的，因为他们把时间计划的很紧凑，当然不乏那些真的时间概念真的不强的人，但又不得不承认，这种踩着点的人，也真是让人佩服的。人们总是有掌控欲的，心里对时间，对事情都会有一定的掂量和思考，总是喜欢左右除自身以外的种种，因为没有人不喜欢顺自己心意的。</p><p>　　 其实，我是一个讨厌等待的人，等待会让我觉得无聊，觉得孤独，觉得无所适从，特别是一个人的时候。我见过一个人等车时目光呆滞而空洞；也曾见过一个人低着头，脚掌不停地磨搓着地面；还见过一个人目光注视着车来的方向无声地叹着气；最多见的还是一个人低着头默默地玩着手机……他们是他们，可有时也是我。</p><p>　　 当人们越来越依赖外物的时候，是真的孤独了。而等待会放大这种孤独，此时你渴望着手边的手机铃声响起，渴望着身边站着一个会和你聊天的人，渴望着眼睛可看到之处，有一个能吸引你的场景……有时为了缓解这种孤独的尴尬，也想要静静地发个呆，可是，只有自己知道，一个人在等待的时候，根本无法做到全心全意地漫长发呆。等要坐的车，等要见的人……有多少次等来的是无聊、无趣和无味，只有自己知道。等想坐的车，等想见的人……有多少次伴随着焦灼和孤独，但却心甘情愿。</p><p>　　 很久以前在一次等车的时候，我想到这样一句话“等待是痛苦的，被等待是幸福的”，但现在，觉得这句话得加上好几个前提才能成立。小时候的我非常的怕黑，当然现在也没好多少，有次在别人家玩的忘了时间，天黑了，一个人不敢回家，也不好意思留在别人家过夜，更不好意思开口让别家的大人送我回家，就一个人默默地坐在路口，渴望着有人来接我，不敢出声，默默地坐着，等着。时间一分一秒地过去，天也早已黑透，只有路旁的那户人家的门灯在亮着，心里越来越害怕，可是还没等到来接我的人，抬头望着四周，搜寻着熟悉的身影，那时真的有个人从不远处经过，很像父亲，可是因为胆小不敢喊出声，不敢跑到黑暗里，所以看着那个黑影，从我眼前走过再走过。我哭了，却不敢哭出声。也有一次，有个人向我表白，我拒绝了，他说可以等我，而我内心却觉得惶恐，觉得愧疚。原来，不是自己喜欢的、想要的，等与被等都是痛苦的。</p><p>　　 我不喜欢等待，更准确地说不喜欢等待时的那种煎熬和不知所措的孤独。很多人都喜欢说，成长就是学会忍受孤独，在我认为并不是这样，我觉得成长就是逐渐地知道怎样与自己相处，这或许也是另外一种说法，但很多时候我们自己都不了解自己，反而当局者迷旁观者清。大多数人都将眼光放在除自身以外的地方，就如同好多人买鞋子，看重了它们了漂亮，穿出去，一定能吸引别人的目光，而不管穿着它们是否舒服与合适。就是因为大多数的时候，我们将目光、心思放在除自身以外的地方，所以在等待时才会不知所措，觉得孤独和煎熬，因为我们很多人不习惯和自己相处，或者说是对和自己相处的一种逃避。</p><p>　　 等待中，假装着镇定，却乱了心跳。等来了，皆大欢喜；等不来……也要学着习惯；或许有一天会等到怦然心动，或许有一天也会等到心如止水。车，总会等来；人，总会有等不来；究根结底，只看愿不愿意与值不值得。</p><p>　　“嘀……”等的车来了，等的人不来了，是上车还是回头？</p><p><br/></p>', '等车', '等车', '0', '20', '16', null, '满天星', '2018-02-28 15:41:57', '2018-04-28 16:03:16', '0', '1', '1');
INSERT INTO `article` VALUES ('17', '缘起则聚，缘灭则散', '12', '缘起则聚，缘灭则散', 'admin/Upload/20180301/aee1ad7fc4600a9516b0bce8ddc3bb64.jpg', '<p>&nbsp;&nbsp;夜，结束了一天的喧嚣后安静下来，伴随着远处路灯那微弱的光。风，毫无预兆地席卷整片旷野，撩动人的思绪万千。星，遥遥地挂在天空之中，闪烁着它那微微星光，不如阳光般灿烂却如花儿般如痴如醉。</p><p>　　 ——题记</p><p>　　欲将沉醉换悲凉，清歌莫断肠。这混乱的尘世，究竟充斥了多少绝望和悲伤。你想去做一个勇敢的男子，为爱，为信仰，轰轰烈烈的奋斗一场。你周身充斥着无人可比的灵气和光芒。你有着与伟人比肩的才气和名声，你是那样高傲孤洁的男子。你的一寸狂心未说，已经几度黄昏雨。</p><p>　　曾经以为，相爱的人一定要相守，只有相守，情感才能长久。可是，此岸和彼岸只不过是空间的差距，却无法拉长心灵的距离。</p><p>　　时光荏苒，岁月无声。日子不紧不慢的如涓涓溪水静静的流去，而从身边流去的只有时光，沉淀下来的是与你一路相伴的幸福和快乐，温馨和安暖。于我，在这个凋零都感受到诗意横溢的秋，只想做一件事，拈一片绯红的枫叶，轻轻地刻上我的心语。对信仰，是我今生永不改变的主题！而后，幸福的寄往有你的那个城市。从此，在我心里，于我的生命里，轻握你许的安暖，静静地在岁月的彼岸，为你守候一世永恒！</p><p>　　一段情，反复的掂量，最后加深了岁月的绵长。一路追赶里，一路追忆里，最后得到的是什么，最后又失去的是什么。或许，只有我们在静思的时候才会明白，这路的追忆里，我们得到的快乐往往比痛苦要少。当相思成殇的时候，除了对月徒悲叹之外，什么也不曾抓到。</p><p>　　一直执着于生命的简约格调，只愿用一颗淡然的心看云卷云舒，看季节更迭。许多不合时节的事物，必然会被光阴遗落，就像曾经繁茂的秋叶，待生命枯竭，终将脱离叶脉的相系相牵。而我，一直在这里，抖落一身的负累，永恒以树的姿态站立于季风中，昂首向着那无边温暖的碧蓝晴空。</p><p>　　夜幕降临了，春雨柔柔的亲吻着薄如蝉翼的纱帘，有节奏的淅沥在窗棂上，更增添了无限的意念。意念中的我，在幸福和恬淡中，漫捻心弦化为若水般的轻柔曼妙在深情的雨夜里。此刻，窗外的雨不再是清冷的秋雨了，在我的眸里是一种柔软，似撒娇少女的情怀，是怜、是爱、是柔、是润在我的心里是一种憧憬，憧憬着一份美好的未来，与你相拥在花雨飘飞的时节，让爱情肆意的怒放在油纸伞下，青石边，丁香小巷这样的心境，这样的时光，这样的时刻，心不经意间便醉了、醉了、醉在这如曼妙轻盈舞步的秋雨中</p><p>　　晚霞破月的天际，依旧映红了归家的路途，流浪了好久的笔调写满了疲倦的素笺，伴着枯萎的憔悴，与年华斑驳的碎影相逢，溅起涟漪在心田的几缕游丝浅叹，让年华牵住执笔的手，静听潺潺流水；波动在逐流的心海，唤名为；婆娑年华，勾勒了谁的斑驳唯有情醉琉璃一宿，又是一个不眠的夜。</p><p>　　真正的相爱，是人在千里，却梦魂相依；真正的相爱，是岁月流转，却不离不弃；真正的相爱，是彼此付出，却无怨无悔。</p><p>　　樱花有单樱和双樱，她们绽放时满树灿烂，清香扑鼻，单樱白的如雪如云，双樱色彩如火似霞。但是无论是单樱还是双樱，她们盛开的时间都不长，二十多天的光景，开的绚丽多彩、满树烂漫，落得星星瓣瓣，匆匆忙忙。</p><p>　　深秋还远，徐徐的风吹着，却也有了几分萧瑟，春天，不仅有满天飘飞的花儿，还有到处弥散着花的幽香。随着秋韵渐渐浓郁起来，院子里的花便盛开了，整个院子里香气四溢，溢漫着甜丝丝的味儿。金灿的花儿一串串、一撮撮，重重叠叠簇涌着点缀在茂密的绿叶之间，温温暖暖象极了一个个孩子的笑脸，仿佛是给这温暖的春天注入了一道亮丽的风景。</p><p>　　指尖轻触玻窗，嗤嗤的响声，惊动了脆弱的心脏，一阵阵的酸楚，像浪潮般袭来，若果这样酸酸的痛可以代替撕心裂肺，那就让他长久点，这样时间会把我忘记，这样便可躲在这里，让那些软弱手舞足蹈，让那些脆弱和不堪拼命娱乐，让那颗紧绷的心，少少松弦。</p><p>　　曾过往，伊颜纯美无暇，如玉般璀璨，许多人像发现了财富，紧抱于怀，怜香般害怕失去。那时，遇见你的是洗礼过后的悔过者，只懂怜香，而不懂惜玉，再璀璨也掩盖不了他身上久积的灰尘，铸造不了你，也成就不了他，于是乎，迷糊坚固了戏剧化的情谊，疼只是简单的疼。</p><p>　　有人曾说，会白首不相离。可是终究一口空言，转眼，便抛之彼岸。可是，我却一直相信着。然后，在追逐的沿途，荆棘布满，却一直从未低头</p><p>　　原来有些等待是不值得的，那么多年换来的却是沧海桑田，我们只是沧海一粟，不能左右命运给予的奇迹与苦楚，面对些许，能做的也许只剩下了等待，可是等待却又是需要勇气，更要有勇气来承担与等待而来的结果大相径庭的结果。</p><p>　　思绪渺渺，发怀古之悲情，世间种种，诱愁之决堤，三山五岳，撼天地之威名，流水之昌吉，涓涓之柔情，不禁闲情满溢。</p><p>　　人生自是有情痴，此恨不关风与月。谁为谁真情缱绻？谁为谁痴傻疯狂？谁为谁望穿秋水？谁为谁痛彻心扉？都是你我解不开的白线团。无缘何生斯世，有情能累此生。</p><p>　　相思成为了很多人生命里的一种自我安慰，在月圆的时候无尽思念，在月缺的时候无限惆怅，想念着岁月里和情有关的所有悲欢离合。在情感的世界里，我们一直带着所有的铿锵前行，不管风雨肆虐，不管风和日丽。</p><p>　　染窗前。那堪清风曲径，不似甚似还满。份外湮留韶华，时节正乱红，空留余恨。淡眉醉眼，红妆轻粉，旧时依恋尘缘。只皓月朗朗，乾坤转，故国山川。次第红颜，疑是讴歌回畔。</p><p>　　生命中；总有太多的遗憾要留给回忆，年华里；屡不清的斑驳，总是勾勒了无数支离破碎的心伤。太多的执着所放不下，只是；那一份不屈的痛过，不期而遇的却是最美的意外。文字依旧可以华美朴实，年华不可唯美梦境，快乐不是一件不可奢侈的事情，忧伤，而往往是一度的颓废。</p><p>　　人生的路途，经历无数的驿站之后，总会出现陌生或熟悉的风景。时间所说的过客，只是注定走过的人和事，没有太多是刻意要去记住或淡忘的。铺就在黑白交错里的，无非就是忧伤曾穿越过的黑暗，在记忆的角落里，诉说了全部的待续。那些停驻在指尖的薄凉，是曾绽放在年华里的微碎。</p><p>　　指间年华，渲染着无暇斑驳的彩塑，悲伤的城池依旧提笔挥墨，画下四季风吹过的无痕。微笑掩饰了寂寞过的眼泪，是因为，在婆娑的年华里，聆听着没有人的相伴相知。那些了不断的往事，是梦绕在悲伤情愫深处，盈满心扉的最初和凝眸的叹息。终不过似水流年，清风凄语，唯独旧梦难拾。</p><p><br/></p>', '前端技术', '​   夜，结束了一天的喧嚣后安静下来，伴随着远处路灯那微弱的光。风，毫无预兆地席卷整片旷野，撩动人的思绪万千。星，遥遥地挂在天空之中，闪烁着它那微微星光，不如阳光般灿烂却如花儿般如痴如醉。', '0', '20', '18', null, '满天星', '2018-02-28 15:41:57', '2018-04-28 16:03:17', '0', '1', '1');
INSERT INTO `article` VALUES ('18', '一纸婚书，情感知多少', '12', '婚书', 'admin/Upload/20180301/c9fe9f80af404b39dd87f7834dc2e6ab.jpg', '<p>&nbsp; &nbsp;吃罢晚饭，正要收拾碗筷，楼下突然传来激烈的争吵声。仔细听听，原来是两口子准备离婚。为孩子，房子，车子和存款达不成最终分割的统一意见而大战马路。丝毫不顾及路人的眼光和哭的撕心裂肺的孩子。</p><p>　　 离婚，在这个物欲日益膨胀的社会，已经不是什么新鲜事。离婚的理由也是五花八门，只要婚姻中有一方，想要退出婚姻的围城，围城的墙壁上随时都可以找到一个可以钻出去的洞口。一个芝麻绿豆的事态，也会被人用心良苦的大肆宣扬，无限的扩大，直到另一方妥协，被吹大的肥皂泡泡才会破灭，才会息事宁人。</p><p>　　 无形的洪荒之力，把应有的责任心冲打的溃不成军，霓虹灯飘渺的光影下，一颗心越来越虚浮。忍耐的底线越来越高，包容的尺度越来越小，关怀的甜点越来越少。越来越多的与时俱进的学会了自私自利的享受，把自己高高在上的放在心中无可替代的位置。爱，成了一个人的独角戏，不再分享与共。</p><p>　　 庄严神圣的大红色结婚证书，渐渐的褪去了它的终身制和不可侵犯的尊严，变成了可以随意调换的商品，不再用“从一而终”的心态去呵护它，守卫它。闪婚，闪离，踏着快节奏的生活旋律，走着快节奏的步伐，新生代的名词在一闪过后，铺天盖地的流行起来。</p><p>　　 一只刚出炉的面包，在适宜的温度下有三天的寿命，若放在高温潮湿的地方，能隔夜不发霉，就很不错了。采摘下一只健康的苹果，在妥善的保存下要半年才能脱水干瘪或者是腐烂。若放在桌上置若罔闻，顶多采摘下来一个月，外观就让人不忍直视。它们寿命的长短，不在乎是否劣质，而是没有正确的，稳妥的，用心的去呵护。</p><p>　　&nbsp;爱情也一样，需要妥善的保管才能让它的生命力发挥到极致。携手走入婚姻围城的俩个人，不管初遇时的心动多么激烈，若是不好好的用心供养爱情的玫瑰花园，保鲜期会提前，会在凋零之后发霉腐烂。不管贫富与否，爱情里从来不缺少浪漫，缺少的是把激情正确的转化为亲情的方向，让两颗心，不仅仅是你装着我，我装着你，还默契的装着我们对未来的打算和珍藏一路共有的记忆。</p><p>　　 婚姻，不是用来捆绑感情的，而是责任的升级。有感情的人不一定非要结婚，但结了婚就一定要负起责任。组建起这个家庭的男人和女人，不再是单纯的个体，而是既能自由活动又能互相约束的结合体。同在一个屋檐下，共同打造一个家，而这个“家”质量的好坏，能不能长久的使用，和遭遇风雨时能不能得到妥善的维护，是要两个人齐心协力去付出的。</p><p>　　 好的婚姻，都是用心经营出来的。一份呵护打造一份和谐，一份容忍收获一份安稳，一份付出得到一份明媚。经营的阳光和绚，鸟语花香的家，不管涉入尘世的心有多疲惫，迈进家门的那一刻，轻松宜人的氛围都会把心灵上的尘埃轻轻的拂去。一句暖心的话，一杯喜欢的茶，在日复一日的简单中，让纯真的感情愈发的浓厚。</p><p>　　&nbsp;温暖心灵的不是暖气，拂去烦躁的不是空调，而是一份懂得与体贴。能够拴住一颗心的不是容貌与身材，也不是地位与身价，而是另一颗心的内涵。两情相悦，四壁透风的草房，也会被内在的幸福填满。家是心灵的港湾，是灵魂栖息的地方。不需要奢华，却一定要舒适。</p><p>　　 婚姻的灵魂是感情，一个没有好好维系感情的婚姻就是一个空壳，无论装潢多么的奢侈华丽，都会在风雨中不堪一击。每天，细腻的感受爱人的一颦一笑，适时的调节自己心情，转换自己的思想。用心的接受身边人的悲与欢，对与错的同时，也坦诚的对身边人敞开自己心扉，把自己的苦与乐，阴与晴用恰当的方式倾诉与爱人。</p><p>　　 交流，是让爱情长久不可或缺的方式之一。有些顾虑，说出来，心结会轻轻的打开。有些矛盾，说到明处，干戈就会化为玉帛。有些苦衷，说出来，紧蹙的眉头就会云淡风轻。爱人也是人，不会未卜先知，也不会能掐会算，有些话，不说，就失去了让TA懂得的机会。即便不懂，TA还可以问，你还可以解释。</p><p>　　 夫妻关系和任何一种关系都不一样。手足之情，朋友之情，都是真挚的，甚至我们可以拿命去换，可真的某天利益当头，还是会掂量下轻重，考虑一下是否可行。夫妻的利益不分孰重孰轻，一荣俱荣，一损俱损，没有彼此之分，没有胜败之说，确切的说，彼此之间的任何事态，都没有什么利益或是损失可以是单对某一方的。</p><p>　　 这样的两个人，没有理由不相互敞开心扉，掏出肺腑。在为事业打拼的空闲时，在烟熏火燎的罅隙里，背靠着背，对着一窗新月，把柔软的心敞开，听听爱人的心，也让爱人听听自己的心。用自己温柔的体贴缝补爱人闯荡在外留下的伤口，而自己在人际中的憋屈也让爱人用TA的细腻温情融化。</p><p>　　 最好的婚姻，不是郎才女貌，门当户对，而是无才无貌，门不当户不对的两个人，彼此在乎，彼此珍惜，彼此忍让，彼此奉献，因为心中真挚的爱，粗茶淡饭才会在唇齿间留香，粗布衣衫才能穿出眼中旖旎的风景。</p><p>　　 能够走进婚姻的殿堂，无外乎是一见倾心或是日久生情。只是那个当初怦然心动的人在柴米油盐中黯然了色彩。当青蛙王子天天为生活奔波的时候，他的帅气和体贴难免打些折扣；当白雪公主时时为生活操碎了心的时候，她的贤淑与温柔难免遗失些许。不要抱怨婚前与婚后的TA判若两人，能够改变对方的因素大部分是自己造成的。</p><p>　　 作为一个男人若能顶天立地的为爱人撑起一片晴空，用关怀和呵护让她无忧无虑如少女般生活在温馨的摇篮里，被爱情滋润的她会更加可爱，比婚前有过之而无不及。没有哪个女人愿意做怨妇，心情舒畅的女人会把锅碗瓢盆的协奏曲弹奏的很美妙，虽比不了天籁之音，却也能让人流连忘返。</p><p>　　 做为一个女人若能用一个贤妻良母的温婉为爱人解去后顾之忧，用体贴和善解人意让他能够放手自己的事业，被爱情浇灌的他会更加成熟稳重，用全身心去为家打拼。没有哪个男人愿意把家庭当作战场，温暖舒适的小窝会让他乐此不彼的为家人系上围裙，用一粥一饭诱惑爱人的味蕾，温暖爱人的心扉。</p><p>　　 生活的快节奏，社会的高强度压力，免不了会有心理上的压抑需要用发泄来缓解。而发泄的方式有很多种，有人酗酒，有人玩命工作，有人大发雷霆，喋喋不休的抱怨，只有少数明智的人，才会对着爱人，娓娓道出自己的感觉。甘苦与共的另一半，也有自己的独立的思想，有时候TA会无法接受你的缓解的方式。勺子碰到锅沿时，恰当的退一步，给枕边人一份包容，一份体谅和一分钟的解释。</p><p>　　 细腻的爱情会擦去在尘世中跋涉而蒙尘的心，会修复在风吹雨打中道道伤痕，会营养在外应酬疲惫而枯燥的心。好的爱情是婚姻的润滑剂，在不断的注入下，婚姻会永远的和谐美好。“百年修得同船渡，千年修得共枕眠。”虽然我不相信前世来生的说法，相信人只是个一次性的肉体，用完之后，就会回归自然的垃圾场。但在千千万万的人之中，能够遇见真的不容易，应该用生命珍惜这份来之不易的缘分。</p><p>　　 两个人在一起，同在一个屋檐下，并驾齐驱，没有高低贵贱之分，没有聪明笨傻之分。在日久的相濡以沫里，习惯慢慢融合，性格慢慢互补，爱的激情慢慢升华成血脉相连的亲情。爱与情，不仅仅是左手牵右手的浪漫，更是息息相通的心与肺；不仅仅是心甘情愿的做另一半的拐杖，更愿意做TA的眼，TA的嘴，TA的一切的一切……</p><p><br/></p>', '婚书', '婚书', '0', '20', '19', null, '满天星', '2018-02-28 15:41:57', '2018-04-28 16:03:17', '0', '1', '1');
INSERT INTO `article` VALUES ('19', '当一败涂地时，我们要靠盲目乐观的活着', '7', '乐观', 'admin/Upload/20180418/8b0e45a690f31109991511e1d3025294.jpg', '<p>&nbsp;&nbsp;曾有人说过，要让这世界充满欢笑，这是一个很美的目标，事实上，现实社会也确实处处充满着笑，可是这里的笑不仅仅有欢乐的笑，善意的笑，也有恶意的嘲笑讥笑耻笑，这个就让说这话的人很难为情了。</p><p>　　高中的时候，班上有一位同学家境很不好，但学习很认真，父母对他寄于厚望，希望他能考上大学找个好工作，改变自己和家庭命运，但是一到高考就紧张，总是发挥失常，考了三年都没能进大学，这时村子里的人就开始讥笑他们家，就一个鸡窝还想变成个凤凰不成。</p><p>　　父母觉得抬不起头来，为了供他读书已经借不到钱了，他也很沮丧，面对这一阵阵的冷嘲热讽，他几个月不说一句话，到了开学的前一天，他对父母说：还想再读一年，这一次没考上，就不上学了。</p><p>　　最后，父母还是选择支持了他，经过这一年的努力学习，总结失败教训，他终于考上了梦寐以求的大学，拿到通知书的那一刻，他请我吃了一顿饭。席间，他几次差点落泪，他说：“复读了四年，你知道吗？我是靠盲目自信支持走过来……</p><p>　　我确信他说的是真的，因为他的情况我是了解的，前三次的失败，引来的是一波更比一波大的嘲笑讥笑，如果没有强大的内心，一般人就会止步向前，而走向那些普通的大多数，做一个泥瓦匠，水电工，工地小工，而当初嘲笑他的那些人实际上也是正这些的普通阶层。</p><p>　　人一旦长大，就会被分为大多数和少数，大多数人多势众，似乎天生就是对的，于是乎，大多数对少数的嘲笑讥笑就成了一种特权。可是，问题大多数有时并不是对的，少数人往往是对的，不是有句话吗？真理往往掌握在少数人手里。</p><p>　　那么，当少数人被大多数人嘲讽伤害时，内心的痛苦肯定是少不了的，但是他们坚持认为自己是对的，并为此抵抗住压力，负重前行，这里面是有一种乐观精神的，正是这一种精神支撑着一直向前。</p><p>　　含着泪也在奔跑的是让人尊敬的，他们是真正的勇者，正因为别人的嘲讽，在他们心里更坚定了信心和信念。他看到了嘲讽他的人还在原地踏步，他看到了那些人红尘醉歌，不思进取，日复一日的重复着不变的平凡。</p><p>　　人生旅途中，注定有一段旅程要一个人走。《叮当猫伴我同行》里的大雄长大了，于是叮当猫就走了。大雄哭着说：你为什么要离开我啊？</p><p>　　叮当猫说：总有些路要你一个走</p><p>　　看到这里，很多人不禁落泪了。是啊，其实每一个人都是孤独的，当我们在父母的襁褓里，是体会不到孤独的含义的，当我们的长大了，走进了社会，就时常觉得孤独。对待孤独来袭，有一些人会和一群人把酒言欢，高唱纵情，也有一些人是直面内心的孤独，宁静的看书学习，或者听曲。这样的结果是，第一种人成了长不大的大多数，而后一种内在却不断成长提升。是啊，孤独带给我们的真正含义是要求我们成长。</p><p>　　多年以后，那位同学事业有成，家庭幸福，还拥有了一批真正的朋友，他们聚会的方式是茶室里，书吧里，谈书论道，不亦乐哉。现在，他们成了大多数，反观当初村里嘲笑他家的那一些人，还过着穷酸的日子，说着毫无素质的话，做着毫无技术含量的工作。</p><p>　　我不禁感叹，三十年河东，四十年河西。大多数和少数并无本质的区别，当初的大多数可能会变成少数，当初的当数也有可能变成大多数。当一个社会进步与否时，我们要看处于大多数的人会不会理解包容少数人，而不是嘲笑讥讽；我们要看残障车位是否堆满了杂物，盲人车道是不是停满了自行车，弱者是不是被同情，菜市场听到的是不是污言秽语，街道是不是到处都是烟蒂……</p><p><br/></p>', '乐观', '乐观', '0', '20', '19', null, '满天星', '2018-02-06 16:52:22', '2018-04-28 16:03:17', '0', '1', '1');
INSERT INTO `article` VALUES ('20', '路与人', '7', '路', 'admin/Upload/20180418/30cdf3ced0c520aaf2cdeb6e0502107a.jpg', '<p>　　有的路来回走多了，就成为一种习惯在心里。有种人相处久了，就自然走进到心里去。一条路上我和你，每次行走都欢乐带着忧郁。不是所有的人都会跑进心里去，不是所有的路都会熟悉，邂逅美里带诗意，分离痛里带哭泣。有些时候经历太多就会习惯坚强在每一天里。</p><p>　　心总是会有莫名的欢乐和悲泣，只为一瞬昙花的美丽而惊喜，只为一次受伤的无意，或多或少的感悟都会深藏在心里，路走多了就不怕坎坷和崎岖，阅人多了就不怕相聚和别离，人的一生有时候有的路还是要靠自己走下去，人的一生有些人还是要用心去忘记。尽管痛楚会一阵一阵蔓延在心里，为了梦想我们就要学会放弃。</p><p>　　人生沿途的风景尽收在眼里，一边走就一边有感受，有的路由于太坎坷崎岖就会消耗太多的力气，有的人虽然一直都住在你的心里，却未曾感觉到温暖和甜蜜，也许这就是上天安排的人生经历，也许这就是上天给足的面对勇气。</p><p>　　一条路一直延伸在眼睛里，一个人一旦有了泪，侧身的时候也能感觉到不畅的呼吸，世界上没有人随时都能够陪着你，也没有几个人能够真正懂得你，无论多疲惫路还要继续走下去，无论多受罪都要学会去整理。好的心情总会带来快乐和如意，好的心情总会让人淋浴阳光和呼吸到新空气。</p><p>　　当无路可走的时候并不是一生的结局，也许惊慌失措以后转身又见新天地，只要生命不息每天就会有欣喜，只要生命不息每天都必须坚强和努力。无论我们置身在哪里，都要做好自己。无论我们身在何方都要保持淡定别恐惧。人生无定数，许多事不一定是你想象那样的美丽，许多时候一条直路也会分叉在东西，所以习惯坚强了就没有不可以接受的风雨，所以习惯忍让了就没有一道坎跨不去。</p><p>　　路过的人早已经忘记，经过的事早已经随风而去，别让烦恼缠绕自己，别让忧伤成为哭泣，坚强的人不怕人生的风雨，乐观的人不怕面对怎样的结局，该来的躲不过，该走的就放弃，只要有个好心态，忧愁烦恼就会随风而去。</p><p>　　一直在走所以心里才有了太多的感受和委屈，一直在用心对待每事每人所以才知道&nbsp;珍惜和放弃，人生总有很多的选择题，当你倾尽所有的去努力，也许得到的只是淡淡的微笑而已，万事尽力就好，万事无愧于心就是最好的结局。</p><p>　　艰难行走在风雨里，风雨里才能感觉到成长的定义。努力向前，向前才能看到希望如太阳一样每天都会升起，只要用心去做自己，每天就会轻松快乐无比，只要用真爱，两心就一定容易贴近在一起。生命总是累不起也伤不起，所以你最好做个向善向阳的人，向善就会收获一份美丽，向阳就会感觉到一份自然和如意。</p><p>　　包容的心不是因为很大才把所有都包容进去，而是有了大的肚量才能容纳着所有，有的人有的事一旦难放进心里，就最好学会淡然放下。如果有些事让生命有了烦恼和忧虑，那么就要慢慢的学会清理，就像杂乱的房间一样，经过清理之后照样能够舒适和安逸。</p><p>　　荆棘密布的路上照样有阳光，只要阳光照到哪里，哪里就有希望欣喜。别把万事都堆积在心里，柔小的心根本放不下太多的东西，随着自然和简单把随意放进心里去，也许心里就会充满自然和轻松，也许心里就会流动着新鲜的空气。</p><p>　　如果爱是不得以，那么就好好爱下去，如果放弃能够感觉到轻松无比，那么再深的情也要清淡描写在书里，以免每次阅读的时候都哭泣，以免每次阅读的时候都难走出过去的阴影里。爱过就要收藏美丽，爱过就要懂得珍惜，人生的情缘不会在每一站都会等着你。</p><p>　　如果苦了就要多休息，如果累了就有多注意，如果烦了就有多整理，只有生命健康快乐在每一天里，每天的人生才有意义。千万别把爱恨栽进泥土里，走走停停要懂得放弃。</p><p>　　不要期望别人随时都能够帮助你，别希望面包随时会从天上掉嘴里，人生没有努力和进取，就根本不会有了收获的欣喜。万事只求尽力，只要尽力不管会收获到多少，只要努力就会有好的结局。</p><p>　　人生路总是蜿蜒又崎岖，人生事总是烦忧掺杂在一起，只要有了定力，无论什么时候都不会被风雨伤彻底，无论面对怎样的困境总有一天一定能够慢慢走出去。</p><p><br/></p>', '路', '路', '0', '20', '20', null, '满天星', '2018-02-28 15:40:02', '2018-04-28 16:03:17', '0', '1', '1');
INSERT INTO `article` VALUES ('21', '民间搞笑荟萃', '9', '搞笑', 'admin/Upload/20180418/950d1f51ce0a74ccfc9ecaa1c85b7f46.jpg', '<p>&nbsp;&nbsp;&nbsp;&nbsp;1。 老公发工资了，拿回家准备讨好老婆。就对他的老婆说：“亲爱的，我发工资了。亲我一下钱归你了。”老婆迟迟无动于衷。老公见老婆没有反应。又喊了一句：“你要是再不来我就去找人伺候我了，把钱都给她了。”老婆淡淡地回了一句：“你要是敢去，这些钱你是怎么花的，我就怎么赚回来。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;2。 妻子突然问丈夫：“你爱我吗？” “爱，当然爱！”丈夫毫不犹豫地回答。 妻子想了想又问：“你是不是怕伤害我才说的？” 丈夫连忙说：“不，不，我是怕你伤害我才说的。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;3。小明数学不好被父母转学到一间教会学校。半年后数学成绩全A。妈妈问：“是修女教得好？是教材好？是祷告？……”“都不是，”小明说，“进学校的第一天，我看见一个人被钉死在加号上面，我就知道……他们是玩真的。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;4。在公厕里，忽然听到厕间有人说话：“哥们儿，有手纸吗？”我翻了翻口袋：“抱歉，没有。”过了几秒钟，那人又问：“那有小块报纸吗？”我无奈地一笑：“对不起，没有，我只是来小便的。”又过了几秒钟，厕间门缝塞出一张10元人民币：“能帮我换成10张1块的吗？”我把钱递过去，厕间里传来一声咆哮：“你别给我十个硬币啊！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;5。 20年前爸爸抱着你等车，人都笑话孩子长得难看，爸爸哭了。一卖香蕉的老大爷拍拍爸爸说：“大兄弟别哭了，拿只香蕉给猴子吃吧！真可怜，饿的都没毛了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;6。 话说某位女士一时兴起，买了一只母鹦鹉。没想到带回家里，它说的第一句话就是：“想跟我上床吗？”女士一听，心想：坏了，外人还以为这话是我教的呢， 这不把我的淑女形象全给毁了。于是她想尽办法，想交那只鹦鹉说些高雅的东西，可是那只母鹦鹉算是铁了心了，只会说一句话：“想跟我上床吗？”……怎么办呢？在那位女士失去主张的时候，听说神父那儿也养了一只鹦鹉（公的），而且那只鹦鹉，不但不讲粗话，反而是个虔诚的教徒，每天大部分时间里都在祷告。于是那位女士去找神父求助。神父明白她的来意之后，面色微难的说：“这个，很难办呀，其实那只鹦鹉，也并没有刻意的教它什么，它之所以这么虔诚，也可能是长期在此受熏陶的缘故吧。”神父见女士很失落，便说道：“这样吧，你把那只鹦鹉带到我这里来，我把它们放在一起。希望经过一段时间，你那只鹦鹉能够被感化。我只能做这些了，有没有效果，就看神的旨意了……”女士一听，也只能这样了，不是有句话叫：近朱者赤吗？试试吧。于是她把鹦鹉带到神父那里。神父依照诺言把两只鹦鹉放在了一起。开始母鹦鹉还有些拘谨，看那只公鹦鹉在笼子的一角，默默的祷告，还真不忍心打扰。可是她还是管不住自己，终于朗声说道：“想跟我上床吗？“公鹦鹉听到这话，停止了祷告，转身看了看母鹦鹉，忽然泪如雨下：“感谢上帝，我祷告这么多年的愿望终于实现了……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;7。青年跋涉深山，历经险阻，终于找到了隐居山中的禅师，他迫不及待地问道：“我长得丑，我该怎么办？”“长得丑就应该像我一样。”青年点点头：“心如止水，独善其身？”“不，长得丑就要像我一样赶紧找个深山躲起来。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;8。本人女，以前一百四十多斤，现在瘦下来了。某日准备出门找不到电话了，于是拿老爸电话打我电话，打过去老爸电话上华丽丽地显示了“大肥猪”三个字。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;9。突然停电了，“大热的天，还让不让人活了，真烦人。”我抱怨道。儿子却在一旁笑呵呵地说：“我觉得停电也挺好的。”听他这么说，就更加来气了：“大热天的，停电有啥好处啊？就知道瞎说！”儿子被我训得低下头，小声嘟囔道：“停电我就不用做作业了呗……”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;10。 渔夫晚上捕鱼回来，看见儿子正与邻居家的姑娘披着渔网在亲热，渔夫怒问：你们这是干嘛？儿笑笑，说：老爸，你不懂，我们这叫网恋！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;11。一次学校让买学习资料，大家都是7块钱，我为了让自己能有点零用钱花一张嘴就要了10块，结果老妈不信我，就跑去问家对面的同班同学，当时那心里忐忑的差点就说实话了。没想到老妈一回来就说：“一看上课就没听老师讲课，人家明明是12块！”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;12。 地铁人满为患，可那些女孩宁愿站着也不愿意坐我身边的空位。直到我到站了也没人来坐。“难道因为我的帅让她们小鹿乱撞了而不敢靠近我？下次是不是应该戴个口罩出门？”我一边把袜子穿好准备下车一边反思。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;13。 经过不懈努力，我戒掉了睡前玩手机的习惯，但不知道能坚持多久，总不能一辈子不睡觉吧！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;14。　——研表究明，汉字的序顺并不定一能影阅响读，比如当你看完这句话后，才发这现里的字全是都乱的。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;15。 一天傍晚在小河边，阿Q对他的的新交女朋友说：我可以讲一个字让你笑。女友说不信。阿Q这时，走到了一只哈八狗面前，扑通一声跪地上，只听一声大喊：爹。结果引来了所有闻言者的哄堂大笑。阿Q回过头来对女友又喊了一句：妈。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;16。 一位男子跑进车厢，着急地嚷嚷：“隔壁车厢里有一位太太晕过去了，谁带了威士忌？”乘客中很快有人拿出了威士忌。这位男子接过。喝了几大口，然后将酒瓶还给乘客道：“太谢谢你了，我这人看见女士晕倒就难受，这下好多了。”</p><p>&nbsp;&nbsp;&nbsp;&nbsp;17。 第一天，小白兔去河边钓鱼，什么也没钓到，回家了。第二天，小白兔又去河边钓鱼，还是什么也没钓到，回家了。第三天，小白兔刚到河边，一条大鱼从河里跳出来，冲着小白兔大叫：你他妈的要是再敢用胡箩卜当鱼饵，我就扁死你！</p><p>&nbsp;&nbsp;&nbsp;&nbsp;18。 顾客：“你们卖的酒怎么没有酒味啊？”服务员接过一闻：“啊，真对不起，忘记给您掺酒了。”</p>', '搞笑', '搞笑', '0', '20', '9', null, '满天星', '2018-04-04 22:49:51', '2018-04-28 16:03:17', '0', '1', '1');
INSERT INTO `article` VALUES ('22', '甘草人生', '7', '甘草人生', 'admin/Upload/20180418/2250e4557c934032474f8809a501fcf0.jpg', '<p>　　大家都知道甘草，都应该吃过甘草，甘草其味甘甜，越嚼越有回甜的味道，甘草几乎在每一剂中药里都不可缺少，也许因为中药很苦所以都掺进去几截甘草。苦苦的中药就会有了回甜的味道。有淡淡的回甜感觉真好，就是那种回甜感觉总在心间索绕，就是那种甘的滋味总让人久久回想，难忘。</p><p>　　记得小时候，只要看见家里有中药的时候就会忙从中把甘草挑出来，然后用小手绢或者一张纸轻轻的把甘草包起来，在上学的路上的时候又一小点一小点的分给伙伴，让大家一起分享回甜的味道，直到甘草在嘴里含得嚼得已经全然无味的时候，才舍得吐掉。可笑的是那个时候我们都把甘草当成零食了。</p><p>　　在那个年代我感觉甘草很好吃，比糖还有味道，甘草与糖相比差别其实很大，因为吃完糖后如果喝上一杯水，那甜味几乎都已经荡然无存，而含完甘草喝杯水后，那淡淡的甘甜味却会长时间在嘴里保持着，由此我喜欢上了甘草，全因那种过后甜的美的回味。</p><p>　　甘草总给我们甜的回味和好的印象，我觉得做人也应该像甘草一样，应该在生活中不断的让自己成长，应该在学习中让自己不断进步，应该在人生中不断的让自己的修养得到提高。只要修养多了提高了，思想的境界就一定会慢慢的升高。只要修养大了丰富了，胸襟就一定宽阔而坦荡。</p><p>　　凡是一个有修养的人不管在任何时候任何地方，不管面对多少的风雨或者人间任何寒霜，都会保持着一份淡定和微笑，因为淡定是一种开阔的思想，因为微笑是一种无形的力量。于是就渐渐与事无争，慢慢就会少于计较。渐渐就会多些包容自然的就会减轻一些烦恼。这根本不是生命的胆怯或者退让，而是一种大包大容的智慧能量体现，当别人跟你相处久了就会知道你的胸怀和宽阔而且很坦荡，这就是你给别人的一种好感觉，这种好感觉就像回味的甘草一样，让人久久难忘。</p><p>　　从人的言谈举止里可以看到听到一个人的修为有多高，也可以感觉到一个人思想品质和情操的高尚，人往往因为一些小节和细节而反映出来一个人的真正思想情况，一些得体的言谈总会留下给人好印象，一些得体的举止总会给人增添几分羡慕和敬仰。言谈举止里面最能显露出一个人的思维和思想。不用包装因为根本就包装不了，不用隐藏因为根本就无法去隐藏，这些都全靠平时的学习和修养，这些都是蕴含在生命里的自然能量。得体的言谈举止就像回味草一样，总会留给别人一些深刻的好印象。</p><p>　　要大方做人大方的做事，大方是一种坦荡也是一种自然的行为习惯，做人不用躲躲藏藏，不用机关算尽，只有坦荡的做人才会让轻松快乐在生命里保持。也不要跟人斤斤计较，当吃亏的时候就全当是一种福气，上当的时候就全当只是摔了一跤，只要把所有的人生经验积累起来那才是真正的财富和宝藏。</p><p>　　做事不用扭扭捏捏的，一定要大大方方，认认真真，仔仔细细的去做。无论做小事还是大事都不要被人小瞧，我的人生信条是；【世界上根本没有我做不到的事，只有我不想做的事，要做就必须尽量做好】。人本来就应该多些勇敢和坚强，多些挑战就会循序渐进努力的向上。大方就是一种容量，只要大方做人大方的做事，无论走到哪里都觉得道路很宽广，大方总会给人留下好印象，大方也如甘草一样慢慢的会有自然的味道。</p><p>　　要看淡得失，一定要在得失中继续积极向上的面对未来，每个人的人生都有太多的感慨和无奈，太多忧伤欢喜总会在内心汹涌澎湃，只要永远相信未来，只要有梦想的存在，希望的翅膀就会在每天展开，只有自信才能走到未来。</p><p>　　一个自信的人总给人惊喜和欣喜，一个自信的人总给人鼓舞和勇气，当这样的人站在人群里一定会自然散发自信的光彩，所以自信就是一盏点亮希望的明灯，因为自信让你浑身充满朝气和光彩，自信的感觉肯定就会像甘草一样慢慢进入到别人的心海。</p><p>　　当我们从痛苦忧伤里面走出来，当我们淡却了烦恼，生命就会恢复往日的平静，别让忧喜左右我们的思想，别让烦恼影响我们的未来。溘然开悟是思想的升华，顿然释怀是一种博大情怀。不再纷争，不再计较，不再理会，不再伤怀。就这样顺其自然的走进茫茫人海，在你的身前身后也许会有更多的喝彩。放下，抛开那是洒脱和情怀，你的洒脱自在就像甘草一样自然入怀。</p><p>　　都说只要人人都献出一点爱，世界就会美丽和精彩，爱是一种微笑，爱是一种善良，爱是一种温暖，爱是一种力量。只要我们用小小的爱去对待真心对待每一人，那么温暖就会常在，那么美丽就会驻留在心海。小爱有小爱的温暖，大爱有大爱的情怀。只要有爱，春天的花朵就会在生命里常开不败。只要爱如花，美丽芬芳就会满天涯，爱也就像甘草一样，其美其香慢慢就会让人感觉出来。</p><p>　　甘草的回味已经深入在人们的生命里，这是一种回味的草，这是一种令人深思具有感想的草，那种淡淡的回甜就是最好最美最自然的感觉，人生就是因为寻梦寻味而美丽精彩的，因为梦美梦醉我们才不停的努力去追，生命总因梦而充满欣喜和神奇。一生因梦而忙于拼搏努力和进取。寻梦就像寻着甘草一样回甜的滋味，一直寻着好的感觉而不悔。</p><p>　　有了自立自强自信才会散发自己的人格魅力，只有不断的努力，只有积极的向上人生才会充满精彩和美丽。每个人做人不一定要有多高贵但是一定要追求高尚，因为高尚是一种至上的代表，人只有不断追求积极向上的东西生命才会充实和愉快。有时间就要学会多看多听多学多思考，那么无论做人做事还是做文就一定会走顺利路过自然桥的。只要生命蕴含着太多东西，就能充分的发挥出自己的能量。就像甘草一样越嚼味道越好。</p><p>　　要做就做一棵充满回味的草，小草不用拘泥于世俗的安排，小草不用刻意装扮和隐藏。一生大大方方，痛痛快快，自然潇洒的向阳生长，只要微笑走向未来，那么未来一定充满许多色彩，那么未来的感觉一定是一种淡淡的回甜慢慢涌上来。生命一定因为回甜而每天喜笑颜开，人生一定因为回甜而充满无限的精彩。</p><p><br/></p>', '甘草人生', '甘草人生', '0', '20', '76', null, '满天星', '2018-03-16 16:52:22', '2018-04-28 16:03:17', '0', '1', '1');
INSERT INTO `article` VALUES ('23', 'input标签内设置禁止输入空格', '15', 'input', 'admin/Upload/20180418/28755abd30dd88bf48058e30895e9ae9.png', '<pre style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; white-space: pre-wrap; word-wrap: break-word; color: rgb(73, 73, 73); font-size: 14px; line-height: 22.4px; widows: 1;\"><br/></pre><p>通过正则匹配解决问题　\\s:匹配任意空白符　\\S:匹配任意非空白字符</p><p>&lt;input type=&quot;text&quot; onkeyup=&quot;this.value=this.value.replace(/\\s+/g,&#39;&#39;)&quot; /&gt;</p><p><br/></p>', '空格', 'input禁止输入空格', '0', '0', '1', null, '满天星', '2018-04-10 14:14:01', '2018-04-28 16:03:17', '0', '1', '1');
INSERT INTO `article` VALUES ('24', 'TP框架自带的正则验证的规则', '13', '正则', 'admin/Upload/20180418/fb9b1453e5f5eddb2cfbd977c4208da6.jpg', '<p><span style=\"color: rgb(34, 34, 34); line-height: normal; white-space: pre-wrap; widows: 1; background-color: rgb(255, 255, 255); font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;thinkphp框架里面自带有很多自动验证的规则，下面是框架自带的正则验证的规则，官方的说明文档里面没有这么多，所以记下来，以备使用。</span></p><p><span style=\"color: rgb(34, 34, 34); line-height: normal; white-space: pre-wrap; widows: 1; background-color: rgb(255, 255, 255); font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">static $regex = array( </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&#39;require&#39;=&gt; &#39;/.+/&#39;, //匹配任意字符，除了空和断行符 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&#39;email&#39; =&gt; &#39;/^\\w+([-+.]\\w+)*@\\w+([-.]\\w+)*\\.\\w+([-.]\\w+)*$/&#39;, </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&#39;phone&#39; =&gt; &#39;/^((\\(\\d{2,3}\\))|(\\d{3}\\-))?(\\(0\\d{2,3}\\)|0\\d{2,3}-)?[1-9]\\d{6,7}(\\-\\d{1,4})?$/&#39;, </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&#39;mobile&#39; =&gt; &#39;/^((\\(\\d{2,3}\\))|(\\d{3}\\-))?13\\d{9}$/&#39;,</span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&#39;url&#39; =&gt; &#39;/^http:\\/\\/[A-Za-z0-9]+\\.[A-Za-z0-9]+[\\/=\\?%\\-&amp;_~`@[\\]\\&#39;:+!]*([^&lt;&gt;\\&quot;\\&quot;])*$/&#39;, </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&#39;currency&#39; =&gt; &#39;/^\\d+(\\.\\d+)?$/&#39;,</span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&#39;number&#39; =&gt; &#39;/\\d+$/&#39;,</span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&#39;zip&#39; =&gt; &#39;/^[1-9]\\d{5}$/&#39;, </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&#39;qq&#39; =&gt; &#39;/^[1-9]\\d{4,12}$/&#39;, </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&#39;integer&#39; =&gt; &#39;/^[-\\+]?\\d+$/&#39;, </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&#39;double&#39; =&gt; &#39;/^[-\\+]?\\d+(\\.\\d+)?$/&#39;, </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&#39;english&#39; =&gt; &#39;/^[A-Za-z]+$/&#39;</span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">); </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">自动验证的使用方法，具体可以参见官方手册</span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">var $_validate=array( </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;array(&#39;name&#39;,&#39;require&#39;,&#39;姓名必须填写！&#39;), </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;array(&#39;banji&#39;,&#39;require&#39;,&#39;班级必须填写！&#39;),</span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;array(&#39;jiguan&#39;,&#39;require&#39;,&#39;籍贯必须填写！&#39;),</span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;array(&#39;rtsj&#39;,&#39;/^\\d{4}\\-\\d{1,2}-\\d{1,2}$/&#39;,&#39;入团时间格式错误！&#39;), </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;array(&#39;rtdd&#39;,&#39;require&#39;,&#39;入团地点必须填写！&#39;), </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;array(&#39;birthday&#39;,&#39;/^\\d{4}\\-\\d{1,2}-\\d{1,2}$/&#39;,&#39;出生年月格式错误！&#39;),</span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;array(&#39;qianfarq&#39;,&#39;/^\\d{4}\\-\\d{1,2}-\\d{1,2}$/&#39;,&#39;签发日期格式错误！&#39;), </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;array(&#39;bianhao&#39;,&#39;require&#39;,&#39;团员编号必须填写！&#39;)</span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">); &nbsp; </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">array(‘name’,’/^[a-z]\\w{3,}$/i’,’名字不符合要求！’); </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">array(‘password’,’/^[a-z]\\w{6,30}$/i’,’密码不符合要求！’); </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">array(‘account’,’/^[A-Za-z]+$/’,’账号必须使用英文！’); </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">附上一些表单验证中比较常用的正则表达式写法： </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配中文字符的正则表达式： [\\u4e00-\\u9fa5] </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配双字节字符(包括汉字在内)：[^\\x00-\\xff] </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配Email地址的正则表达式：\\w+([-+.]\\w+)*@\\w+([-.]\\w+)*\\.\\w+([-.]\\w+)* </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配网址URL的正则表达式：[a-zA-z]+://[^\\s]* </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配帐号是否合法(字母开头，允许5-16字节，允许字母数字下划线)：^[a-zA-Z][a-zA-Z0-9_]{4,15}$ </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配国内电话号码：\\d{3}-\\d{8}|\\d{4}-\\d{7} </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配中国邮政编码：[1-9]\\d{5}(?!\\d) </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配ip地址：\\d+\\.\\d+\\.\\d+\\.\\d+ </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配特定数字： </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;^[1-9]\\d*$&nbsp;&nbsp;&nbsp;&nbsp;//匹配正整数 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;^-[1-9]\\d*$ 　 //匹配负整数 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;^-?[1-9]\\d*$&nbsp;&nbsp;&nbsp;&nbsp;//匹配整数 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;^[1-9]\\d*|0$&nbsp;&nbsp;&nbsp;&nbsp;//匹配非负整数（正整数 + 0） </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;^-[1-9]\\d*|0$　　 //匹配非正整数（负整数 + 0） </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;^[1-9]\\d*\\.\\d*|0\\.\\d*[1-9]\\d*$　　 //匹配正浮点数 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;^-([1-9]\\d*\\.\\d*|0\\.\\d*[1-9]\\d*)$　 //匹配负浮点数 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;^-?([1-9]\\d*\\.\\d*|0\\.\\d*[1-9]\\d*|0?\\.0+|0)$　 //匹配浮点数 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;^[1-9]\\d*\\.\\d*|0\\.\\d*[1-9]\\d*|0?\\.0+|0$　　 //匹配非负浮点数（正浮点数 + 0） </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;^(-([1-9]\\d*\\.\\d*|0\\.\\d*[1-9]\\d*))|0?\\.0+|0$　　//匹配非正浮点数（负浮点数 + 0） </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配特定字符串： </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;^[A-Za-z]+$　　//匹配由26个英文字母组成的字符串 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;^[A-Z]+$　　//匹配由26个英文字母的大写组成的字符串 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;^[a-z]+$　　//匹配由26个英文字母的小写组成的字符串 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;^[A-Za-z0-9]+$　　//匹配由数字和26个英文字母组成的字符串 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;^\\w+$　　//匹配由数字、26个英文字母或者下划线组成的字符串 &nbsp; </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); font-size: 12px; white-space: pre-wrap; widows: 1; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配中文字符的正则表达式： [\\u4e00-\\u9fa5] </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;评注：匹配中文还真是个头疼的事，有了这个表达式就好办了 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配双字节字符(包括汉字在内)：[^\\x00-\\xff] </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;评注：可以用来计算字符串的长度（一个双字节字符长度计2，ASCII字符计1） </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配空白行的正则表达式：\\n\\s*\\r </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;评注：可以用来删除空白行 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配HTML标记的正则表达式：&lt;(\\S*?)[^&gt;]*&gt;.*?&lt;/\\1&gt;|&lt;.*? /&gt; </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;评注：网上流传的版本太糟糕，上面这个也仅仅能匹配部分，对于复杂的嵌套标记依旧无能为力 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配首尾空白字符的正则表达式：^\\s*|\\s*$ </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;评注：可以用来删除行首行尾的空白字符(包括空格、制表符、换页符等等)，非常有用的表达式 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配Email地址的正则表达式：\\w+([-+.]\\w+)*@\\w+([-.]\\w+)*\\.\\w+([-.]\\w+)* </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;评注：表单验证时很实用 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配网址URL的正则表达式：[a-zA-z]+://[^\\s]* </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;评注：网上流传的版本功能很有限，上面这个基本可以满足需求 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配帐号是否合法(字母开头，允许5-16字节，允许字母数字下划线)：^[a-zA-Z][a-zA-Z0-9_]{4,15}$ </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;评注：表单验证时很实用 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配国内电话号码：\\d{3}-\\d{8}|\\d{4}-\\d{7} </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;评注：匹配形式如 0511-4405222 或 021-87888822 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配腾讯QQ号：[1-9][0-9]{4,} </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;评注：腾讯QQ号从10000开始 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配中国邮政编码：[1-9]\\d{5}(?!\\d) </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;评注：中国邮政编码为6位数字 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配身份证：\\d{15}|\\d{18} </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;评注：中国的身份证为15位或18位 </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;匹配ip地址：\\d+\\.\\d+\\.\\d+\\.\\d+ </span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(34, 34, 34); white-space: pre-wrap; widows: 1; font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;评注：提取ip地址时有用</span></p>', '正则', '正则验证的规则', '0', '0', '14', null, '满天星', '2018-04-10 15:29:00', '2018-04-28 16:03:17', '0', '1', '1');
INSERT INTO `article` VALUES ('25', 'PHP中用正则表达式清除字符串的空白', '6', '字符串|空白', 'admin/Upload/20180418/8344ca157df85d5ee892c237f48e30cb.jpg', '<p><span style=\"color: rgb(51, 51, 51); font-family: PingFangSC, &quot;helvetica neue&quot;, &quot;hiragino sans gb&quot;, arial, &quot;microsoft yahei ui&quot;, &quot;microsoft yahei&quot;, simsun, sans-serif; line-height: 24px; widows: 1; background-color: rgb(255, 255, 255);\">如果您想要去掉字符串开始和结束的空白可以使用</span>PHP<span style=\"color: rgb(51, 51, 51); font-family: PingFangSC, &quot;helvetica neue&quot;, &quot;hiragino sans gb&quot;, arial, &quot;microsoft yahei ui&quot;, &quot;microsoft yahei&quot;, simsun, sans-serif; line-height: 24px; widows: 1; background-color: rgb(255, 255, 255);\">内部函数trim() 。但是, 我们经常想完全清除空白。需要把开始和结束的空白清除掉，将多个空白变为一个空白，使用一个规则来处理同样的类型的其它空白。&nbsp;</span><br/><br/><span style=\"color: rgb(51, 51, 51); font-family: PingFangSC, &quot;helvetica neue&quot;, &quot;hiragino sans gb&quot;, arial, &quot;microsoft yahei ui&quot;, &quot;microsoft yahei&quot;, simsun, sans-serif; line-height: 24px; widows: 1; background-color: rgb(255, 255, 255);\">完成这些可以使用PHP的正则表达式来完成&nbsp;</span><br/><br/><span style=\"color: rgb(51, 51, 51); font-family: PingFangSC, &quot;helvetica neue&quot;, &quot;hiragino sans gb&quot;, arial, &quot;microsoft yahei ui&quot;, &quot;microsoft yahei&quot;, simsun, sans-serif; line-height: 24px; widows: 1; background-color: rgb(255, 255, 255);\">下例可以去除额外Whitespace&nbsp;</span><br/></p><p>代码如下:</p><p><br/>&lt;?php&nbsp;<br/>$str = &quot; This line contains\\tliberal \\r\\n use of whitespace.\\n\\n&quot;;&nbsp;<br/><br/>// First remove the leading/trailing whitespace&nbsp;<br/>//去掉开始和结束的空白&nbsp;<br/>$str = trim($str);&nbsp;<br/><br/>// Now remove any doubled-up whitespace&nbsp;<br/>//去掉跟随别的挤在一块的空白&nbsp;<br/>$str = preg_replace(&#39;/\\s(?=\\s)/&#39;, &#39;&#39;, $str);&nbsp;<br/><br/>// Finally, replace any non-space whitespace, with a space&nbsp;<br/>//最后，去掉非space 的空白，用一个空格代替&nbsp;<br/>$str = preg_replace(&#39;/[\\n\\r\\t]/&#39;, &#39; &#39;, $str);&nbsp;<br/><br/>// Echo out: &#39;This line contains liberal use of whitespace.&#39;&nbsp;<br/>echo &quot;&lt;pre&gt;{$str}&lt;/pre&gt;&quot;;&nbsp;<br/>?&gt;&nbsp;<br/></p><p><br/><br/><span style=\"color: rgb(51, 51, 51); font-family: PingFangSC, &quot;helvetica neue&quot;, &quot;hiragino sans gb&quot;, arial, &quot;microsoft yahei ui&quot;, &quot;microsoft yahei&quot;, simsun, sans-serif; line-height: 24px; widows: 1; background-color: rgb(255, 255, 255);\">上例一步一步的去掉所有的空白。首先我们使用trim()函数来去掉开始和结束的空白。然后，我们使用preg_replace() 去除重复的。\\s代表任何whitespace 。(?=) 表示向前查找 。它味着只匹配后面有和它本身相同字符的字符。所以这个正则表达式的意思是: &quot;被whitespace 字符跟随的任何whitespace 字符。&quot; 我们用空白来替换掉，这样也就去除了，留下的将是唯一的whitespace 字符。&nbsp;</span><br/><br/><span style=\"color: rgb(51, 51, 51); font-family: PingFangSC, &quot;helvetica neue&quot;, &quot;hiragino sans gb&quot;, arial, &quot;microsoft yahei ui&quot;, &quot;microsoft yahei&quot;, simsun, sans-serif; line-height: 24px; widows: 1; background-color: rgb(255, 255, 255);\">最后, 我们使用另一个正则表达式[\\n\\r\\t]来查找任何残余的换行符(\\n), 回车(\\r), 或制表符(\\t) 。我们用一个空格来替换这些。</span></p><p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; color: rgb(51, 51, 51); font-family: PingFangSC, &quot;helvetica neue&quot;, &quot;hiragino sans gb&quot;, arial, &quot;microsoft yahei ui&quot;, &quot;microsoft yahei&quot;, simsun, sans-serif; line-height: 24px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">以上是云栖社区小编为您精心准备的的内容，在云栖社区的博客、问答、公众号、人物、课程等栏目也有的相关内容，欢迎继续使用右上角搜索按钮进行搜索字符串 空白 正则表达式 空白字符、正则表达式 空白、正则表达式 空白行、正则表达式 匹配空白、正则表达式空白符，以便于您获取更多的相关知识。</p><p><br/></p>', '字符串|空白', '', '2', '0', '143', '0', '满天星', '2018-04-10 15:58:36', '2018-04-29 09:40:02', '0', '1', '1');
INSERT INTO `article` VALUES ('26', 'HTML中Form表单提交时,采用 get和post的区别', '15', 'HTML|get|post', 'admin/Upload/20180418/29289fc8c7e5571d013bee86288d2aa7.png', '<p><span style=\"color: rgb(85, 85, 85); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; font-size: 15px; line-height: 35px; widows: 1; background-color: rgb(255, 255, 255);\">Form中的get和post方法，在数据传输过程中分别对应了HTTP协议中的GET和POST方法。二者主要区别如下：</span><br/><span style=\"color: rgb(85, 85, 85); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; font-size: 15px; line-height: 35px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;1、Get是用来从服务器上获得数据，而Post是用来向服务器上传递数据。</span><br/><span style=\"color: rgb(85, 85, 85); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; font-size: 15px; line-height: 35px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;2、Get将表单中数据的按照variable=value的形式，添加到action所指向的URL后面，并且两者使用“?”连接，而各个变量之间使用“&amp;”连接；Post是将表单中的数据放在form的数据体中，按照变量和值相对应的方式，传递到action所指向URL。</span><br/><span style=\"color: rgb(85, 85, 85); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; font-size: 15px; line-height: 35px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;3、Get是不安全的，因为在传输过程，数据被放在请求的URL中，而如今现有的很多服务器、代理服务器或者用户代理都会将请求URL记录到日志文件中，然后放在某个地方，这样就可能会有一些隐私的信息被第三方看到。另外，用户也可以在浏览器上直接看到提交的数据，一些系统内部消息将会一同显示在用户面前。Post的所有操作对用户来说都是不可见的。</span><br/><span style=\"color: rgb(85, 85, 85); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; font-size: 15px; line-height: 35px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;4、Get传输的数据量小，这主要是因为受URL长度限制；而Post可以传输大量的数据，所以在上传文件只能使用Post（当然还有一个原因，将在后面的提到）。</span><br/><span style=\"color: rgb(85, 85, 85); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; font-size: 15px; line-height: 35px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;5、Get限制Form表单的数据集的值必须为ASCII字符；而Post支持整个ISO10646字符集。</span><br/><span style=\"color: rgb(85, 85, 85); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; font-size: 15px; line-height: 35px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;6、Get是Form的默认方法。&nbsp;</span></p>', 'get|post', '', '0', '0', '0', null, '满天星', '2018-04-17 16:47:52', '2018-04-28 16:03:17', '0', '1', '1');
INSERT INTO `article` VALUES ('29', 'MySQL数据库优化的八种方式(经典必看)', '14', 'MySQL学习|MySQL优化|MySQL数据库', 'admin/Upload/20180418/04190f21563d3167620736a3e5f2ec5a.jpg', '<h4 style=\"margin: 10px 0px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">1、选取最适用的字段属性</h4><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;MySQL可以很好的支持大数据量的存取，但是一般说来，数据库中的表越小，在它上面执行的查询也就会越快。因此，在创建表的时候，为了获得更好的性能，我们可以将表中字段的宽度设得尽可能小。</p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;例如，在定义邮政编码这个字段时，如果将其设置为CHAR(255),显然给数据库增加了不必要的空间，甚至使用VARCHAR这种类型也是多余的，因为CHAR(6)就可以很好的完成任务了。同样的，如果可以的话，我们应该使用MEDIUMINT而不是BIGIN来定义整型字段。</p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><strong style=\"margin: 0px; padding: 0px;\">&nbsp;&nbsp;&nbsp;&nbsp;另外一个提高效率的方法是在可能的情况下，应该尽量把字段设置为NOTNULL</strong>，这样在将来执行查询的时候，数据库不用去比较NULL值。<br/>&nbsp;&nbsp;&nbsp;&nbsp;对于某些文本字段，例如“省份”或者“性别”，我们可以将它们定义为ENUM类型。因为在MySQL中，ENUM类型被当作数值型数据来处理，而数值型数据被处理起来的速度要比文本类型快得多。这样，我们又可以提高数据库的性能。</p><h4 style=\"margin: 10px 0px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">2、使用连接（JOIN）来代替子查询(Sub-Queries)</h4><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;MySQL从4.1开始支持SQL的子查询。这个技术可以使用SELECT语句来创建一个单列的查询结果，然后把这个结果作为过滤条件用在另一个查询中。例如，我们要将客户基本信息表中没有任何订单的客户删除掉，就可以利用子查询先从销售信息表中将所有发出订单的客户ID取出来，然后将结果传递给主查询，如下所示：</p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><code style=\"margin: 1px 5px; padding: 0px 5px !important; line-height: 1.8; vertical-align: middle; display: inline-block; font-family: &#39;Courier New&#39;, sans-serif !important; font-size: 12px !important; border: 1px solid rgb(204, 204, 204) !important; border-radius: 3px !important; background-color: rgb(245, 245, 245) !important;\">DELETEFROMcustomerinfo</code></p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><code style=\"margin: 1px 5px; padding: 0px 5px !important; line-height: 1.8; vertical-align: middle; display: inline-block; font-family: &#39;Courier New&#39;, sans-serif !important; font-size: 12px !important; border: 1px solid rgb(204, 204, 204) !important; border-radius: 3px !important; background-color: rgb(245, 245, 245) !important;\">WHERECustomerIDNOTin(SELECTCustomerIDFROMsalesinfo)</code></p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;使用子查询可以一次性的完成很多逻辑上需要多个步骤才能完成的SQL操作，同时也可以避免事务或者表锁死，并且写起来也很容易。但是，有些情况下，子查询可以被更有效率的连接（JOIN）..替代。例如，假设我们要将所有没有订单记录的用户取出来，可以用下面这个查询完成：</p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><code style=\"margin: 1px 5px; padding: 0px 5px !important; line-height: 1.8; vertical-align: middle; display: inline-block; font-family: &#39;Courier New&#39;, sans-serif !important; font-size: 12px !important; border: 1px solid rgb(204, 204, 204) !important; border-radius: 3px !important; background-color: rgb(245, 245, 245) !important;\">SELECT*FROMcustomerinfo</code></p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><code style=\"margin: 1px 5px; padding: 0px 5px !important; line-height: 1.8; vertical-align: middle; display: inline-block; font-family: &#39;Courier New&#39;, sans-serif !important; font-size: 12px !important; border: 1px solid rgb(204, 204, 204) !important; border-radius: 3px !important; background-color: rgb(245, 245, 245) !important;\">WHERECustomerIDNOTin(SELECTCustomerIDFROMsalesinfo)</code></p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><strong style=\"margin: 0px; padding: 0px;\">如果使用连接（JOIN）..来完成这个查询工作，速度将会快很多</strong>。尤其是当salesinfo表中对CustomerID建有索引的话，性能将会更好，查询如下：</p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><code style=\"margin: 1px 5px; padding: 0px 5px !important; line-height: 1.8; vertical-align: middle; display: inline-block; font-family: &#39;Courier New&#39;, sans-serif !important; font-size: 12px !important; border: 1px solid rgb(204, 204, 204) !important; border-radius: 3px !important; background-color: rgb(245, 245, 245) !important;\">SELECT*FROMcustomerinfo</code></p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><code style=\"margin: 1px 5px; padding: 0px 5px !important; line-height: 1.8; vertical-align: middle; display: inline-block; font-family: &#39;Courier New&#39;, sans-serif !important; font-size: 12px !important; border: 1px solid rgb(204, 204, 204) !important; border-radius: 3px !important; background-color: rgb(245, 245, 245) !important;\">LEFTJOINsalesinfoONcustomerinfo.CustomerID=salesinfo.CustomerID</code></p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><code style=\"margin: 1px 5px; padding: 0px 5px !important; line-height: 1.8; vertical-align: middle; display: inline-block; font-family: &#39;Courier New&#39;, sans-serif !important; font-size: 12px !important; border: 1px solid rgb(204, 204, 204) !important; border-radius: 3px !important; background-color: rgb(245, 245, 245) !important;\">WHEREsalesinfo.CustomerIDISNULL</code></p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;连接（JOIN）..之所以更有效率一些，是因为MySQL不需要在内存中创建临时表来完成这个逻辑上的需要两个步骤的查询工作。</p><h4 style=\"margin: 10px 0px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">3、使用联合(UNION)来代替手动创建的临时表</h4><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;MySQL从4.0的版本开始支持union查询，它可以把需要使用临时表的两条或更多的select查询合并的一个查询中。在客户端的查询会话结束的时候，临时表会被自动删除，从而保证数据库整齐、高效。使用union来创建查询的时候，我们只需要用UNION作为关键字把多个select语句连接起来就可以了，要注意的是所有select语句中的字段数目要想同。下面的例子就演示了一个使用UNION的查询。</p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><code style=\"margin: 1px 5px; padding: 0px 5px !important; line-height: 1.8; vertical-align: middle; display: inline-block; font-family: &#39;Courier New&#39;, sans-serif !important; font-size: 12px !important; border: 1px solid rgb(204, 204, 204) !important; border-radius: 3px !important; background-color: rgb(245, 245, 245) !important;\">SELECTName,PhoneFROMclientUNION</code></p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><code style=\"margin: 1px 5px; padding: 0px 5px !important; line-height: 1.8; vertical-align: middle; display: inline-block; font-family: &#39;Courier New&#39;, sans-serif !important; font-size: 12px !important; border: 1px solid rgb(204, 204, 204) !important; border-radius: 3px !important; background-color: rgb(245, 245, 245) !important;\">SELECTName,BirthDateFROMauthorUNION</code></p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><code style=\"margin: 1px 5px; padding: 0px 5px !important; line-height: 1.8; vertical-align: middle; display: inline-block; font-family: &#39;Courier New&#39;, sans-serif !important; font-size: 12px !important; border: 1px solid rgb(204, 204, 204) !important; border-radius: 3px !important; background-color: rgb(245, 245, 245) !important;\">SELECTName,SupplierFROMproduct</code></p><h4 style=\"margin: 10px 0px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">4、事务</h4><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;尽管我们可以使用子查询（Sub-Queries）、连接（JOIN）和联合（UNION）来创建各种各样的查询，但不是所有的数据库操作都可以只用一条或少数几条SQL语句就可以完成的。更多的时候是需要用到一系列的语句来完成某种工作。但是在这种情况下，当这个语句块中的某一条语句运行出错的时候，整个语句块的操作就会变得不确定起来。设想一下，要把某个数据同时插入两个相关联的表中，可能会出现这样的情况：第一个表中成功更新后，数据库突然出现意外状况，造成第二个表中的操作没有完成，这样，就会造成数据的不完整，甚至会破坏数据库中的数据。要避免这种情况，就应该使用事务，它的作用是：要么语句块中每条语句都操作成功，要么都失败。换句话说，就是可以保持数据库中数据的一致性和完整性。事物以BEGIN关键字开始，COMMIT关键字结束。在这之间的一条SQL操作失败，那么，ROLLBACK命令就可以把数据库恢复到BEGIN开始之前的状态。</p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><code style=\"margin: 1px 5px; padding: 0px 5px !important; line-height: 1.8; vertical-align: middle; display: inline-block; font-family: &#39;Courier New&#39;, sans-serif !important; font-size: 12px !important; border: 1px solid rgb(204, 204, 204) !important; border-radius: 3px !important; background-color: rgb(245, 245, 245) !important;\">BEGIN; INSERTINTOsalesinfoSETCustomerID=14;UPDATEinventorySETQuantity=11WHEREitem=&#39;book&#39;;COMMIT;</code></p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;事务的另一个重要作用是当多个用户同时使用相同的数据源时，它可以利用锁定数据库的方法来为用户提供一种安全的访问方式，这样可以保证用户的操作不被其它的用户所干扰。</p><h4 style=\"margin: 10px 0px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">5、锁定表</h4><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;尽管事务是维护数据库完整性的一个非常好的方法，但却因为它的独占性，有时会影响数据库的性能，尤其是在很大的应用系统中。由于在事务执行的过程中，数据库将会被锁定，因此其它的用户请求只能暂时等待直到该事务结束。如果一个数据库系统只有少数几个用户来使用，事务造成的影响不会成为一个太大的问题；但假设有成千上万的用户同时访问一个数据库系统，例如访问一个电子商务网站，就会产生比较严重的响应延迟。</p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">其实，有些情况下我们可以通过锁定表的方法来获得更好的性能。下面的例子就用锁定表的方法来完成前面一个例子中事务的功能。</p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><code style=\"margin: 1px 5px; padding: 0px 5px !important; line-height: 1.8; vertical-align: middle; display: inline-block; font-family: &#39;Courier New&#39;, sans-serif !important; font-size: 12px !important; border: 1px solid rgb(204, 204, 204) !important; border-radius: 3px !important; background-color: rgb(245, 245, 245) !important;\">LOCKTABLEinventoryWRITESELECTQuantityFROMinventoryWHEREItem=&#39;book&#39;;</code></p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><code style=\"margin: 1px 5px; padding: 0px 5px !important; line-height: 1.8; vertical-align: middle; display: inline-block; font-family: &#39;Courier New&#39;, sans-serif !important; font-size: 12px !important; border: 1px solid rgb(204, 204, 204) !important; border-radius: 3px !important; background-color: rgb(245, 245, 245) !important;\">...</code></p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><code style=\"margin: 1px 5px; padding: 0px 5px !important; line-height: 1.8; vertical-align: middle; display: inline-block; font-family: &#39;Courier New&#39;, sans-serif !important; font-size: 12px !important; border: 1px solid rgb(204, 204, 204) !important; border-radius: 3px !important; background-color: rgb(245, 245, 245) !important;\">UPDATEinventorySETQuantity=11WHEREItem=&#39;book&#39;;UNLOCKTABLES</code></p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;这里，我们用一个select语句取出初始数据，通过一些计算，用update语句将新值更新到表中。包含有WRITE关键字的LOCKTABLE语句可以保证在UNLOCKTABLES命令被执行之前，不会有其它的访问来对inventory进行插入、更新或者删除的操作。</p><h4 style=\"margin: 10px 0px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">6、使用外键</h4><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><strong style=\"margin: 0px; padding: 0px;\">&nbsp;&nbsp;&nbsp;&nbsp;锁定表的方法可以维护数据的完整性，但是它却不能保证数据的关联性。这个时候我们就可以使用外键。</strong></p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;例如，外键可以保证每一条销售记录都指向某一个存在的客户。在这里，外键可以把customerinfo表中的CustomerID映射到salesinfo表中CustomerID，任何一条没有合法CustomerID的记录都不会被更新或插入到salesinfo中。</p><pre style=\"margin-top: 10px; margin-bottom: 10px; padding: 0px; white-space: pre-wrap; word-wrap: break-word; font-size: 14px; line-height: 23.8px; widows: 1; background-color: rgb(255, 255, 255);\">CREATETABLEcustomerinfo(&nbsp;CustomerIDINTNOTNULL,PRIMARYKEY(CustomerID))TYPE=INNODB;CREATETABLEsalesinfo(&nbsp;SalesIDINTNOTNULL,CustomerIDINTNOTNULL,\r\n\r\nPRIMARYKEY(CustomerID,SalesID),\r\n\r\nFOREIGNKEY(CustomerID)REFERENCEScustomerinfo(CustomerID)ONDELETECASCADE)TYPE=INNODB;</pre><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;注意例子中的参数“ONDELETECASCADE”。该参数保证当customerinfo表中的一条客户记录被删除的时候，salesinfo表中所有与该客户相关的记录也会被自动删除。如果要在MySQL中使用外键，一定要记住在创建表的时候将表的类型定义为事务安全表InnoDB类型。该类型不是MySQL表的默认类型。定义的方法是在CREATETABLE语句中加上TYPE=INNODB。如例中所示。</p><h4 style=\"margin: 10px 0px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">7、使用索引</h4><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;索引是提高数据库性能的常用方法，它可以令数据库服务器以比没有索引快得多的速度检索特定的行，尤其是在查询语句当中包含有MAX(),MIN()和ORDERBY这些命令的时候，性能提高更为明显。</p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;那该对哪些字段建立索引呢？</p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;一般说来，索引应建立在那些将用于JOIN,WHERE判断和ORDERBY排序的字段上。尽量不要对数据库中某个含有大量重复的值的字段建立索引。对于一个ENUM类型的字段来说，出现大量重复值是很有可能的情况</p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;例如customerinfo中的“province”..字段，在这样的字段上建立索引将不会有什么帮助；相反，还有可能降低数据库的性能。我们在创建表的时候可以同时创建合适的索引，也可以使用ALTERTABLE或CREATEINDEX在以后创建索引。此外，MySQL从版本3.23.23开始支持全文索引和搜索。全文索引在MySQL中是一个FULLTEXT类型索引，但仅能用于MyISAM类型的表。对于一个大的数据库，将数据装载到一个没有FULLTEXT索引的表中，然后再使用ALTERTABLE或CREATEINDEX创建索引，将是非常快的。但如果将数据装载到一个已经有FULLTEXT索引的表中，执行过程将会非常慢。</p><h4 style=\"margin: 10px 0px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">8、优化的查询语句</h4><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;绝大多数情况下，使用索引可以提高查询的速度，但如果SQL语句使用不恰当的话，索引将无法发挥它应有的作用。</p><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;下面是应该注意的几个方面。</p><ul style=\"margin-left: 30px; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\" class=\" list-paddingleft-2\"><ul class=\" list-paddingleft-2\" style=\"list-style-type: square;\"><li><p style=\"margin: 10px auto; padding: 0px;\">首先，最好是在相同类型的字段间进行比较的操作。</p><blockquote style=\"margin: 10px 0px; padding: 5px 10px; border: 2px solid rgb(239, 239, 239); min-height: 35px; line-height: 1.6em; color: rgb(51, 51, 51); background: none;\"><p style=\"margin: 10px auto; padding: 0px;\">在MySQL3.23版之前，这甚至是一个必须的条件。例如不能将一个建有索引的INT字段和BIGINT字段进行比较；但是作为特殊的情况，在CHAR类型的字段和VARCHAR类型字段的字段大小相同的时候，可以将它们进行比较。</p></blockquote></li><li><p style=\"margin: 10px auto; padding: 0px;\">其次，在建有索引的字段上尽量不要使用函数进行操作。</p></li></ul></ul><blockquote style=\"margin: 10px 0px; padding: 5px 10px; border: 2px solid rgb(239, 239, 239); min-height: 35px; line-height: 1.6em; color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; white-space: normal; widows: 1; background: none rgb(255, 255, 255);\"><p style=\"margin: 10px auto; padding: 0px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;例如，在一个DATE类型的字段上使用YEAE()函数时，将会使索引不能发挥应有的作用。所以，下面的两个查询虽然返回的结果一样，但后者要比前者快得多。</p></blockquote><ul style=\"margin-left: 30px; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\" class=\" list-paddingleft-2\"><ul class=\" list-paddingleft-2\" style=\"list-style-type: square;\"><li><p>第三，在搜索字符型字段时，我们有时会使用LIKE关键字和通配符，这种做法虽然简单，但却也是以牺牲系统性能为代价的。</p></li></ul></ul><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;例如下面的查询将会比较表中的每一条记录。</p><pre style=\"margin-top: 10px; margin-bottom: 10px; padding: 0px; white-space: pre-wrap; word-wrap: break-word; font-size: 14px; line-height: 23.8px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;SELECT*FROMbooks\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;WHEREnamelike&quot;MySQL%&quot;</pre><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;但是如果换用下面的查询，返回的结果一样，但速度就要快上很多：</p><pre style=\"margin-top: 10px; margin-bottom: 10px; padding: 0px; white-space: pre-wrap; word-wrap: break-word; font-size: 14px; line-height: 23.8px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;SELECT*FROMbooks\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;WHEREname＞=&quot;MySQL&quot;andname＜&quot;MySQM&quot;</pre><p style=\"margin: 10px auto; padding: 0px; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif; font-size: 14px; line-height: 23.8px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;最后，应该注意避免在查询中让MySQL进行自动类型转换，因为转换过程也会使索引变得不起作用。</p><p><br/></p>', 'MySQL学习|MySQL优化|MySQL数据库', '', '0', '0', '6', null, '满天星', '2018-04-17 16:56:33', '2018-04-28 16:03:17', '0', '1', '1');
INSERT INTO `article` VALUES ('30', 'PHP中include()与require()的区别说明', '6', 'PHP|include|require', 'admin/Upload/20180418/44df18642f04a35b45b846eea64fb6d4.jpg', '<p><span style=\"color: rgb(255, 0, 0);\">require </span>的使用方法如 <code><span style=\"font-family:NSimsun\">require(&quot;MyRequireFile.php&quot;);</span></code> 。这个函数通常放在 PHP 程序的最前面，PHP 程序在执行前，就会先读入 require 所指定引入的文件，使它变成 PHP 程序网页的一部份。常用的函数，亦可以这个方法将它引入网页中。</p><p><span style=\"color: rgb(255, 0, 0);\">include </span>使用方法如 <code><span style=\"font-family:NSimsun\">include(&quot;MyIncludeFile.php&quot;);</span></code> 。这个函数一般是放在流程控制的处理部分中。PHP 程序网页在读到 include 的文件时，才将它读进来。这种方式，可以把程序执行时的流程简单化。<br/><br/>他们两个的用途是完全一样的，不一定非得哪个放在最前面哪个放在中间。他们最根本的区别在于错误处理的方式不一样。 <br/><br/>require一个文件存在错误的话，那么程序就会中断执行了，并显示致命错误 <br/>include一个文件存在错误的话，那么程序不会中端，而是继续执行，并显示一个警告错误。 <br/><br/><strong>以下为补充：<br/></strong><br/>&nbsp;&nbsp;&nbsp;&nbsp;1. include有返回值，而require没有。 <br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;2. include()包括并运行指定文件 在处理失败时include() 产生一个警告,被导入的程序代码都会被执行，而且这些程序在执行的时候会拥有和源文件中呼叫到include()语句的位置相同的变量范围。你可以导入同一个服务器中的静态页面。 <br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;3. include_once()的作用和include()是几乎相同的 <br/>唯一的差别在于include_once()会先检查要导入的档案是不是已经在该程序中的其它地方被导入过了，如果有的话就不会再次重复导入（这项功能有时候是很重要的，比方说要导入的里面宣告了一些你自行定义好的函数，那么如果在同一个程序重复导入这个文件，在第二次导入的时候便会发生错误讯息，因为PHP不允许相同名称的函数被重复宣告第二次）。 <br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;4. require()会将目标文件的内容读入，并且把自己本身代换成这些读入的内容 在处理失败时require() 则导致一个致命错。 <br/>这个读入并且代换的动作是在PHP引擎编译你的程序代码的时候发生的，而不是发生在PHP引擎开始执行编译好的程序代码的时候（PHP 3.0引擎的工作方式是编译一行执行一行，但是到了PHP 4.0以后就有所改变了，PHP 4.0是先把整个程序代码全部编译完成后，再将这些编译好的程序代码一次执行完毕，在编译的过程中不会执行任何程序代码）。require()通常来导入静态的内容，而include()则适合用导入动态的程序代码。 <br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;5. 如同include_once()，require_once()会先检查目标文件的内容是不是在之前就已经导入过了，如果是的话，便不会再次重复导入同样的内容。 <br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;6. require是无条件包含也就是如果一个流程里加入require,无论条件成立与否都会先执行require。 <br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;7. require通常放在PHP程序的最前面，PHP程序在执行前，就会先读入require所指定引入的文件，使它变成PHP程序网页的一部份。常用的函数，亦可以这个方法将它引入网页中。 <br/><br/>&nbsp;&nbsp;&nbsp;&nbsp;8. include一般是放在流程控制的处理部分中PHP程序网页在读到include的文件时，才将它读进来。这种方式可以把程序执行时的流程简单化。&nbsp;</p><p><br/></p>', 'include|require', '', '0', '0', '1', null, '满天星', '2018-04-17 17:04:33', '2018-04-28 16:03:17', '0', '1', '1');
INSERT INTO `article` VALUES ('31', 'php中传值与传引用的区别?', '6', 'PHP|传值|传引用', 'admin/Upload/20180418/e98c9fe1402a6c6681d781797c9ff467.jpg', '<p style=\"widows: 1; text-align: left;\"><a href=\"https://blog.csdn.net/qq_28194557/article/details/70543753\" target=\"_blank\" title=\"php中传值与传引用的区别?\"><span style=\"color:#111111;font-family:Verdana, Arial, Helvetica, sans-serif\"><span style=\"font-size: 13px; background-color: rgb(255, 255, 255);\"><strong>【<span style=\"color: rgb(120, 128, 135); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; font-size: 14px; line-height: 24px; widows: 1; background-color: rgb(255, 255, 255);\">转载</span>】</strong></span></span></a></p><p style=\"text-align: left;\"><span style=\"box-sizing: border-box; font-weight: 700; color: rgb(69, 69, 69); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; line-height: 24px; widows: 1; background-color: rgb(255, 255, 255);\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; color: rgb(17, 17, 17); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13px;\">php传值</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; color: rgb(17, 17, 17); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13px;\">：在函数范围内，改变变量值得大小，都不不会影响到函数外边的变量值。</span><br/></span><span style=\"color: rgb(69, 69, 69); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; line-height: 24px; widows: 1; background-color: rgb(255, 255, 255);\"></span></p><p style=\"margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: left; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; font-weight: 700;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; color: rgb(17, 17, 17); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13px;\">PHP按引用</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; color: rgb(17, 17, 17); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13px;\">：在函数范围内，对值的任何改变，在函数外部也有所体现，因为传引用传的是内存地址。</span></span></p><p style=\"margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: left; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; font-weight: 700;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; color: rgb(17, 17, 17); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13px;\"><span style=\"box-sizing: border-box; font-weight: 700; color: rgb(79, 79, 79); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; font-size: 18px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">传值</span><span style=\"color: rgb(79, 79, 79); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; font-size: 18px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">：和copy是一样的。【</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; font-size: 18px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">打个比方，我有</span></span></span><span style=\"box-sizing: border-box; font-weight: 700;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; color: rgb(17, 17, 17); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13px;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; font-size: 18px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">一橦房子，我给你建筑材料，你建了一个根我的房子一模一样的房子，你在你的房子做什么事都不会影响到我，我在我的房子里做什么事也不会影响到你，彼此独立。</span><span style=\"color: rgb(79, 79, 79); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; font-size: 18px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">】</span></span></span></p><p style=\"margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; font-weight: 700;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; color: rgb(17, 17, 17); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13px;\"><span style=\"color: rgb(79, 79, 79); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; font-size: 18px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\"></span></span></span></p><p><span style=\"color:#111111;font-family:Verdana, Arial, Helvetica, sans-serif\"><span style=\"font-size: 13px; background-color: rgb(255, 255, 255);\"></span></span></p><p>&lt;?php &nbsp; &nbsp; &nbsp;</p><ol class=\"dp-c list-paddingleft-2\" style=\"margin-bottom: 24px; padding: 0px; box-sizing: border-box; list-style-position: initial; list-style-image: initial; border-style: none solid none none; border-right-width: 1px; border-right-color: rgb(231, 229, 220); color: rgb(92, 92, 92); font-family: Consolas, &quot;Courier New&quot;, Courier, mono, serif; font-size: 12px; line-height: 24px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><li><p><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(0, 0, 0); background-color: inherit;\"><span class=\"vars\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(221, 0, 0); background-color: inherit;\">$testa</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">=1;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span class=\"comment\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(0, 130, 0); background-color: inherit;\">//定义变量a&nbsp;</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">&nbsp;&nbsp;</span></span></p></li><li><p><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(0, 0, 0); background-color: inherit;\"><span class=\"vars\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(221, 0, 0); background-color: inherit;\">$testb</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">=2;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span class=\"comment\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(0, 130, 0); background-color: inherit;\">//定义变量b&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">&nbsp;&nbsp;</span></span></p></li><li><p><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(0, 0, 0); background-color: inherit;\"><span class=\"vars\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(221, 0, 0); background-color: inherit;\">$testb</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">&nbsp;=&nbsp;</span><span class=\"vars\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(221, 0, 0); background-color: inherit;\">$testa</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span class=\"comment\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(0, 130, 0); background-color: inherit;\">//变量a赋值给变量b&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">&nbsp;&nbsp;</span></span></p></li><li><p><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(0, 0, 0); background-color: inherit;\"><span class=\"func\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">echo</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">&nbsp;</span><span class=\"vars\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(221, 0, 0); background-color: inherit;\">$testb</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span class=\"comment\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(0, 130, 0); background-color: inherit;\">//显示为1&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">&nbsp;&nbsp;</span></span></p></li><li><p><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(0, 0, 0); background-color: inherit;\">?&gt;</span></p></li></ol><p><br/></p><p style=\"margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; font-weight: 700;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; color: rgb(17, 17, 17); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 13px;\"><span style=\"color: rgb(79, 79, 79); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; font-size: 18px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\"></span></span></span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; line-height: 26px; text-align: justify; widows: 1; font-size: 18px; background-color: rgb(255, 255, 255);\"><span style=\"box-sizing: border-box; font-weight: 700;\">传引用</span></span><span style=\"color: rgb(79, 79, 79); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">：</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; line-height: 26px; text-align: justify; widows: 1; font-size: 18px; background-color: rgb(255, 255, 255);\">类似于C语言的指针了，感觉差不多。打个比方，我有一橦房子，我给你一把钥匙，我们二个都可以进入这个房子，你在房子做什么都会影响到我。</span></p><p><span style=\"background-color: rgb(255, 255, 255); color: rgb(79, 79, 79); text-align: justify; widows: 1;\">&lt;?php &nbsp; &nbsp; &nbsp;</span><br/></p><ol class=\"dp-c list-paddingleft-2\" style=\"margin-bottom: 24px; padding: 0px; box-sizing: border-box; list-style-position: initial; list-style-image: initial; border-style: none solid none none; border-right-width: 1px; border-right-color: rgb(231, 229, 220); color: rgb(92, 92, 92); background-color: rgb(255, 255, 255);\"><li><p><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(0, 0, 0); background-color: inherit;\"><span class=\"vars\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(221, 0, 0); background-color: inherit;\">$param2</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">=1;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span class=\"comment\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(0, 130, 0); background-color: inherit;\">//定义变量2&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">&nbsp;&nbsp;</span></span></p></li><li><p><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(0, 0, 0); background-color: inherit;\"><span class=\"vars\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(221, 0, 0); background-color: inherit;\">$param1</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">&nbsp;=&nbsp;&amp;</span><span class=\"vars\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(221, 0, 0); background-color: inherit;\">$param2</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span class=\"comment\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(0, 130, 0); background-color: inherit;\">//将变量2的引用传给变量1&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">&nbsp;&nbsp;</span></span></p></li><li><p><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(0, 0, 0); background-color: inherit;\"><span class=\"func\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">echo</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">&nbsp;</span><span class=\"vars\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(221, 0, 0); background-color: inherit;\">$param2</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span class=\"comment\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(0, 130, 0); background-color: inherit;\">//显示为1&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">&nbsp;&nbsp;</span></span></p></li><li><p><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(0, 0, 0); background-color: inherit;\"><span class=\"vars\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(221, 0, 0); background-color: inherit;\">$param1</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">&nbsp;=&nbsp;2;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span class=\"comment\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(0, 130, 0); background-color: inherit;\">//把2赋值给变量1&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">&nbsp;&nbsp;</span></span></p></li><li><p><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(0, 0, 0); background-color: inherit;\"><span class=\"func\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">echo</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">&nbsp;</span><span class=\"vars\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(221, 0, 0); background-color: inherit;\">$param2</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span class=\"comment\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(0, 130, 0); background-color: inherit;\">//显示为2&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; background-color: inherit;\">&nbsp;&nbsp;</span></span></p></li><li><p><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; color: rgb(0, 0, 0); background-color: inherit;\">?&gt;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p><span style=\"color: rgb(69, 69, 69); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; font-size: 18px; line-height: 24px; widows: 1; background-color: rgb(255, 255, 255);\">【优缺点：】传值会很耗时间，特别是对于大型的字符串和对象来说，这将会是一个代价很大的操作,传送引用，函数内的任何操作等同于对传送变量的操作，传送大型变量时效率高！</span><br/></p></li></ol>', '传值|传引用', '', '0', '0', '8', null, '满天星', '2018-04-17 17:17:11', '2018-04-28 16:03:17', '0', '1', '1');
INSERT INTO `article` VALUES ('32', 'MVC分别指哪三层，有什么优点？', '2', 'MVC', 'admin/Upload/20180418/22f1cdfcb5ee7d45d4300f69a38f2272.jpg', '<pre class=\"best-text mb-10\" style=\"margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Microsoft YaHei&quot;, arial, 宋体, sans-serif, tahoma; white-space: pre-wrap; word-wrap: break-word; line-height: 29px; color: rgb(51, 51, 51); widows: 1; min-height: 55px; background-color: rgb(255, 255, 255);\">MVC是由Model（模型）放置业务实体，表示操作数据；\r\nView（视图）放置UI模板文件，负责展示输出结果；\r\nController（控制器）放置Controller类，处理URL请求&nbsp;组成的。</pre><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 25.2px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">MVC模式的目的就是实现Web系统的职能分工。</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 25.2px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><strong style=\"margin: 0px; padding: 0px;\">1. MVC模式的好处：</strong></p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 25.2px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">1.各施其职，互不干涉</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 25.2px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">在MVC模式中，三个层各施其职，所以如果一旦哪一层的需求发生了变化，就只需要更改相应的层中的代码而不会影响到其它层中的代码。</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 25.2px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">2.有利于开发中的分工</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 25.2px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">在MVC模式中，由于按层把系统分开，那么就能更好的实现开发中的分工。网页设计人员可以进行开发视图层中的JSP，对业务熟悉的开发人员可开发业务层，而其它开发人员可开发控制层。</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 25.2px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">3.有利于组件的重用</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 25.2px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">分层后更有利于组件的重用。如控制层可独立成一个能用的组件，视图层也可做成通用的操作界面。</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 25.2px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><strong style=\"margin: 0px; padding: 0px;\">2. MVC模式的不足</strong></p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 25.2px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">MVC的不足体现在以下几个方面：</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 25.2px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">(1)增加了系统结构和实现的复杂性。对于简单的界面，严格遵循MVC，使模型、视图与控制器分离，会增加结构的复杂性，并可能产生过多的更新操作，降低运行效率。</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 25.2px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">(2)视图与控制器间的过于紧密的连接。视图与控制器是相互分离，但确实联系紧密的部件，视图没有控制器的存在，其应用是很有限的，反之亦然，这样就妨碍了他们的独立重用。</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 25.2px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">(3)视图对模型数据的低效率访问。依据模型操作接口的不同，视图可能需要多次调用才能获得足够的显示数据。对未变化数据的不必要的频繁访问，也将损害操作性能。</p><p style=\"margin: 10px auto; padding: 0px; color: rgb(51, 51, 51); font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 25.2px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">(4) 目前，一般高级的界面工具或构造器不支持MVC模式。改造这些工具以适应MVC需要和建立分离的部件的代价是很高的，从而造成使用MVC的困难。</p><p><br/></p>', 'MVC', '', '0', '0', '0', null, '满天星', '2018-04-17 17:38:37', '2018-04-28 16:03:18', '0', '1', '1');
INSERT INTO `article` VALUES ('33', 'COOKIE和SESSION有什么区别？', '6', 'COOKIE和SESSION', 'admin/Upload/20180418/382c5900d3e7a78cbd5c61d11a23ec84.jpg', '<p><a href=\"http://https://www.zhihu.com/question/19786827/answer/28752144\" target=\"_blank\" title=\"COOKIE和SESSION有什么区别\">【转载】</a></p><p>&nbsp;&nbsp;&nbsp;&nbsp;1. 由于HTTP协议是无状态的协议，所以服务端需要记录用户的状态时，就需要用某种机制来识具体的用户，这个机制就是Session.典型的场景比如购物车，当你点击下单按钮时，由于HTTP协议无状态，所以并不知道是哪个用户操作的，所以服务端要为特定的用户创建了特定的Session，用用于标识这个用户，并且跟踪用户，这样才知道购物车里面有几本书。这个Session是保存在服务端的，有一个唯一标识。在服务端保存Session的方法很多，内存、数据库、文件都有。集群的时候也要考虑Session的转移，在大型的网站，一般会有专门的Session服务器集群，用来保存用户会话，这个时候 Session 信息都是放在内存的，使用一些缓存服务比如Memcached之类的来放 Session。<br/>&nbsp;&nbsp;&nbsp;&nbsp;2. 思考一下服务端如何识别特定的客户？这个时候Cookie就登场了。每次HTTP请求的时候，客户端都会发送相应的Cookie信息到服务端。实际上大多数的应用都是用 Cookie 来实现Session跟踪的，第一次创建Session的时候，服务端会在HTTP协议中告诉客户端，需要在 Cookie 里面记录一个Session ID，以后每次请求把这个会话ID发送到服务器，我就知道你是谁了。有人问，如果客户端的浏览器禁用了 Cookie 怎么办？一般这种情况下，会使用一种叫做URL重写的技术来进行会话跟踪，即每次HTTP交互，URL后面都会被附加上一个诸如 sid=xxxxx 这样的参数，服务端据此来识别用户。<br/>&nbsp;&nbsp;&nbsp;&nbsp;3. Cookie其实还可以用在一些方便用户的场景下，设想你某次登陆过一个网站，下次登录的时候不想再次输入账号了，怎么办？这个信息可以写到Cookie里面，访问网站的时候，网站页面的脚本可以读取这个信息，就自动帮你把用户名给填了，能够方便一下用户。这也是Cookie名称的由来，给用户的一点甜头。<br/>所以，总结一下：<br/>Session是在服务端保存的一个数据结构，用来跟踪用户的状态，这个数据可以保存在集群、数据库、文件中；<br/>Cookie是客户端保存用户信息的一种机制，用来记录用户的一些信息，也是实现Session的一种方式。</p><p><br/></p>', 'COOKIE和SESSION', '', '0', '0', '0', null, '满天星', '2018-04-17 17:45:08', '2018-04-28 16:03:18', '0', '1', '1');
INSERT INTO `article` VALUES ('34', '（mysql）请写出数据类型（int char varchar datetime text）的意思，varchar和char有什么区别。', '14', 'mysql数据类型', 'admin/Upload/20180418/4a266dee9a63eeb6509434514d784fe8.jpg', '<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; font-size: 14px; text-indent: 2em; line-height: 32px; font-family: 宋体; color: rgb(51, 51, 51); white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-align: left;\">int : 数值类型</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; font-size: 14px; text-indent: 2em; line-height: 32px; font-family: 宋体; color: rgb(51, 51, 51); white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-align: left;\">char : 固定长度字符串类型</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; font-size: 14px; text-indent: 2em; line-height: 32px; font-family: 宋体; color: rgb(51, 51, 51); white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-align: left;\">varchar : 可变长度字符串类型</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; font-size: 14px; text-indent: 2em; line-height: 32px; font-family: 宋体; color: rgb(51, 51, 51); white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-align: left;\">datetime : 时期时间类型</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; font-size: 14px; text-indent: 2em; line-height: 32px; font-family: 宋体; color: rgb(51, 51, 51); white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-align: left;\">text : 文本类型</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\"><strong>varchar和char 的区别：</strong></p><h3 style=\"font-size: 16px; line-height: 1.5; margin: 10px 0px; font-family: 微软雅黑, 宋体, Arial; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">区别一，定长和变长</h3><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">char 表示定长，长度固定，varchar表示变长，即长度可变。当所插入的字符串超出它们的长度时，视情况来处理，如果是严格模式，则会拒绝插入并提示错误信息，如果是宽松模式，则会截取然后插入。如果插入的字符串长度小于定义长度时，则会以不同的方式来处理，如char（10），表示存储的是10个字符，无论你插入的是多少，都是10个，如果少于10个，则用空格填满。而varchar（10），小于10个的话，则插入多少个字符就存多少个。<br/>varchar怎么知道所存储字符串的长度呢？实际上，对于varchar字段来说，需要使用一个（如果字符串长度小于255）或两个字节（长度大于255）来存储字符串的长度。但是因为他需要有一个prefix来表示他具体bytes数是多少（因为varchar是变长的，没有这个长度值他不知道如何读取数据）。</p><h3 style=\"font-size: 16px; line-height: 1.5; margin: 10px 0px; font-family: 微软雅黑, 宋体, Arial; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">区别之二，存储的容量不同</h3><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">对 char 来说，最多能存放的字符个数 255，和编码无关。<br/>而 varchar 呢，最多能存放 65532 个字符。VARCHAR 的最大有效长度由最大行大小和使用的字符集确定。整体最大长度是 65,532字节</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">在MySQL中用来判断是否需要进行对据列类型转换的规则</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">1、在一个数据表里，如果每一个数据列的长度都是固定的，那么每一个数据行的长度也将是固定的．</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">2、只要数据表里有一个数据列的长度的可变的，那么各数据行的长度都是可变的．</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">3、如果某个数据表里的数据行的长度是可变的，那么，为了节约存储空间，MySQL会把这个数据表里的固定长度类型的数据列转换为相应的可变长度类型．例外：长度小于4个字符的char数据列不会被转换varchar类型</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\"><strong>三.&nbsp;</strong>MySQL中varchar最大长度是多少？</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">这不是一个固定的数字。先简要说明一下限制规则。</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\"><strong>1、限制规则</strong></p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">字段的限制在字段定义的时候有以下规则：</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">a) 存储限制</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">varchar 字段是将实际内容单独存储在聚簇索引之外，内容开头用1到2个字节表示实际长度（长度超过255时需要2个字节），因此最大长度不能超过65535字符。</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">b) 编码长度限制</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">字符类型若为gbk，每个字符最多占2个字节，最大长度不能超过32766;</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">字符类型若为utf8，每个字符最多占3个字节，最大长度不能超过21845。</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">若定义的时候超过上述限制，则varchar字段会被强行转为text类型，并产生warning。</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">c) 行长度限制</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\"><span style=\"\">导致实际应用中varchar长度限制的是一个行定义的长度</span>。 MySQL要求一个行的定义长度不能超过65535bytes。若定义的表长度超过这个值，则提示</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">ERROR 1118 (42000): Row size too large. The maximum row size for the used table type, not counting BLOBs, is 65535. You have to change some columns to TEXT or BLOBs。</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">由字符集来确定，字符集分单字节和多字节<br/>Latin1 一个字符占一个字节，最多能存放 65532 个字符<br/>GBK 一个字符占两个字节， 最多能存 32766 个字符<br/>UTF8 一个字符占三个字节， 最多能存 21844 个字符<br/>注意，char 和 varchar 后面的长度表示的是字符的个数，而不是字节数。</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\"><strong>2、计算例子</strong></p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">举两个例说明一下实际长度的计算。</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">a) 若一个表只有一个varchar类型，如定义为</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">create table t4(c varchar(N)) charset=gbk;</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">则此处N的最大值为(65535-1-2)/2= 32766 个字符。</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">减1的原因是实际行存储从第二个字节开始’;</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">减2的原因是varchar头部的2个字节表示长度;</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">除2的原因是字符编码是gbk。</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">b) 若一个表定义为</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">create table t4(c int, c2 char(30), c3 varchar(N)) charset=utf8;</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">则此处N的最大值为 (65535-1-2-4-30*3)/3=21812</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">减1和减2与上例相同;</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">减4的原因是int类型的c占4个字节;</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">减30*3的原因是char(30)占用90个字节，编码是utf8。</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\">如果被varchar超过上述的b规则，被强转成text类型，则每个字段占用定义长度为11字节，当然这已经不是“varchar”了。</p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\"><strong>则此处N的最大值为 (65535-1-2-4-30*3)/3=21812</strong></p><p style=\"margin: 10px auto; line-height: 24px; font-family: 微软雅黑, 宋体, Arial; font-size: 15px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left;\"><strong>create table t4(c int, c2 char(30), c3 varchar(21812)) ENGINE=InnoDB DEFAULT CHARSET=utf8</strong></p><p><br/></p>', 'mysql数据类型', '', '0', '0', '1', null, '满天星', '2018-04-17 17:46:55', '2018-04-28 16:03:18', '0', '1', '1');
INSERT INTO `article` VALUES ('35', 'PHP isset()与empty()的使用区别详解', '6', 'isset和empty', 'admin/Upload/20180418/304711d85cf6b6c198cba824b8c79903.jpg', '<h3 style=\"font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; margin: 8px 0px 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-size: 22px; line-height: 30px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">1、empty</h3><p style=\"margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">判断一个变量是否为空</p><h3 style=\"font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; margin: 8px 0px 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); font-size: 22px; line-height: 30px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><a style=\"color: rgb(78, 161, 219); outline: 0px; margin: 0px; padding: 0px; font-weight: 400; box-sizing: border-box;\"></a>2、isset</h3><p style=\"margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; color: rgb(79, 79, 79); line-height: 26px; text-align: justify; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\">判断一个变量是否设置</p><p><br/></p>', 'isset和empty', '通过对PHP语言的学习，应该知道它是基于函数的一款HTML脚本语言。庞大的函数库支持着PHP语言功能的实现。下面我们为大家介绍有关PHP函数isset()与empty()的相关用法。', '0', '0', '0', null, '满天星', '2018-04-17 17:56:31', '2018-04-28 16:03:18', '0', '1', '1');
INSERT INTO `article` VALUES ('36', 'echo print() print_r() var_dump()的区别', '6', 'echo、print()、print_r()、var_dump()', 'admin/Upload/20180418/e4afddd1ccc58d3cf678b9bcaa54b009.jpg', '<p style=\"margin: 10px auto; padding: 0px; box-sizing: border-box; font-size: 14px; color: rgb(51, 51, 51); line-height: 25px; text-align: justify; white-space: normal; widows: 1; font-family: verdana, Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">常见的输出语句</p><p style=\"margin: 10px auto; padding: 0px; box-sizing: border-box; font-size: 14px; color: rgb(51, 51, 51); line-height: 25px; text-align: justify; white-space: normal; widows: 1; font-family: verdana, Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; line-height: 1.8; color: rgb(255, 102, 0);\">echo()</span></span><br/>可以一次输出多个值，多个值之间用逗号分隔。echo是语言结构(language construct)，而并不是真正的函数，因此不能作为表达式的一部分使用。</p><p style=\"margin: 10px auto; padding: 0px; box-sizing: border-box; font-size: 14px; color: rgb(51, 51, 51); line-height: 25px; text-align: justify; white-space: normal; widows: 1; font-family: verdana, Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; line-height: 1.8; color: rgb(255, 102, 0);\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box;\">print()</span></span><br/>函数print()打印一个值（它的参数），如果字符串成功显示则返回true，否则返回false。</p><p style=\"margin: 10px auto; padding: 0px; box-sizing: border-box; font-size: 14px; color: rgb(51, 51, 51); line-height: 25px; text-align: justify; white-space: normal; widows: 1; font-family: verdana, Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; line-height: 1.8; color: rgb(255, 102, 0);\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box;\">print_r()</span></span><br/>可以把字符串和数字简单地打印出来，而数组则以括起来的键和值得列表形式显示，并以Array开头。但print_r()输出布尔值和NULL的结果没有意义，因为都是打印&quot;\\n&quot;。因此用var_dump()函数更适合调试。</p><p style=\"margin: 10px auto; padding: 0px; box-sizing: border-box; font-size: 14px; color: rgb(51, 51, 51); line-height: 25px; text-align: justify; white-space: normal; widows: 1; font-family: verdana, Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; line-height: 1.8; color: rgb(255, 102, 0);\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box;\">var_dump()</span></span><br/>判断一个变量的类型与长度,并输出变量的数值,如果变量有值输的是变量的值并回返数据类型。此函数显示关于一个或多个表达式的结构信息，包括表达式的类型与值。数组将递归展开值，通过缩进显示其结构。</p><p><br/></p>', 'echo、print()、print_r()、var_dump()', '', '0', '0', '0', null, '满天星', '2018-04-17 17:58:39', '2018-04-28 16:03:18', '0', '1', '1');
INSERT INTO `article` VALUES ('37', '如何通俗地理解MySQL的三个范式？', '14', 'MySQL范式', 'admin/Upload/20180418/b94b79b020b12aeddcf9ef3e320cd740.jpg', '<p><span style=\"color: rgb(50, 50, 50); font-family: &quot;Century Gothic&quot;, &quot;Microsoft yahei&quot;; line-height: 25.6px; widows: 1; background-color: rgb(255, 255, 255);\">第一范式：原子性，您可以简单的理解我 每一个字段存放的信息是唯一的。</span><br/><span style=\"color: rgb(50, 50, 50); font-family: &quot;Century Gothic&quot;, &quot;Microsoft yahei&quot;; line-height: 25.6px; widows: 1; background-color: rgb(255, 255, 255);\">第二范式：满足第一范式的基础上，表中的每一列的字段都和主键先关</span><br/><span style=\"color: rgb(50, 50, 50); font-family: &quot;Century Gothic&quot;, &quot;Microsoft yahei&quot;; line-height: 25.6px; widows: 1; background-color: rgb(255, 255, 255);\">第三方式：表中的每一列的字段都和主键直接先关，而不是间接相关。</span></p>', 'MySQL范式', '', '0', '0', '14', null, '满天星', '2018-04-17 18:01:18', '2018-04-28 16:03:18', '0', '1', '1');
INSERT INTO `article` VALUES ('38', '面向对象(oo)思想怎么理解？', '2', '面向对象', 'admin/Upload/20180418/e6b17c3eaafd52b9eb274bdaa7d259ca.jpg', '<pre style=\"margin-top: 10px; margin-bottom: 10px; padding: 0px; font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Microsoft YaHei&quot;, arial, 宋体, sans-serif, tahoma; white-space: pre-wrap; word-wrap: break-word; font-size: 14px; line-height: 26px; color: rgb(51, 51, 51); widows: 1; min-height: 55px; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;我理解的面向对象就是把某事物的特点和功能都抽象出来封装成一个类，由这个类来创建具体的对象，比如我们可以把狗封装成一个类，狗有的特点比如有毛，腿，眼睛，耳朵（这个相当于类的成员变量），狗的功能呢比如会叫，咬人（这个就相当于类的成员函数），具体要要用的时候呢，可以根据这个狗（类）来创建小白，小黑，小花等具体的狗（对象）。</pre><p><br/></p>', '面向对象', '', '0', '1', '4', null, '满天星', '2018-04-17 18:07:43', '2018-04-28 16:03:18', '0', '1', '1');
INSERT INTO `article` VALUES ('39', '简述 private、 protected、 public修饰符的访问权限。', '6', ' private、 protected、 public', 'admin/Upload/20180418/46f816a37ecb32d081ff19caf01e0199.jpg', '<p style=\"margin-bottom: 16px\"><a href=\"https://www.nowcoder.com/questionTerminal/62d31b4235444a609a79d8f94092657e\" target=\"_blank\" title=\"简述 private、 protected、 public、 internal 修饰符的访问权限。\"><span style=\"color:#0000ff;font-family:宋体\">【转载】</span></a></p><p><span style=\"font-size:16px;font-family:宋体;color:#666666\">private : </span><span style=\"font-size: 16px;font-family:宋体;color:#666666\">私有成员, 在类的内部才可以访问。 </span></p><p><span style=\"font-size:16px;font-family:宋体;color:#666666\">protected : </span><span style=\"font-size: 16px;font-family:宋体;color:#666666\">保护成员，该类内部和继承类中可以访问。 </span></p><p><span style=\"font-size:16px;font-family:宋体;color:#666666\">public : </span><span style=\"font-size: 16px;font-family:宋体;color:#666666\">公共成员，完全公开，没有访问限制。 </span></p><p><span style=\"font-size:16px;font-family:宋体;color:#666666\">internal: </span><span style=\"font-size: 16px;font-family:宋体;color:#666666\">在同一命名空间内可以访问。</span></p><p><br/></p>', ' private、 protected、 public', '', '0', '0', '0', null, '满天星', '2018-04-17 18:10:58', '2018-04-28 16:03:18', '0', '1', '1');
INSERT INTO `article` VALUES ('40', '什么是构造函数，什么是析构函数，作用是什么？', '6', '构造、析构', 'admin/Upload/20180418/60d178f8d3543b5ae101092d9e01d536.jpg', '<p><span style=\"background-color: rgb(255, 255, 255);\"><span style=\"color: rgb(57, 57, 57); font-family: verdana, &quot;ms song&quot;, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 21px; widows: 1;\"></span></span></p><pre style=\"padding: 0px; margin-top: 0px; margin-bottom: 0px; white-space: pre-wrap; font-family: &quot;Microsoft YaHei&quot;, SimHei, SimSun, Arial, sans-serif; word-break: break-all; word-wrap: break-word; color: rgb(34, 34, 34); font-size: 14px; line-height: 22px; widows: 1; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;构造函数是一个特殊的成员函数，能够自动初始化对象。（不论何时创建对象，该特殊的成员函数，即构造函数都将被执行）\r\n&nbsp;&nbsp;&nbsp;&nbsp;构造函数用于创建对象并使用传递到其中的参数初始化数据成员。此对象正像任意变量一样，在作用域规则之后仍然存在。当对象不存在是，即它的作用域结束时，还会调用名为析构函数的另一特殊成员函数。当对象将要被自动销毁时，如果存在一个析构函数，则会自动调用该析构函数。\r\n&nbsp;&nbsp;&nbsp;&nbsp;析构函数是在一个销毁对象时自动调用的成员函数。\r\n&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;构造函数的作用：用于新建对象的初始化工作。\r\n&nbsp;&nbsp;&nbsp;&nbsp;析构函数的作用：用于在撤销对象前，完成一些清理工作，比如：释放内存等。</pre>', '构造、析构', '', '0', '0', '0', null, '满天星', '2018-04-17 18:17:10', '2018-04-28 16:03:18', '0', '1', '1');
INSERT INTO `article` VALUES ('41', '$this和self、parent这三个关键词分别代表什么？在哪些场合下使用？', '6', '$this、self、parent', 'admin/Upload/20180418/1cbe624e6410d9234d49a33750aea2b8.jpg', '<p style=\"margin: 10px auto; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 25.2px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><span style=\"background-image: none; background-position: 0% 0%; background-size: initial; background-repeat: repeat; background-attachment: scroll; background-origin: initial; background-clip: initial;\"><span style=\"font-family: 宋体;\">$this：</span></span><span style=\"background-image: none; background-position: 0% 0%; background-size: initial; background-repeat: repeat; background-attachment: scroll; background-origin: initial; background-clip: initial;\"><span style=\"font-family: 宋体;\">当前对象</span></span></p><p style=\"margin: 10px auto; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 25.2px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><span style=\"background-image: none; background-position: 0% 0%; background-size: initial; background-repeat: repeat; background-attachment: scroll; background-origin: initial; background-clip: initial;\">self： 当前类</span></p><p style=\"margin: 10px auto; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 25.2px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><span style=\"background-image: none; background-position: 0% 0%; background-size: initial; background-repeat: repeat; background-attachment: scroll; background-origin: initial; background-clip: initial;\">parent： 当前类的父类</span></p><p style=\"margin: 10px auto; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 25.2px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><span style=\"background-image: none; background-position: 0% 0%; background-size: initial; background-repeat: repeat; background-attachment: scroll; background-origin: initial; background-clip: initial;\">$this在当前类中使用,使用-&gt;调用属性和方法。</span></p><p style=\"margin: 10px auto; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 25.2px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><span style=\"background-image: none; background-position: 0% 0%; background-size: initial; background-repeat: repeat; background-attachment: scroll; background-origin: initial; background-clip: initial;\">self也在当前类中使用，不过需要使用::调用。</span></p><p style=\"margin: 10px auto; font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 25.2px; white-space: normal; widows: 1; background-color: rgb(255, 255, 255);\"><span style=\"background-image: none; background-position: 0% 0%; background-size: initial; background-repeat: repeat; background-attachment: scroll; background-origin: initial; background-clip: initial;\">静态属性，不能在类里使用$this访问，应该使用self后跟范围解析操作符(::),后面是带着美元符号的变量名。</span></p><p><br/></p>', '$this、self、parent', '', '5', '0', '26', null, '满天星', '2018-04-17 18:18:49', '2018-04-28 16:03:18', '0', '1', '1');
INSERT INTO `article` VALUES ('42', ' php代码优化方法，加速运行速度的技巧总结', '6', 'php', 'admin/Upload/20180419/4f827ae6d991dc05f55bb3df874c81d9.jpg', '<p><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">1. 尽量采用大量的PHP内置函数。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">2. echo 比 print 快。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">3. 不要把方法细分得过多，仔细想想你真正打算重用的是哪些代码？</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">4. 在执行for循环之前确定最大循环数，不要每循环一次都计算最大值。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">5. 注销那些不用的变量尤其是大数组，以便释放内存。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">6. 并非要用类实现所有的数据结构，数组也很有用。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">7. $row[&#39;id&#39;]的效率是$row[id]的7倍。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">8. 在包含文件时使用完整路径，解析操作系统路径所需的时间会更少。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">9. 如果你想知道脚本开始执行（译注：即服务器端收到客户端请求）的时刻，使用$_SERVER[‘REQUEST_TIME’]要好于time()。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">10. 检查是否能用strncasecmp，strpbrk，stripos函数代替正则表达式完成相同功能。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">11. str_replace函数比preg_replace函数快，但strtr函数的效率是str_replace函数的四倍。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">12. 如果一个字符串替换函数，可接受数组或字符作为参数，并且参数长度不太长，那么可以考虑额外写一段替换代码，使得每次传递参数是一个字符，而不是只写一行代码接受数组作为查询和替换的参数。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">13. 使用选择分支语句（译注：即switch case）好于使用多个if，else if语句。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">14. 用@屏蔽错误消息的做法非常低效。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">15. 打开apache的mod_deflate模块。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">16. 数据库连接当使用完毕时应关掉。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">18. 错误消息代价昂贵。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">19. 尽量不要在for循环中使用函数，比如for ($x=0; $x &lt; count($array); $x)每循环一次都会调用count()函数。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">20. 在方法中递增局部变量，速度是最快的。几乎与在函数中调用局部变量的速度相当。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">21. 递增一个全局变量要比递增一个局部变量慢2倍。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">22. 递增一个对象属性（如：$this-&gt;prop++）要比递增一个局部变量慢3倍。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">23. 递增一个未预定义的局部变量要比递增一个预定义的局部变量慢9至10倍。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">24. 仅定义一个局部变量而没在函数中调用它，同样会减慢速度（其程度相当于递增一个局部变量）。PHP大概会检查看是否存在全局变量。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">25. 方法调用看来与类中定义的方法的数量无关，因为我（在测试方法之前和之后都）添加了10个方法，但性能上没有变化。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">26. 派生类中的方法运行起来要快于在基类中定义的同样的方法。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">27. 调用带有一个参数的空函数，其花费的时间相当于执行7至8次的局部变量递增操作。类似的方法调用所花费的时间接近于15次的局部变量递增操作。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">28. 用单引号代替双引号来包含字符串，这样做会更快一些。因为PHP会在双引号包围的字符串中搜寻变量，单引号则不会。当然，只有当你不需要在字符串中包含变量时才可以这么做。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">29. 用echo 输出多个字符串时，用逗号代替句点来分隔字符串，速度更快。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">30. Apache解析一个PHP脚本的时间要比解析一个静态HTML页面慢2至10倍。尽量多用静态HTML页面，少用脚本。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">31. 除非脚本可以缓存，否则每次调用时都会重新编译一次。引入一套PHP缓存机制通常可以提升25%至100%的性能，以免除编译开销。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">32. 尽量做缓存，可使用memcached。memcached是一款高性能的内存对象缓存系统，可用来加速动态Web应用程序，减轻数据库负载。对运算码(OP code)的缓存很有用，使得脚本不必为每个请求做重新编译。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">33. 当操作字符串并需要检验其长度是否满足某种要求时，你想当然地会使用strlen()函数。此函数执行起来相当快，因为它不做任何计算，只返回在zval 结构（C的内置数据结构，用于存储PHP变量）中存储的已知字符串长度。但是，由于strlen()是函数，多多少少会有些慢，因为函数调用会经过诸多步骤，如字母小写化（译注：指函数名小写化，PHP不区分函数名大小写）、哈希查找，会跟随被调用的函数一起执行。在某些情况下，你可以使用isset() 技巧加速执行你的代码。</span><br/><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">Ex.（举例如下）</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">[phpif (strlen($foo) &lt; 5) { echo &quot;Foo is too short&quot;; }[/php]</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">vs.（与下面的技巧做比较）</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">查看源代码打印帮助1</span><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; white-space: pre; background-color: rgb(255, 255, 255);\"></span><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">if (!isset($foo{5})) { echo &quot;Foo is too short&quot;; }</span><br/></p><p style=\"margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; font-size: 14px; color: rgb(51, 51, 51); line-height: 26px; text-align: justify; word-wrap: break-word; word-break: normal; white-space: normal; widows: 1; font-family: Arial; background-color: rgb(255, 255, 255);\">调用isset()恰巧比strlen()快，因为与后者不同的是，isset()作为一种语言结构，意味着它的执行不需要函数查找和字母小写化。也就是说，实际上在检验字符串长度的顶层代码中你没有花太多开销。</p><p style=\"margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; font-size: 14px; color: rgb(51, 51, 51); line-height: 26px; text-align: justify; word-wrap: break-word; word-break: normal; white-space: normal; widows: 1; font-family: Arial; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; font-size: 14px; color: rgb(51, 51, 51); line-height: 26px; text-align: justify; word-wrap: break-word; word-break: normal; white-space: normal; widows: 1; font-family: Arial; background-color: rgb(255, 255, 255);\">34. 当执行变量$i的递增或递减时，$i++会比++$i慢一些。这种差异是PHP特有的，并不适用于其他语言，所以请不要修改你的C或Java代码并指望它们能立即变快，没用的。++$i更快是因为它只需要3条指令(opcodes)，$i++则需要4条指令。后置递增实际上会产生一个临时变量，这个临时变量随后被递增。而前置递增直接在原值上递增。这是最优化处理的一种，正如Zend的PHP优化器所作的那样。牢记这个优化处理不失为一个好主意，因为并不是所有的指令优化器都会做同样的优化处理，并且存在大量没有装配指令优化器的互联网服务提供商（ISPs）和服务器。</p><p><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">35. 并不是事必面向对象(OOP)，面向对象往往开销很大，每个方法和对象调用都会消耗很多内存。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">36. 如果在代码中存在大量耗时的函数，你可以考虑用C扩展的方式实现它们。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">37. 评估检验(profile)你的代码。检验器会告诉你，代码的哪些部分消耗了多少时间。Xdebug调试器包含了检验程序，评估检验总体上可以显示出代码的瓶颈。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">38. mod_zip可作为Apache模块，用来即时压缩你的数据，并可让数据传输量降低80%</span><br/><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">PHP是一种执行起来非常迅速的编程语言，但是比起仅仅优化代码来说仍然值得优化PHP本身。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">本文我们将根据一些实效阐述为什么优化PHP本身要比优化代码来的更贴切，以及为什么需要理解根据PHP在你的服务器上其他相关子系统的表现找出瓶颈并修复之。与此同时，我们也提到了如何优化您的PHP代码来让他们拥有更快的执行速度。</span><br/></p><p style=\"margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; font-size: 14px; color: rgb(51, 51, 51); line-height: 26px; text-align: justify; word-wrap: break-word; word-break: normal; white-space: normal; widows: 1; font-family: Arial; background-color: rgb(255, 255, 255);\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 16px; padding: 0px; box-sizing: border-box; font-size: 14px; color: rgb(51, 51, 51); line-height: 26px; text-align: justify; word-wrap: break-word; word-break: normal; white-space: normal; widows: 1; font-family: Arial; background-color: rgb(255, 255, 255);\">获得高性能</p><p><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">当我们谈及好的性能，往往不仅仅是指您的PHP代码执行起来有多快。性能是一套在可量化评测和速度之间取出的平衡。仅仅依靠使用更少资源的代码执行起来也许比在高速缓存中之行的代码更慢，并且相同的一组（在高速缓冲中执行的）代码可以在同时并发执行在一台Web服务器上。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">在下面的例子中，A.php算作一位尽可能跑得快的赛跑选手，而B.php是一个几乎可以以同一慢速永远跑下去的马拉松选手。轻负荷情况下，A.php可以充分的快，但是当流量增加后，B.php的性能表现将仅仅降低一点点而A.php会垮掉。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">让我们来通过一个事实来验证此说法更深远的本质意义。假设我们需要读取一个250K的文件并生成一个关于此文件的HTML概要。我们写了两个脚本来做同样一件事：hare.php将一次性读取整个文件到内存中，然后一步执行到位；而tortoise.php每次只读取文件的一行，并且决不超过内容容量。结果Tortoise.php因为多次读写需要更多的系统回应而慢得多。”</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">程序每执行一次，hare.php需要0.04秒CPU执行时间和10Mb的内存，而tortoise.php需要0.06秒CPU执行时间和5Mb 的内存。服务器共有100Mb实际内存容量并且其CPU有99%是空闲的。我们同时假定执行这样一个简单事件不产生内存碎片。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">当有10各程序并发执行时，hare.php将发生内存溢出（10 ×10 = 100）。与此同时，tortoise.php仍将有50Mb空余内存可用！11个程序并发执行将使hare.php彻底“溃败”因为它开始需要使用虚拟内存——执行速度有可能降低到其常规速度的一半以下；而且现在每一个单独程序进程需要0.08秒CPU执行时间。而此期间，tortoise.php仍旧运行在其常规CPU执行时间——0.06秒！</span><br/><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">以下表格中，执行得更快的PHP脚本使用粗体区分开来：</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">[/php]</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">如您在上例中看到的，获得更好的性能不再仅仅是写出执行起来更快的PHP程序。高性能PHP表现需要对底层硬件知识以及操作系统、软件支持如Web服务器、数据库等有一个良好认识和理解。</span><br/><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">瓶颈</span><br/><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">以上两个例子让我们看到了(性能)下降的瓶颈所在。当拥有无限大容量的内存时，hare.php的确是始终比tortoise.php快。但是，仅仅认为内存是PHP整体性能的瓶颈所在显得过于单纯——实际上远不止这些：</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">(a) 网络</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">你的网络有可能是最大的瓶颈所在。如果你有10M的带宽——最多你只能获得1M/秒的传输速度。如果假设每个PHP页为30k，那么每秒仅仅只传输 33页就将使你的网络带宽达到饱和。更多导致瓶颈产生的因素包括频繁访问低速DNS，或者网络设备仅能获得十分有限的存储。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">(b) CPU</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">如果你监视一下你的CPU负荷情况，发送一个纯静态HTML页面并不会增加CPU负担——就像我们以上提到的，此时瓶颈在于网络。当然啦，对于由 PHP生成的复杂动态页面，你的CPU速度自然将成为限制因素之一。拥有包含多个CPU的服务器或者一个服务器阵列将减轻因CPU带来的影响。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">(c) 共享存储</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">共享存储主要在通讯进程中发挥作用，或者是用于储存公共资源（比如多个CPU间的缓存数据或代码）。如果共享存储分配不足，同样将导致瓶颈的发生。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">(d) 文件系统</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">访问硬盘的速度至少比直接读取内存要慢50到100倍。使用高速缓存将使情况有所好转。然而低内存空间将适用于文件缓冲的内存（通常称为虚拟内存）空间减少，进而导致整体性能下降。虚拟内存容易产生大量文件碎片，导致访问硬盘速度下降。在Unix系统上使用大量链接标记同样也会石访问硬盘的速度下降。缺省Linux安装模式下的默认硬盘访问设置也是很不被看好的，因为它是为了兼容性而不是速度。必要时请使用hdparm命令来更改Linux硬盘配置。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">(e) 进程管理</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">在某些操作系统上（比如说Windows系统）创建一个新的进程是很缓慢的操作。这意味着在这些操作系统上作涉及到进程创建的CGI应用会慢许多。这种时候在多进程模式下运行PHP将会有所改善。（注意：老版本PHP尚不支持多进程模式）</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">应避免你的服务器产生过多的无效进程。举个例子，如果你的服务器是纯Web服务，需关闭（设置是不安装）X-Windows窗口系统。Windows系统上，应禁用微软快速搜索（Office的一部分）和第三方屏保程序（通常会需要100%的CPU利用）。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">有些程序你可以考虑删除包括一些不常用的网络协议、邮件服务、病毒扫描、（鼠标，红外线端口）驱动程序等。Unix系统上，假定你通过SSH访问你的服务器，那么你可以考虑去除：</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">telnetd,inetd,atd,ftpd,lpd,sambad模块</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">用于收取邮件的sendmail模块</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">NFS的映射表</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">xfs，fvwm，xinit，X</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">你也可以通过修改启动脚本（其路径通常位于/etc/init* 或 /etc/rc*/init*）来实现禁用不同的启动程序。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">也可以检查一下定期处理任务安排是否合理，看是否可以在服务器空闲期执行。</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">(f) 连接到其他服务器</span><br/><span style=\"box-sizing: border-box; widows: 1; color: rgb(51, 51, 51); font-family: Arial; font-size: 14px; line-height: 26px; background-color: rgb(255, 255, 255);\">如果你的服务器需要其他服务器提供的服务，此时有可能是其他服务器产生了瓶颈。最常见的就是一个很慢的执行诸多复杂查询的数据库服务器服务于很多其他服务器。</span></p><p><br/></p>', 'php', '', '0', '0', '55', '0', '满天星', '2018-04-19 09:52:53', '2018-04-28 16:03:18', '0', '1', '1');
INSERT INTO `article` VALUES ('43', 'php常见的面试题目', '19', '', null, '<p style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-top: 0px;margin-bottom: 8px;font-size: 14px;color: rgb(51, 51, 51);line-height: 28px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);border-width: 0px;list-style: none;text-indent: 2em;font-family: 宋体\">一. 基本知识点<br/>1.1 HTTP协议中几个状态码的含义:503 500 401 403 404 200 301 302。。。<br/>200 : 请求成功，请求的数据随之返回。<br/>301 : 永久性重定向。<br/>302 : 暂时行重定向。<br/>401 : 当前请求需要用户验证。<br/>403 : 服务器拒绝执行请求，即没有权限。<br/>404 : 请求失败，请求的数据在服务器上未发现。<br/>500 : 服务器错误。一般服务器端程序执行错误。<br/>503 : 服务器临时维护或过载。这个状态时临时性的。</p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-top: 0px;margin-bottom: 8px;font-size: 14px;color: rgb(51, 51, 51);line-height: 28px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);border-width: 0px;list-style: none;text-indent: 2em;font-family: 宋体\">1.2 Include require include_once require_once 的区别.<br/>处理失败方式不同：<br/>require 失败时会产生一个致命级别错误，并停止程序运行。<br/>include 失败时只产生一个警告级别错误，程序继续运行。</p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-top: 0px;margin-bottom: 8px;font-size: 14px;color: rgb(51, 51, 51);line-height: 28px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);border-width: 0px;list-style: none;text-indent: 2em;font-family: 宋体\">include_once/require_once和include/require 处理错误方式一样，<br/>唯一区别在于当所包含的文件代码已经存在时候，不在包含。</p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-top: 0px;margin-bottom: 8px;font-size: 14px;color: rgb(51, 51, 51);line-height: 28px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);border-width: 0px;list-style: none;text-indent: 2em;font-family: 宋体\"><br/></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-top: 0px;margin-bottom: 8px;font-size: 14px;color: rgb(51, 51, 51);line-height: 28px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);border-width: 0px;list-style: none;text-indent: 2em;font-family: 宋体\"><br/></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-top: 0px;margin-bottom: 8px;font-size: 14px;color: rgb(51, 51, 51);line-height: 28px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);border-width: 0px;list-style: none;text-indent: 2em;font-family: 宋体\">1.3 (mysql)请写出数据类型(int char varchar datetime text)的意思; 请问varchar和char有什么区别;<br/>int : 数值类型<br/>char : 固定长度字符串类型<br/>varchar : 可变长度字符串类型<br/>datetime : 时期时间类型<br/>text : 文本类型</p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-top: 0px;margin-bottom: 8px;font-size: 14px;color: rgb(51, 51, 51);line-height: 28px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);border-width: 0px;list-style: none;text-indent: 2em;font-family: 宋体\">varchar和char有什么区别:<br/>&nbsp; &nbsp; &nbsp;a. char 长度是固定的，不管你存储的数据是多少他都会都固定的长度。<br/>而varchar则处可变长度但他要在总长度上加1字符，这个用来存储位置。</p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-top: 0px;margin-bottom: 8px;font-size: 14px;color: rgb(51, 51, 51);line-height: 28px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);border-width: 0px;list-style: none;text-indent: 2em;font-family: 宋体\">b. char 固定长度，所以在处理速度上要比varchar快速很多，但是浪费存储空间，<br/>所以对存储不大，但在速度上有要求的可以使用char类型，反之可以用varchar类型来实例。</p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-top: 0px;margin-bottom: 8px;font-size: 14px;color: rgb(51, 51, 51);line-height: 28px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);border-width: 0px;list-style: none;text-indent: 2em;font-family: 宋体\"><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">1.4 error_reporting 等调试函数使用</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">error_reporting() 函数能够在运行时设置php.ini中 error_reporting 指令。</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">所以可以再程序中随时调节显示的错误级别。</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">使用此函数时 display_errors必须是打开状态。</span><br/></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-top: 0px;margin-bottom: 8px;font-size: 14px;color: rgb(51, 51, 51);line-height: 28px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);border-width: 0px;list-style: none;text-indent: 2em;font-family: 宋体\"><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\"><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">1.5 写代码来解决多进程/线程同时读写一个文件的问题。</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">PHP是不支持多线程的，可以使用php的flock加锁函数实现。</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">$fp = fopen(&quot;/tmp/lock.txt&quot;, &quot;w+&quot;);</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">if (flock($fp, LOCK_EX)) { // 进行排它型锁定</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">fwrite($fp, &quot;Write something here\\n&quot;);</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">flock($fp, LOCK_UN); // 释放锁定</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">} else {</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">echo &quot;Couldn&#39;t lock the file !&quot;;</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">}</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">fclose($fp);</span><br/></span></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-top: 0px;margin-bottom: 8px;font-size: 14px;color: rgb(51, 51, 51);line-height: 28px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);border-width: 0px;list-style: none;text-indent: 2em;font-family: 宋体\">1.6 写一段上传文件的代码。<br/>upload.html<br/>&lt;form enctype=&quot;multipart/form-data&quot; method=&quot;POST&quot; action=&quot;Upload.php&quot;&gt;<br/>Send this file: &lt;input name=&quot;name&quot; type=&quot;file&quot; /&gt;<br/>&lt;input type=&quot;submit&quot; value=&quot;Send File&quot; /&gt;<br/>&lt;/form&gt;</p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-top: 0px;margin-bottom: 8px;font-size: 14px;color: rgb(51, 51, 51);line-height: 28px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);border-width: 0px;list-style: none;text-indent: 2em;font-family: 宋体\">Upload.php<br/>$uploads_dir = &#39;/uploads&#39;;<br/>foreach ($_FILES[&quot;error&quot;] as $key =&gt; $error) {<br/>if ($error == UPLOAD_ERR_OK) {<br/>$tmp_name = $_FILES[&quot;tmp_name&quot;][$key];<br/>$name = $_FILES[&quot;name&quot;][$key];<br/>move_uploaded_file($tmp_name, &quot;$uploads_dir/$name&quot;);<br/>}<br/>}</p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-top: 0px;margin-bottom: 8px;font-size: 14px;color: rgb(51, 51, 51);line-height: 28px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);border-width: 0px;list-style: none;text-indent: 2em;font-family: 宋体\"><br/></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-top: 0px;margin-bottom: 8px;font-size: 14px;color: rgb(51, 51, 51);line-height: 28px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);border-width: 0px;list-style: none;text-indent: 2em;font-family: 宋体\"><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">1.7 Mysql 的存储引擎,myisam和innodb的区别。</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">a. MyISAM类型不支持事务处理等高级处理，而InnoDB类型支持.</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">b. MyISAM类型的表强调的是性能，其执行数度比InnoDB类型更快.</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">c. InnoDB不支持FULLTEXT类型的索引.</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">d. InnoDB 中不保存表的具体行数，也就是说，</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">执行select count(*) from table时，InnoDB要扫描一遍整个表来计算有多少行，</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">但是MyISAM只要简单的读出保存好的行数即可.</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">e. 对于AUTO_INCREMENT类型的字段，InnoDB中必须包含只有该字段的索引，但是在MyISAM表中，可以和其他字段一起建立联合索引。</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">f. DELETE FROM table时，InnoDB不会重新建立表，而是一行一行的删除。</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">g. LOAD TABLE FROM MASTER操作对InnoDB是不起作用的，解决方法是首先把InnoDB表改成MyISAM表，导入数据后再改成InnoDB表，</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">但是对于使用的额外的InnoDB特性(例如外键)的表不适用.</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">h. MyISAM支持表锁，InnoDB支持行锁。</span><br/></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-top: 0px;margin-bottom: 8px;font-size: 14px;color: rgb(51, 51, 51);line-height: 28px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);border-width: 0px;list-style: none;text-indent: 2em;font-family: 宋体\"><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\"><br/></span></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-top: 0px;margin-bottom: 8px;font-size: 14px;color: rgb(51, 51, 51);line-height: 28px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);border-width: 0px;list-style: none;text-indent: 2em;font-family: 宋体\"><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\"><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">二. web 架构，安全,项目经验</span><br/></span></p><p><span style=\"box-sizing: border-box;outline: 0px;color: rgb(51, 51, 51);font-size: 14px;background-color: rgb(255, 255, 255);font-family: 宋体;line-height: 28px;text-indent: 28px\">2.1 MySQL数据库作发布系统的存储，一天五万条以上的增量，预计运维三年,怎么优化？</span><br/><span style=\"box-sizing: border-box;outline: 0px;color: rgb(51, 51, 51);font-size: 14px;background-color: rgb(255, 255, 255);font-family: 宋体;line-height: 28px;text-indent: 28px\">a. 设计良好的数据库结构，允许部分数据冗余，尽量避免join查询，提高效率。</span><br/><span style=\"box-sizing: border-box;outline: 0px;color: rgb(51, 51, 51);font-size: 14px;background-color: rgb(255, 255, 255);font-family: 宋体;line-height: 28px;text-indent: 28px\">b. 选择合适的表字段数据类型和存储引擎，适当的添加索引。</span><br/><span style=\"box-sizing: border-box;outline: 0px;color: rgb(51, 51, 51);font-size: 14px;background-color: rgb(255, 255, 255);font-family: 宋体;line-height: 28px;text-indent: 28px\">c. mysql库主从读写分离。</span><br/><span style=\"box-sizing: border-box;outline: 0px;color: rgb(51, 51, 51);font-size: 14px;background-color: rgb(255, 255, 255);font-family: 宋体;line-height: 28px;text-indent: 28px\">d. 找规律分表，减少单表中的数据量提高查询速度。</span><br/><span style=\"box-sizing: border-box;outline: 0px;color: rgb(51, 51, 51);font-size: 14px;background-color: rgb(255, 255, 255);font-family: 宋体;line-height: 28px;text-indent: 28px\">e。添加缓存机制，比如memcached，apc等。</span><br/><span style=\"box-sizing: border-box;outline: 0px;color: rgb(51, 51, 51);font-size: 14px;background-color: rgb(255, 255, 255);font-family: 宋体;line-height: 28px;text-indent: 28px\">f. 不经常改动的页面，生成静态页面。</span><br/><span style=\"box-sizing: border-box;outline: 0px;color: rgb(51, 51, 51);font-size: 14px;background-color: rgb(255, 255, 255);font-family: 宋体;line-height: 28px;text-indent: 28px\">g. 书写高效率的SQL。比如 SELECT * FROM TABEL 改为 SELECT field_1, field_2, field_3 FROM TABLE.</span><br/></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-top: 0px;margin-bottom: 8px;font-size: 14px;color: rgb(51, 51, 51);line-height: 28px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);border-width: 0px;list-style: none;text-indent: 2em;font-family: 宋体\"><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\"><br/></span></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-top: 0px;margin-bottom: 8px;font-size: 14px;color: rgb(51, 51, 51);line-height: 28px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);border-width: 0px;list-style: none;text-indent: 2em;font-family: 宋体\"><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\"><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">2.2 对于大流量的网站,您采用什么样的方法来解决各页面访问量统计问题</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">a. 确认服务器是否能支撑当前访问量。</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">b. 优化数据库访问。参考2.3</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">c. 禁止外部访问链接（盗链）, 比如图片盗链。</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">d. 控制文件下载。</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">e. 使用不同主机分流。</span><br/><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\">f. 使用浏览统计软件，了解访问量，有针对性的进行优化。</span><br/></span></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-top: 0px;margin-bottom: 8px;font-size: 14px;color: rgb(51, 51, 51);line-height: 28px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);border-width: 0px;list-style: none;text-indent: 2em;font-family: 宋体\"><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\"><br/></span></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-top: 0px;margin-bottom: 8px;font-size: 14px;color: rgb(51, 51, 51);line-height: 28px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);border-width: 0px;list-style: none;text-indent: 2em;font-family: 宋体\"><span style=\"box-sizing: border-box;outline: 0px;line-height: 28px\"></span></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;color: rgb(102, 102, 102);line-height: 26px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);font-family: 宋体, Arial\"><span style=\"box-sizing: border-box;outline: 0px;color: rgb(255, 0, 0)\"><span style=\"box-sizing: border-box;outline: 0px;color: rgb(0, 0, 0)\">2.3 写出一个正则表达式，过虑网页上的所有JS/VBS脚本（即把标记及其内容都去掉）：（9）。</span></span></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;color: rgb(102, 102, 102);line-height: 26px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);font-family: 宋体, Arial\"><span style=\"box-sizing: border-box;outline: 0px;color: rgb(255, 0, 0)\">答：/&lt;[^&gt;].*?&gt;.*?&lt;\\/&gt;/si</span></p><p><br/></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-top: 0px;margin-bottom: 8px;font-size: 14px;color: rgb(51, 51, 51);line-height: 28px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);border-width: 0px;list-style: none;text-indent: 2em;font-family: 宋体\"><br/></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;color: rgb(102, 102, 102);line-height: 26px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);font-family: 宋体, Arial\"><span style=\"box-sizing: border-box;outline: 0px;color: rgb(255, 0, 0)\"><span style=\"box-sizing: border-box;outline: 0px;color: rgb(0, 0, 0)\">2.4 用PHP打印出前一天的时间格式是2006-5-10 22:21:21</span></span></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;color: rgb(102, 102, 102);line-height: 26px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);font-family: 宋体, Arial\"><span style=\"box-sizing: border-box;outline: 0px;color: rgb(255, 0, 0)\">答：echo date(&#39;Y-m-d H:i:s&#39;, strtotime(&#39;-1 day&#39;));</span></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;color: rgb(102, 102, 102);line-height: 26px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);font-family: 宋体, Arial\"><span style=\"box-sizing: border-box;outline: 0px;color: rgb(255, 0, 0)\"><br/></span></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;color: rgb(102, 102, 102);line-height: 26px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);font-family: 宋体, Arial\"><br/></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;color: rgb(102, 102, 102);line-height: 26px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);font-family: 宋体, Arial\"><span style=\"box-sizing: border-box;outline: 0px;color: rgb(255, 0, 0)\"><span style=\"box-sizing: border-box;outline: 0px;color: rgb(0, 0, 0)\">2.5 echo(),print(),print_r()的区别</span></span></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;color: rgb(102, 102, 102);line-height: 26px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);font-family: 宋体, Arial\"><span style=\"box-sizing: border-box;outline: 0px;color: rgb(255, 0, 0)\">答：echo是语言结构，无返回值;print功能和echo基本相同，不同的是print是函数，有返回值;print_r是递归打印，用于输出数组对象</span></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;color: rgb(102, 102, 102);line-height: 26px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);font-family: 宋体, Arial\"><span style=\"box-sizing: border-box;outline: 0px;color: rgb(255, 0, 0)\"><br/></span></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;color: rgb(102, 102, 102);line-height: 26px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);font-family: 宋体, Arial\"><br/></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;color: rgb(102, 102, 102);line-height: 26px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);font-family: 宋体, Arial\"><span style=\"box-sizing: border-box;outline: 0px;color: rgb(255, 0, 0)\"><span style=\"box-sizing: border-box;outline: 0px;color: rgb(0, 0, 0)\">2.6 如何实现字符串翻转？</span></span></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;color: rgb(102, 102, 102);line-height: 26px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);font-family: 宋体, Arial\"><span style=\"box-sizing: border-box;outline: 0px;color: rgb(255, 0, 0)\">答：.用strrev函数呗,不准用PHP内置的就自己写:<br/></span></p><ol style=\"box-sizing: border-box;outline: 0px;padding: 0px;margin-bottom: 24px;list-style-position: initial;list-style-image: initial;border-top: none;border-right: 1px solid rgb(231, 229, 220);border-bottom: none;border-left: none;border-image: initial;background-color: rgb(255, 255, 255);color: rgb(92, 92, 92)\" class=\" list-paddingleft-2\"><li><p><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: black;background-color: inherit\"><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;background-color: inherit\">strrev</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;background-color: inherit\">(</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: rgb(221, 0, 0);background-color: inherit\">$str</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;background-color: inherit\">)&nbsp;&nbsp;</span></span></p></li><li><p><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: black;background-color: inherit\">{&nbsp;&nbsp;</span></p></li><li><p><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: black;background-color: inherit\">&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: rgb(221, 0, 0);background-color: inherit\">$len</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;background-color: inherit\">=</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;background-color: inherit\">strlen</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;background-color: inherit\">(</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: rgb(221, 0, 0);background-color: inherit\">$str</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;background-color: inherit\">);&nbsp;&nbsp;</span></span></p></li><li><p><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: black;background-color: inherit\">&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: rgb(221, 0, 0);background-color: inherit\">$newstr</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;background-color: inherit\">&nbsp;=&nbsp;</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: blue;background-color: inherit\">&#39;&#39;</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;background-color: inherit\">;&nbsp;&nbsp;</span></span></p></li><li><p><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: black;background-color: inherit\">&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: rgb(0, 102, 153);background-color: inherit;font-weight: bold\">for</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;background-color: inherit\">(</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: rgb(221, 0, 0);background-color: inherit\">$i</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;background-color: inherit\">=</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: rgb(221, 0, 0);background-color: inherit\">$len</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;background-color: inherit\">;</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: rgb(221, 0, 0);background-color: inherit\">$i</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;background-color: inherit\">&gt;=0;</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: rgb(221, 0, 0);background-color: inherit\">$i</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;background-color: inherit\">--)&nbsp;&nbsp;</span></span></p></li><li><p><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: black;background-color: inherit\">&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;&nbsp;</span></p></li><li><p><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: black;background-color: inherit\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: rgb(221, 0, 0);background-color: inherit\">$newstr</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;background-color: inherit\">&nbsp;.=&nbsp;</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: rgb(221, 0, 0);background-color: inherit\">$str</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;background-color: inherit\">{</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: rgb(221, 0, 0);background-color: inherit\">$i</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;background-color: inherit\">};&nbsp;&nbsp;</span></span></p></li><li><p><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: black;background-color: inherit\">&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;</span></p></li><li><p><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: black;background-color: inherit\">&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: rgb(0, 102, 153);background-color: inherit;font-weight: bold\">return</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;background-color: inherit\">&nbsp;</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: rgb(221, 0, 0);background-color: inherit\">$newstr</span><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;background-color: inherit\">;&nbsp;&nbsp;</span></span></p></li><li><p><span style=\"box-sizing: border-box;outline: 0px;padding: 0px;border: none;color: black;background-color: inherit\">}&nbsp;&nbsp;</span></p></li></ol><p><br/><br/><br/><span style=\"box-sizing: border-box;outline: 0px;font-family: &#39;PingFang SC&#39;, &#39;Microsoft YaHei&#39;, SimHei, Arial, SimSun;font-size: 14px;background-color: rgb(255, 255, 255);color: rgb(255, 0, 0)\"><span style=\"box-sizing: border-box;outline: 0px;color: rgb(0, 0, 0)\">2.7 实现中文字串截取无乱码的方法。</span></span></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;color: rgb(102, 102, 102);line-height: 26px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);font-family: 宋体, Arial\"><span style=\"box-sizing: border-box;outline: 0px;color: rgb(255, 0, 0)\">答：mb_substr()</span></p><p><br/></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;color: rgb(102, 102, 102);line-height: 26px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);font-family: 宋体, Arial\">2.8<span style=\"box-sizing: border-box;outline: 0px;font-size: 14px\"><span style=\"box-sizing: border-box;outline: 0px;font-family: 宋体\"><span style=\"box-sizing: border-box;outline: 0px\">&nbsp;</span>如何用<span style=\"box-sizing: border-box;outline: 0px\">php</span>的环境变量得到一个网页地址的内容？<span style=\"box-sizing: border-box;outline: 0px\">ip</span>地址又要怎样得到？</span></span></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;color: rgb(102, 102, 102);line-height: 26px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);font-family: 宋体, Arial\"><span style=\"box-sizing: border-box;outline: 0px;color: rgb(255, 0, 0)\"><span style=\"box-sizing: border-box;outline: 0px;font-size: 14px\"><span style=\"box-sizing: border-box;outline: 0px;font-family: 宋体\">答：$_SERVSR[‘REQUEST_URI’] ,&nbsp;$_SERVER[‘REMOTE_ADDR’]</span></span></span></p><p><br/></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;color: rgb(102, 102, 102);line-height: 26px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);font-family: 宋体, Arial\">2.9 求两个日期的差数，例如2007-2-5 ~ 2007-3-6 的日期差数&nbsp;<br/><br/><span style=\"box-sizing: border-box;outline: 0px;color: rgb(255, 0, 0)\">答：(strtotime(‘2007-3-6’)-strtotime(‘2007-2-5’))/3600*24</span></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;color: rgb(102, 102, 102);line-height: 26px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);font-family: 宋体, Arial\"><span style=\"box-sizing: border-box;outline: 0px;color: rgb(255, 0, 0)\"><br/></span></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;color: rgb(102, 102, 102);line-height: 26px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);font-family: 宋体, Arial\"><span style=\"box-sizing: border-box;outline: 0px;color: rgb(255, 0, 0)\"><span style=\"box-sizing: border-box;outline: 0px;color: rgb(0, 0, 0)\">2.10 如何通过javascript判断一个窗口是否已经被屏蔽</span></span></p><p style=\"box-sizing: border-box;outline: 0px;padding: 0px;color: rgb(102, 102, 102);line-height: 26px;text-align: justify;white-space: normal;background-color: rgb(255, 255, 255);font-family: 宋体, Arial\"><span style=\"box-sizing: border-box;outline: 0px;color: rgb(255, 0, 0)\">答：获取open()的返回值，如果是null，就是屏蔽了</span></p><p><br/></p>', 'PHP面试题', '', '0', '0', '8', '0', '满天星', '2018-04-28 15:56:44', '2018-04-28 16:09:24', '0', '1', '1');
INSERT INTO `article` VALUES ('44', 'PHP随机生成中国人姓名的类', '6', '', null, '<pre class=\"hljs xml\" style=\"box-sizing: border-box; overflow: auto; font-family: &quot;Source Code Pro&quot;, Consolas, Menlo, Monaco, &quot;Courier New&quot;, monospace; font-size: 0.93em; padding: 1em; margin-bottom: 1.5em; line-height: 1.45; word-break: break-all; word-wrap: break-word; color: rgb(51, 51, 51); border: none; border-radius: 4px; max-height: 35em; position: relative; margin-top: 0px !important;\">/*&nbsp;&nbsp;&nbsp;随机生成国人的名字&nbsp;&nbsp;&nbsp;*/\r\n&lt;?php\r\n&nbsp;&nbsp;&nbsp;&nbsp;/*rndChinaName.class.php*/\r\n&nbsp;&nbsp;&nbsp;&nbsp;Class&nbsp;rndChinaName{&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;private&nbsp;$arrXing,$numbXing;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;private&nbsp;$arrMing,$numbMing;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;rndChinaName(){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$this-&gt;getXingList();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$this-&gt;getMingList();\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*&nbsp;获取姓列表&nbsp;*/\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;private&nbsp;function&nbsp;getXingList(){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$this-&gt;arrXing=array(&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;赵&#39;,&#39;钱&#39;,&#39;孙&#39;,&#39;李&#39;,&#39;周&#39;,&#39;吴&#39;,&#39;郑&#39;,&#39;王&#39;,&#39;冯&#39;,&#39;陈&#39;,&#39;褚&#39;,&#39;卫&#39;,&#39;蒋&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;沈&#39;,&#39;韩&#39;,&#39;杨&#39;,&#39;朱&#39;,&#39;秦&#39;,&#39;尤&#39;,&#39;许&#39;,&#39;何&#39;,&#39;吕&#39;,&#39;施&#39;,&#39;张&#39;,&#39;孔&#39;,&#39;曹&#39;,&#39;严&#39;,&#39;华&#39;,&#39;金&#39;,&#39;魏&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;陶&#39;,&#39;姜&#39;,&#39;戚&#39;,&#39;谢&#39;,&#39;邹&#39;,&#39;喻&#39;,&#39;柏&#39;,&#39;水&#39;,&#39;窦&#39;,&#39;章&#39;,&#39;云&#39;,&#39;苏&#39;,&#39;潘&#39;,&#39;葛&#39;,&#39;奚&#39;,&#39;范&#39;,&#39;彭&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;郎&#39;,&#39;鲁&#39;,&#39;韦&#39;,&#39;昌&#39;,&#39;马&#39;,&#39;苗&#39;,&#39;凤&#39;,&#39;花&#39;,&#39;方&#39;,&#39;任&#39;,&#39;袁&#39;,&#39;柳&#39;,&#39;鲍&#39;,&#39;史&#39;,&#39;唐&#39;,&#39;费&#39;,&#39;薛&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;雷&#39;,&#39;贺&#39;,&#39;倪&#39;,&#39;汤&#39;,&#39;滕&#39;,&#39;殷&#39;,&#39;罗&#39;,&#39;毕&#39;,&#39;郝&#39;,&#39;安&#39;,&#39;常&#39;,&#39;傅&#39;,&#39;卞&#39;,&#39;齐&#39;,&#39;元&#39;,&#39;顾&#39;,&#39;孟&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;平&#39;,&#39;黄&#39;,&#39;穆&#39;,&#39;萧&#39;,&#39;尹&#39;,&#39;姚&#39;,&#39;邵&#39;,&#39;湛&#39;,&#39;汪&#39;,&#39;祁&#39;,&#39;毛&#39;,&#39;狄&#39;,&#39;米&#39;,&#39;伏&#39;,&#39;成&#39;,&#39;戴&#39;,&#39;谈&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;宋&#39;,&#39;茅&#39;,&#39;庞&#39;,&#39;熊&#39;,&#39;纪&#39;,&#39;舒&#39;,&#39;屈&#39;,&#39;项&#39;,&#39;祝&#39;,&#39;董&#39;,&#39;梁&#39;,&#39;杜&#39;,&#39;阮&#39;,&#39;蓝&#39;,&#39;闵&#39;,&#39;季&#39;,&#39;贾&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;路&#39;,&#39;娄&#39;,&#39;江&#39;,&#39;童&#39;,&#39;颜&#39;,&#39;郭&#39;,&#39;梅&#39;,&#39;盛&#39;,&#39;林&#39;,&#39;钟&#39;,&#39;徐&#39;,&#39;邱&#39;,&#39;骆&#39;,&#39;高&#39;,&#39;夏&#39;,&#39;蔡&#39;,&#39;田&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;樊&#39;,&#39;胡&#39;,&#39;凌&#39;,&#39;霍&#39;,&#39;虞&#39;,&#39;万&#39;,&#39;支&#39;,&#39;柯&#39;,&#39;管&#39;,&#39;卢&#39;,&#39;莫&#39;,&#39;柯&#39;,&#39;房&#39;,&#39;裘&#39;,&#39;缪&#39;,&#39;解&#39;,&#39;应&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;宗&#39;,&#39;丁&#39;,&#39;宣&#39;,&#39;邓&#39;,&#39;单&#39;,&#39;杭&#39;,&#39;洪&#39;,&#39;包&#39;,&#39;诸&#39;,&#39;左&#39;,&#39;石&#39;,&#39;崔&#39;,&#39;吉&#39;,&#39;龚&#39;,&#39;程&#39;,&#39;嵇&#39;,&#39;邢&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;裴&#39;,&#39;陆&#39;,&#39;荣&#39;,&#39;翁&#39;,&#39;荀&#39;,&#39;于&#39;,&#39;惠&#39;,&#39;甄&#39;,&#39;曲&#39;,&#39;封&#39;,&#39;储&#39;,&#39;仲&#39;,&#39;伊&#39;,&#39;宁&#39;,&#39;仇&#39;,&#39;甘&#39;,&#39;武&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;符&#39;,&#39;刘&#39;,&#39;景&#39;,&#39;詹&#39;,&#39;龙&#39;,&#39;叶&#39;,&#39;幸&#39;,&#39;司&#39;,&#39;黎&#39;,&#39;溥&#39;,&#39;印&#39;,&#39;怀&#39;,&#39;蒲&#39;,&#39;邰&#39;,&#39;从&#39;,&#39;索&#39;,&#39;赖&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;卓&#39;,&#39;屠&#39;,&#39;池&#39;,&#39;乔&#39;,&#39;胥&#39;,&#39;闻&#39;,&#39;莘&#39;,&#39;党&#39;,&#39;翟&#39;,&#39;谭&#39;,&#39;贡&#39;,&#39;劳&#39;,&#39;逄&#39;,&#39;姬&#39;,&#39;申&#39;,&#39;扶&#39;,&#39;堵&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;冉&#39;,&#39;宰&#39;,&#39;雍&#39;,&#39;桑&#39;,&#39;寿&#39;,&#39;通&#39;,&#39;燕&#39;,&#39;浦&#39;,&#39;尚&#39;,&#39;农&#39;,&#39;温&#39;,&#39;别&#39;,&#39;庄&#39;,&#39;晏&#39;,&#39;柴&#39;,&#39;瞿&#39;,&#39;阎&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;连&#39;,&#39;习&#39;,&#39;容&#39;,&#39;向&#39;,&#39;古&#39;,&#39;易&#39;,&#39;廖&#39;,&#39;庾&#39;,&#39;终&#39;,&#39;步&#39;,&#39;都&#39;,&#39;耿&#39;,&#39;满&#39;,&#39;弘&#39;,&#39;匡&#39;,&#39;国&#39;,&#39;文&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;寇&#39;,&#39;广&#39;,&#39;禄&#39;,&#39;阙&#39;,&#39;东&#39;,&#39;欧&#39;,&#39;利&#39;,&#39;师&#39;,&#39;巩&#39;,&#39;聂&#39;,&#39;关&#39;,&#39;荆&#39;,&#39;司马&#39;,&#39;上官&#39;,&#39;欧阳&#39;,&#39;夏侯&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;诸葛&#39;,&#39;闻人&#39;,&#39;东方&#39;,&#39;赫连&#39;,&#39;皇甫&#39;,&#39;尉迟&#39;,&#39;公羊&#39;,&#39;澹台&#39;,&#39;公冶&#39;,&#39;宗政&#39;,&#39;濮阳&#39;,&#39;淳于&#39;,&#39;单于&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;太叔&#39;,&#39;申屠&#39;,&#39;公孙&#39;,&#39;仲孙&#39;,&#39;轩辕&#39;,&#39;令狐&#39;,&#39;徐离&#39;,&#39;宇文&#39;,&#39;长孙&#39;,&#39;慕容&#39;,&#39;司徒&#39;,&#39;司空&#39;);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$this-&gt;numbXing&nbsp;=&nbsp;count($this-&gt;arrXing);&nbsp;//姓总数\r\n&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/*&nbsp;获取名列表&nbsp;*/\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;private&nbsp;function&nbsp;getMingList()\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$this-&gt;arrMing=array(&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;伟&#39;,&#39;刚&#39;,&#39;勇&#39;,&#39;毅&#39;,&#39;俊&#39;,&#39;峰&#39;,&#39;强&#39;,&#39;军&#39;,&#39;平&#39;,&#39;保&#39;,&#39;东&#39;,&#39;文&#39;,&#39;辉&#39;,&#39;力&#39;,&#39;明&#39;,&#39;永&#39;,&#39;健&#39;,&#39;世&#39;,&#39;广&#39;,&#39;志&#39;,&#39;义&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;兴&#39;,&#39;良&#39;,&#39;海&#39;,&#39;山&#39;,&#39;仁&#39;,&#39;波&#39;,&#39;宁&#39;,&#39;贵&#39;,&#39;福&#39;,&#39;生&#39;,&#39;龙&#39;,&#39;元&#39;,&#39;全&#39;,&#39;国&#39;,&#39;胜&#39;,&#39;学&#39;,&#39;祥&#39;,&#39;才&#39;,&#39;发&#39;,&#39;武&#39;,&#39;新&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;利&#39;,&#39;清&#39;,&#39;飞&#39;,&#39;彬&#39;,&#39;富&#39;,&#39;顺&#39;,&#39;信&#39;,&#39;子&#39;,&#39;杰&#39;,&#39;涛&#39;,&#39;昌&#39;,&#39;成&#39;,&#39;康&#39;,&#39;星&#39;,&#39;光&#39;,&#39;天&#39;,&#39;达&#39;,&#39;安&#39;,&#39;岩&#39;,&#39;中&#39;,&#39;茂&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;进&#39;,&#39;林&#39;,&#39;有&#39;,&#39;坚&#39;,&#39;和&#39;,&#39;彪&#39;,&#39;博&#39;,&#39;诚&#39;,&#39;先&#39;,&#39;敬&#39;,&#39;震&#39;,&#39;振&#39;,&#39;壮&#39;,&#39;会&#39;,&#39;思&#39;,&#39;群&#39;,&#39;豪&#39;,&#39;心&#39;,&#39;邦&#39;,&#39;承&#39;,&#39;乐&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;绍&#39;,&#39;功&#39;,&#39;松&#39;,&#39;善&#39;,&#39;厚&#39;,&#39;庆&#39;,&#39;磊&#39;,&#39;民&#39;,&#39;友&#39;,&#39;裕&#39;,&#39;河&#39;,&#39;哲&#39;,&#39;江&#39;,&#39;超&#39;,&#39;浩&#39;,&#39;亮&#39;,&#39;政&#39;,&#39;谦&#39;,&#39;亨&#39;,&#39;奇&#39;,&#39;固&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;之&#39;,&#39;轮&#39;,&#39;翰&#39;,&#39;朗&#39;,&#39;伯&#39;,&#39;宏&#39;,&#39;言&#39;,&#39;若&#39;,&#39;鸣&#39;,&#39;朋&#39;,&#39;斌&#39;,&#39;梁&#39;,&#39;栋&#39;,&#39;维&#39;,&#39;启&#39;,&#39;克&#39;,&#39;伦&#39;,&#39;翔&#39;,&#39;旭&#39;,&#39;鹏&#39;,&#39;泽&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;晨&#39;,&#39;辰&#39;,&#39;士&#39;,&#39;以&#39;,&#39;建&#39;,&#39;家&#39;,&#39;致&#39;,&#39;树&#39;,&#39;炎&#39;,&#39;德&#39;,&#39;行&#39;,&#39;时&#39;,&#39;泰&#39;,&#39;盛&#39;,&#39;雄&#39;,&#39;琛&#39;,&#39;钧&#39;,&#39;冠&#39;,&#39;策&#39;,&#39;腾&#39;,&#39;楠&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;榕&#39;,&#39;风&#39;,&#39;航&#39;,&#39;弘&#39;,&#39;秀&#39;,&#39;娟&#39;,&#39;英&#39;,&#39;华&#39;,&#39;慧&#39;,&#39;巧&#39;,&#39;美&#39;,&#39;娜&#39;,&#39;静&#39;,&#39;淑&#39;,&#39;惠&#39;,&#39;珠&#39;,&#39;翠&#39;,&#39;雅&#39;,&#39;芝&#39;,&#39;玉&#39;,&#39;萍&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;红&#39;,&#39;娥&#39;,&#39;玲&#39;,&#39;芬&#39;,&#39;芳&#39;,&#39;燕&#39;,&#39;彩&#39;,&#39;春&#39;,&#39;菊&#39;,&#39;兰&#39;,&#39;凤&#39;,&#39;洁&#39;,&#39;梅&#39;,&#39;琳&#39;,&#39;素&#39;,&#39;云&#39;,&#39;莲&#39;,&#39;真&#39;,&#39;环&#39;,&#39;雪&#39;,&#39;荣&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;爱&#39;,&#39;妹&#39;,&#39;霞&#39;,&#39;香&#39;,&#39;月&#39;,&#39;莺&#39;,&#39;媛&#39;,&#39;艳&#39;,&#39;瑞&#39;,&#39;凡&#39;,&#39;佳&#39;,&#39;嘉&#39;,&#39;琼&#39;,&#39;勤&#39;,&#39;珍&#39;,&#39;贞&#39;,&#39;莉&#39;,&#39;桂&#39;,&#39;娣&#39;,&#39;叶&#39;,&#39;璧&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;璐&#39;,&#39;娅&#39;,&#39;琦&#39;,&#39;晶&#39;,&#39;妍&#39;,&#39;茜&#39;,&#39;秋&#39;,&#39;珊&#39;,&#39;莎&#39;,&#39;锦&#39;,&#39;黛&#39;,&#39;青&#39;,&#39;倩&#39;,&#39;婷&#39;,&#39;姣&#39;,&#39;婉&#39;,&#39;娴&#39;,&#39;瑾&#39;,&#39;颖&#39;,&#39;露&#39;,&#39;瑶&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;怡&#39;,&#39;婵&#39;,&#39;雁&#39;,&#39;蓓&#39;,&#39;纨&#39;,&#39;仪&#39;,&#39;荷&#39;,&#39;丹&#39;,&#39;蓉&#39;,&#39;眉&#39;,&#39;君&#39;,&#39;琴&#39;,&#39;蕊&#39;,&#39;薇&#39;,&#39;菁&#39;,&#39;梦&#39;,&#39;岚&#39;,&#39;苑&#39;,&#39;婕&#39;,&#39;馨&#39;,&#39;瑗&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;琰&#39;,&#39;韵&#39;,&#39;融&#39;,&#39;园&#39;,&#39;艺&#39;,&#39;咏&#39;,&#39;卿&#39;,&#39;聪&#39;,&#39;澜&#39;,&#39;纯&#39;,&#39;毓&#39;,&#39;悦&#39;,&#39;昭&#39;,&#39;冰&#39;,&#39;爽&#39;,&#39;琬&#39;,&#39;茗&#39;,&#39;羽&#39;,&#39;希&#39;,&#39;欣&#39;,&#39;飘&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;育&#39;,&#39;滢&#39;,&#39;馥&#39;,&#39;筠&#39;,&#39;柔&#39;,&#39;竹&#39;,&#39;霭&#39;,&#39;凝&#39;,&#39;晓&#39;,&#39;欢&#39;,&#39;霄&#39;,&#39;枫&#39;,&#39;芸&#39;,&#39;菲&#39;,&#39;寒&#39;,&#39;伊&#39;,&#39;亚&#39;,&#39;宜&#39;,&#39;可&#39;,&#39;姬&#39;,&#39;舒&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;影&#39;,&#39;荔&#39;,&#39;枝&#39;,&#39;丽&#39;,&#39;阳&#39;,&#39;妮&#39;,&#39;宝&#39;,&#39;贝&#39;,&#39;初&#39;,&#39;程&#39;,&#39;梵&#39;,&#39;罡&#39;,&#39;恒&#39;,&#39;鸿&#39;,&#39;桦&#39;,&#39;骅&#39;,&#39;剑&#39;,&#39;娇&#39;,&#39;纪&#39;,&#39;宽&#39;,&#39;苛&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;灵&#39;,&#39;玛&#39;,&#39;媚&#39;,&#39;琪&#39;,&#39;晴&#39;,&#39;容&#39;,&#39;睿&#39;,&#39;烁&#39;,&#39;堂&#39;,&#39;唯&#39;,&#39;威&#39;,&#39;韦&#39;,&#39;雯&#39;,&#39;苇&#39;,&#39;萱&#39;,&#39;阅&#39;,&#39;彦&#39;,&#39;宇&#39;,&#39;雨&#39;,&#39;洋&#39;,&#39;忠&#39;,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;宗&#39;,&#39;曼&#39;,&#39;紫&#39;,&#39;逸&#39;,&#39;贤&#39;,&#39;蝶&#39;,&#39;菡&#39;,&#39;绿&#39;,&#39;蓝&#39;,&#39;儿&#39;,&#39;翠&#39;,&#39;烟&#39;,&#39;小&#39;,&#39;轩&#39;);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//名总数&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$this-&gt;numbMing&nbsp;=&nbsp;count($this-&gt;arrMing);&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;获取姓\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;private&nbsp;function&nbsp;getXing(){&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;mt_rand()&nbsp;比rand()方法快四倍，而且生成的随机数比rand()生成的伪随机数无规律。\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$this-&gt;arrXing[mt_rand(0,$this-&gt;numbXing-1)];&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;获取名字\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;private&nbsp;function&nbsp;getMing(){&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$this-&gt;arrMing[mt_rand(0,$this-&gt;numbMing-1)];&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;获取名字\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;getName($type=0){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$name&nbsp;=&nbsp;&#39;&#39;&nbsp;;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;switch($type){&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case&nbsp;1:&nbsp;&nbsp;&nbsp;&nbsp;//2字\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$name&nbsp;=&nbsp;$this-&gt;getXing().$this-&gt;getMing();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case&nbsp;2:&nbsp;&nbsp;&nbsp;&nbsp;//随机2、3个字\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$name&nbsp;=&nbsp;$this-&gt;getXing().$this-&gt;getMing();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(mt_rand(0,100)&gt;50)$name&nbsp;.=&nbsp;$this-&gt;getMing();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case&nbsp;3:&nbsp;//只取姓\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$name&nbsp;=&nbsp;$this-&gt;getXing();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case&nbsp;4:&nbsp;//只取名\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$name&nbsp;=&nbsp;$this-&gt;getMing();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case&nbsp;0:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;default:&nbsp;//默认情况&nbsp;1姓+2名\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$name&nbsp;=&nbsp;$this-&gt;getXing().$this-&gt;getMing().$this-&gt;getMing();\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$name;\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n\r\n}\r\n?&gt;\r\n\r\n\r\n/*&nbsp;&nbsp;&nbsp;调用类，随机生成名字&nbsp;&nbsp;&nbsp;*/\r\n&lt;?php\r\n//&nbsp;index.php\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-type:&nbsp;text/html;&nbsp;charset=utf-8&#39;);&nbsp;\r\n//&nbsp;引入文件\r\n&nbsp;&nbsp;&nbsp;&nbsp;require(&#39;rndChinaName.class.php&#39;);\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;$name_obj&nbsp;=&nbsp;new&nbsp;rndChinaName();&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;$name&nbsp;=&nbsp;$name_obj-&gt;getName(2);&nbsp;echo&nbsp;$name;\r\n?&gt;</pre><p><br/></p>', '随机姓名', '姓名', '0', '0', '8', '0', '满天星', '2018-04-28 16:19:11', '2018-04-28 17:11:07', '0', '1', '1');
INSERT INTO `article` VALUES ('45', 'js中insertAdjacentHTML函数在元素标签的前中后插入html代码', '16', '', null, '<p style=\"box-sizing: border-box;margin-top: 0px;margin-bottom: 0.5em;padding: 0px;line-height: 2;font-family: &#39;Microsoft Yahei&#39;;font-size: 14px;color: rgb(86, 95, 105);white-space: normal;background-color: rgb(255, 255, 255)\"><span style=\"box-sizing: border-box\">Adajcent&nbsp; 邻近的</span><br/></p><p style=\"box-sizing: border-box;margin-top: 0px;margin-bottom: 0.5em;padding: 0px;line-height: 2;font-family: &#39;Microsoft Yahei&#39;;font-size: 14px;color: rgb(86, 95, 105);white-space: normal;background-color: rgb(255, 255, 255)\">原型：insertAdajcentHTML(swhere,stext)</p><p style=\"box-sizing: border-box;margin-top: 0px;margin-bottom: 0.5em;padding: 0px;line-height: 2;font-family: &#39;Microsoft Yahei&#39;;font-size: 14px;color: rgb(86, 95, 105);white-space: normal;background-color: rgb(255, 255, 255)\">insertAdjacentHTML方法：在指定的地方插入html标签语句&nbsp;<br/>参数：<br/>swhere: 指定插入html标签语句的地方，有四种值可用：<br/>1.beforeBegin: 插入到标签开始前</p><p style=\"box-sizing: border-box;margin-top: 0px;margin-bottom: 0.5em;padding: 0px;line-height: 2;font-family: &#39;Microsoft Yahei&#39;;font-size: 14px;color: rgb(86, 95, 105);white-space: normal;background-color: rgb(255, 255, 255)\">2.afterBegin:插入到标签开始标记之后</p><p style=\"box-sizing: border-box;margin-top: 0px;margin-bottom: 0.5em;padding: 0px;line-height: 2;font-family: &#39;Microsoft Yahei&#39;;font-size: 14px;color: rgb(86, 95, 105);white-space: normal;background-color: rgb(255, 255, 255)\">3.beforeEnd:插入到标签结束标记前</p><p style=\"box-sizing: border-box;margin-top: 0px;margin-bottom: 0.5em;padding: 0px;line-height: 2;font-family: &#39;Microsoft Yahei&#39;;font-size: 14px;color: rgb(86, 95, 105);white-space: normal;background-color: rgb(255, 255, 255)\">4.afterEnd:插入到标签结束标记后</p><p style=\"box-sizing: border-box;margin-top: 0px;margin-bottom: 0.5em;padding: 0px;line-height: 2;font-family: &#39;Microsoft Yahei&#39;;font-size: 14px;color: rgb(86, 95, 105);white-space: normal;background-color: rgb(255, 255, 255)\">stext：要插入的内容</p><pre class=\"html\" style=\"box-sizing: border-box; font-family: Monaco, Menlo, Consolas, &quot;Courier New&quot;, monospace; font-size: 13px; white-space: pre-wrap; padding: 0px; margin-top: 0px; margin-bottom: 0px; line-height: 1.5em; color: rgb(51, 51, 51); word-break: break-all; word-wrap: break-word; background-color: rgb(245, 245, 245); border: 1px solid rgb(204, 204, 204); border-radius: 4px;\">&lt;head&gt;\r\n&lt;title&gt;insertAdjacentHTML插入新内容&lt;/title&gt;\r\n&lt;script&nbsp;language=&quot;jscript&quot;&gt;\r\nfunction&nbsp;addwenzi()\r\n{\r\ndocument.all.dd.insertAdjacentHTML(&quot;afterBegin&quot;,&quot;&lt;h1&gt;hh11在第一个div后边（div内）插入内容&lt;/h1&gt;&quot;);\r\ndocument.all.dd.insertAdjacentHTML(&quot;beforeEnd&quot;,&quot;&lt;h2&gt;hh222在第二个div前边（div内）插入内容&lt;/h2&gt;&quot;);\r\ndocument.all.dd.insertAdjacentHTML(&quot;beforeBegin&quot;,&quot;&lt;h4&gt;hhh444在第一个div前边插入内容&lt;/h1&gt;&quot;);\r\ndocument.all.dd.insertAdjacentHTML(&quot;afterEnd&quot;,&quot;&lt;h5&gt;hh555在第二个div后边（div外）插入内容&lt;/h2&gt;&quot;);\r\n}\r\n&lt;/script&gt;\r\n&lt;/head&gt;\r\n&lt;body&nbsp;onload=&quot;addwenzi()&quot;&gt;\r\n第一个div前边beforeBegin（div外）&lt;div&nbsp;id=&quot;dd&quot;&nbsp;style=&quot;fontsize:6;color=&#39;#ff00ff&#39;&quot;&gt;第一个div后边afterBegin（div内），\r\n;第二个div前边beforeBegin（div内）&lt;/div&gt;第二个div后边afterEnd（div外）&lt;hr&gt;\r\n\r\n&lt;a&nbsp;href=&quot;&quot;&gt;&lt;u&gt;&nbsp;&lt;font&nbsp;color=&quot;red&quot;&gt;This&nbsp;is&nbsp;some&nbsp;text!&lt;/font&gt;&lt;/u&gt;&lt;/a&gt;\r\n&lt;/body&gt;\r\n&lt;/html&gt;</pre><p><br/></p>', 'insertAdjacentHTML', '', '0', '0', '1', '0', '满天星', '2018-04-28 19:09:17', '2018-04-29 09:41:13', '0', '1', '1');

-- ----------------------------
