  `cateAlias` varchar(120) DEFAULT NULL COMMENT '栏目别名',
  `cateFather` varchar(255) DEFAULT NULL COMMENT '父栏目',
  `cateArticles` int(11) DEFAULT NULL COMMENT '栏目文章数',
  `cateKeywords` varchar(200) DEFAULT NULL COMMENT '栏目关键字',
  `cateDescription` text COMMENT '栏目描述',
  `addTime` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '添加时间',
