-- ----------------------------
-- Table structure for comment
-- ----------------------------
DROP TABLE IF EXISTS `comment`;
CREATE TABLE `comment` (
  `commentId` int(11) NOT NULL AUTO_INCREMENT COMMENT '评论id',
  `commentContent` text COMMENT '评论内容',
  `commentArticleid` int(11) DEFAULT NULL,
  `randAtorName` varchar(12) DEFAULT NULL COMMENT '评论者',
  `atorIP` varchar(255) DEFAULT NULL,
  `addTime` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '添加时间',
  `status` int(1) NOT NULL DEFAULT '1' COMMENT '状态值：默认为1，1表示正常显示，0表示已删除',
  `praise` int(11) DEFAULT '0',
  PRIMARY KEY (`commentId`),
  KEY `caid` (`commentArticleid`),
  KEY `cuid` (`randAtorName`),
  CONSTRAINT `comment_ibfk_1` FOREIGN KEY (`commentArticleid`) REFERENCES `article` (`articleId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of comment
-- ----------------------------

-- ----------------------------
-- Table structure for config
-- ----------------------------
DROP TABLE IF EXISTS `config`;
CREATE TABLE `config` (
  `id` int(1) NOT NULL DEFAULT '1',
  `name` varchar(16) NOT NULL COMMENT '博主名',
  `beian` varchar(32) DEFAULT NULL COMMENT '备案号',
  `mobile` decimal(11,0) DEFAULT NULL COMMENT '博主电话',
  `code` int(1) DEFAULT NULL COMMENT '是否启用验证码',
  `logo` text COMMENT 'logo',
  `description` text COMMENT '描述',
  `title` varchar(60) DEFAULT NULL COMMENT '标题',
  `comment` int(1) DEFAULT NULL COMMENT '是否允许评论',
  `message` int(1) DEFAULT NULL COMMENT '是否允许留言',
  `cache` int(1) DEFAULT NULL COMMENT '自动清空缓存',
  `close` int(1) DEFAULT NULL COMMENT '是否关闭网站',
  `url` varchar(64) DEFAULT NULL,
  `email` varchar(64) DEFAULT NULL,
  `keywords` varchar(64) DEFAULT NULL,
  `logOverTime` int(11) DEFAULT NULL COMMENT '登录超时',
  `uploadPic` int(1) DEFAULT NULL COMMENT '是否可以上传图片',
  `visit` decimal(11,0) DEFAULT '0' COMMENT '访问量',
  `alias` varchar(18) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of config
-- ----------------------------
INSERT INTO `config` VALUES ('1', '何坤', '蜀ICP备18008643号', '18615760807', '1', 'admin/Upload/20180408/fa25c0dab76fad0ca585e189cfdf3353.png', '人生在世，谁都难免被寂寞所困，不在寂寞中消亡，就在寂寞中爆发。若能够学会走出寂寞，把生活调节得有滋有味，那你一定会是个幸福的人。寂寞不能太久，否则就成了多愁善感。偶尔的寂寞会是一剂清醒剂，让你更好地面对喧嚣的尘世。<br/>\r\nLife is not always what we want it to be. We fight. We cry. And sometimes, we give up. But in our hearts, we know it\'s still love. - 生活有时不尽如人意。我们挣扎、哭泣，有时甚至放弃。但内心始终充满爱。', 'Babysbreath满 天 星 ❀ 个人博客 — 一个站在PHP开发之路上的草根程序员个人博客网站', '1', '1', '0', '0', 'kkstars.cn', '520@kkstars.cn', '个人博客,何坤个人博客,个人博客系统,Babysbreath满天星博客,何坤', '3000', '0', '10092', '满天星');

