-- ----------------------------
-- Table structure for message
-- ----------------------------
DROP TABLE IF EXISTS `message`;
CREATE TABLE `message` (
  `messageId` int(11) NOT NULL AUTO_INCREMENT COMMENT '留言信息Id',
  `messageContent` text COMMENT '留言信息内容',
  `randAtorName` varchar(12) DEFAULT NULL COMMENT '随机姓名',
  `atorIP` varchar(36) DEFAULT NULL,
  `addTime` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '留言信息时间',
  `status` int(1) DEFAULT '1' COMMENT '状态值：1正常显示，0已被删除',
  `praise` int(11) DEFAULT '0' COMMENT '留言赞',
  PRIMARY KEY (`messageId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of message
-- ----------------------------

-- ----------------------------
-- Table structure for mood
-- ----------------------------
DROP TABLE IF EXISTS `mood`;
CREATE TABLE `mood` (
  `moodId` int(11) NOT NULL AUTO_INCREMENT,
  `moodTitle` varchar(100) DEFAULT NULL,
  `moodContent` text,
  `moodPic` varchar(255) DEFAULT NULL,
  `addTime` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  `status` int(1) DEFAULT '1',
  PRIMARY KEY (`moodId`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of mood
-- ----------------------------
INSERT INTO `mood` VALUES ('1', '我慢慢走在回忆中 却不知早已泪流满面', '<p style=\"text-indent: 2em; text-align: left;\">1、如是颠簸生世亦无悔。</p><p style=\"text-indent: 2em; text-align: left;\">2、仇恨最可悲的是：他的世界没有你，你的世界只有他。</p><p style=\"text-indent: 2em; text-align: left;\">3、雨珠顺着小草的茎滚下来，一滴钻到土里，又一滴钻到了小草的嘴里，找不到了。&nbsp;</p><p style=\"text-indent: 2em; text-align: left;\">4、期待，是所有心痛的根源，心不动则不痛。</p><p style=\"text-indent: 2em; text-align: left;\">5、费尽心思想找到一双自己喜欢的鞋子,原来最喜欢的不一定最适合自己。</p><p style=\"text-indent: 2em; text-align: left;\">6、我心里已经有了 你，再好的我都不要。</p><p style=\"text-indent: 2em; text-align: left;\">7、待我羽翼丰满，必定振翅飞翔。</p><p style=\"text-indent: 2em; text-align: left;\">8、选择友谊是对爱情的逃避</p><p style=\"text-indent: 2em; text-align: left;\">9、一个人自以为刻骨铭心的回忆。别人也许早已经忘记了。</p><p style=\"text-indent: 2em; text-align: left;\">10、一朵花的孤芳，我不用再委屈你来跟我一起欣赏，等你的烛光已经熄灭了。</p><p style=\"text-indent: 2em; text-align: left;\"></p><p style=\"text-indent: 2em; text-align: left;\">11、生活常和我们开着玩笑，你期待谁，谁就会离你越远；你执着谁，谁就会伤你最深。</p><p style=\"text-indent: 2em; text-align: left;\">12、别活在过去，那会令你失去眼前许多美好的事情。</p><p style=\"text-indent: 2em; text-align: left;\">13、如果流言蜚语能打击爱情，那说明我真的不爱你。</p><p style=\"text-indent: 2em; text-align: left;\">14、我慢慢走在回忆中 却不知早已泪流满面</p><p style=\"text-indent: 2em; text-align: left;\">15、我不是你爹妈，别拿你的脾气挑战我的性格。</p><p style=\"text-indent: 2em; text-align: left;\">16、要抒写自己梦想的人，反而更应该清醒。</p><p style=\"text-indent: 2em; text-align: left;\">17、人最大的福气 就是永远没有那么多刻意的在乎</p><p style=\"text-indent: 2em; text-align: left;\">18、曾经的欢笑如今已经风化得只剩下难以追回的标本供我回忆纪念。</p><p style=\"text-indent: 2em; text-align: left;\">19、爱一个不爱你的人，伤了自己，为难了别人。</p><p style=\"text-indent: 2em; text-align: left;\">20、相爱的人不会因为一句分手而结束</p><p style=\"text-indent: 2em; text-align: left;\">21、只因我的心是高高低低的风铃 叮咛叮咛咛 此起彼落 敲叩着一个人的名字</p><p style=\"text-indent: 2em; text-align: left;\">22、千万别在一棵树上吊死，可以到周围的树上多试几次。</p><p style=\"text-indent: 2em; text-align: left;\">23、我爱的终究是不属于我的，陪你到最后的，终究也不是我</p><p style=\"text-indent: 2em; text-align: left;\">24、苏陌宸：　　　　　　　[ 别刻意去改变 ]</p><p style=\"text-indent: 2em; text-align: left;\">25、你一句谎言这么重的分量。</p><p style=\"text-indent: 2em; text-align: left;\">26、一直固执的以为面对什么事情我都能够坦然的微笑，可是，终于在你转身决定离去的一刹那，我泪如泉涌，不可抑制。这是，过往的幸福嘲笑着心中的疼痛，原来，世界上最痛的痛是离开。</p><p style=\"text-indent: 2em; text-align: left;\">27、[　　　我哭的多狼狈也不如她稍皱眉,]</p><p style=\"text-indent: 2em; text-align: left;\">28、爱我的人我不爱，我爱的人不爱我。</p><p style=\"text-indent: 2em; text-align: left;\">29、时间夺去了我们轻狂的眼神 却给了我们嘴角上扬的资本。</p><p style=\"text-indent: 2em; text-align: left;\">30、［我不会放弃任何一个爱我的人。］</p><p style=\"text-indent: 2em; text-align: left;\">31、无聊的人生。天天也就这样无聊的过着生活。</p>', 'admin/Upload/20180418/42faa565dcb2e050bca75ce1f53ce8c7.jpg', '2018-04-18 14:09:06', '1');
INSERT INTO `mood` VALUES ('2', '梦里，不知身是客，梦外，庭院深几许？', '<p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">1、如果有一天全世界都讨厌我了，你会不会对我说我还是那么喜欢你。</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">2、人生的旅途中，总有那么一段时间，需要你自己走，自己扛。</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">3、如我和你对视你那般平淡波澜的眼睛让我无法直视</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">4、我知道 你给的诺言其实都是谎言我却一直相信你到现在</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">5、有时候你会慢慢发现，幼稚也是一种幸福。</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">6、心宽阔了，才能明白没有一个人是孤岛，才能欣赏到对岸的风景，才能懂得如何接纳他人。</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">7、人的一生，总有一些说不出的秘密，挽不回的遗憾，触不到的梦想，忘不了的爱。</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">8、如果时间能够倒退，一切也就没那么珍贵</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">9、也许现在的我还不足以让你注意到我所以我想努力和你站在同一水平线上</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">10、别太自以为是 其实在别人眼里你什么都不是</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">11、[相爱也需要勇气去面对流言蜚语]</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">12、你牛B什么?我过的桥，比你走的路还多。</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">13、爱你并不是因为你长得帅，而是你身上有一种别人给不了我的安全感。</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">14、怪姐太年轻，人狗没分清、</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">15、我难过是因为我想要的你可以给但是你没有给</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">16、继续可能你会失败,但是放弃,你就连失败的机会都没了.</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">17、女人，往往喜欢坦白心事，男人，则恰恰相反。</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">18、一个微笑就能开出绚烂的爱情，一个眼神就能够抵抗寂寞。</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">19、我不需要多么完美的爱情我只需要有一个人永远不会放弃我。</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">20、我只是一個配角，或許，那份屬於我的篇章，不進入你的戏。</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">21、我不是你无聊是陪你玩的玩具，所以请不要在你无聊的时候才想起我</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">22、岁月携着记忆渐渐远去，不得不让我们原谅那次说的再见，忘记那次拥抱的温度。</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">23、为什么做什么都要顾及别人的感受，我顾及别人，那谁来顾及我。</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">24、当一个男人不再爱他的女人，她哭闹是错，静默也是错，活着呼吸是错，死了都是错。</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">25、也曾试着逃避，也曾装作无意，但我无法忘记，因为我很在意。</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">26、你走的那天没，我决定不掉泪，迎着等撑着眼帘用力不眨眼</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">27、每当和别人聊天的时候，我发现。好多话原来只和你说过</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">28、我把未来编织的再美好，也终究抵不过现实的一次次拉扯。</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">29、执于一念，将受困于一念；一念放下，会自在于心间。</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">30、我不要别人的拥抱那里没有你的心跳.</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">31、亲爱的，失去你可以么。. ##</p><p style=\"margin: 0px auto; padding: 0px; font-family: Verdana, Arial, Tahoma; color: rgb(51, 51, 51); font-size: 14px; line-height: 25px; white-space: normal; widows: 1;\">32、[ 时间太狠 宰的我不知所措　]</p><p><br/></p>', 'admin/Upload/20180418/deb1aee0d0de9487e0c479d5b39ea3e4.jpg', '2018-04-18 14:13:33', '1');
INSERT INTO `mood` VALUES ('3', '我喜欢那些能让我笑起来的人，即使是我不想笑得时候', '<p><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">1．也许似乎大概是，然而未必不见得。</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　2．一遍一遍的翻着列表，却发现其实并没有几个能联系的人。</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　3．通往成功的路，总是在施工中。</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　4．有时候，音乐是陪我熬过那些夜晚的唯一朋友。</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　5．我败在了我以为，以为我和你就是我们。</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　6．我喜欢那些能让我笑起来的人，即使是我不想笑得时候。</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　7．路的尽头，仍然是路，只要你愿意走。</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　8．多么幸运遇见不说也能懂、随口蹦出一样的一个词一句话的你。</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　9．与其在别人的生活里跑龙套，不如精彩做自己。</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　10．心是自己的，为什么要让别人来伤。</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　11．我也许给你的不是你想要的，但是我给你的都是我认为最好的。</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　12．我想要一份，放肆的幸福</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　13．当你对自己诚实的时候，世界上就没有人能够欺骗得了你。</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　14．谁能对我没有理由的心疼和不设前提的宽容。</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　15．经常惹怒你的那个人，才是爱你越深的那个，只不过不懂表达罢了。</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　16．把别人看得太重，结果却被别人看成什么都不是。</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　17．心是个口袋，东西装少点叫心灵，多一点叫心眼，再多一点时叫心计，更多是叫心机。</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　18．我想你的时候，你会像鬼一样出现，要是那样就好了。</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　19．风光的背后，不是沧桑，就是肮脏。</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　</span><br/><span style=\"font-family: Verdana, Arial, Tahoma; font-size: 14px; line-height: 25px; widows: 1; background-color: rgb(255, 255, 255);\">　　20．我不怕任何人抢走我的朋友，因为会被抢走的朋友一定不是好朋友。</span><br/></p>', 'admin/Upload/20180418/e7a6ec25306f06df2cc4c9d1d559a412.jpg', '2018-04-18 10:04:09', '1');
INSERT INTO `mood` VALUES ('4', '虽然我们在一起的时间并不多，但是每件事我都记着', '<p style=\"text-align: left;\">1．如果没有作业，没有测验，没有考试，没有家长会，我想我会很喜欢上学。<br/>　　<br/>　　2．如果你准备结婚的话，告诉你一句非常重要的哲学名言：你一定要忍耐包容对方的缺点，世界上没有绝对幸福圆满的婚姻，幸福只是来自于无限的容忍与互相尊重。<br/>　　<br/>　　3．其实越好的人，越不会被珍惜的，真的。<br/>　　<br/>　　4．你有你的生命观，我有我的生命观，我不干涉你。只要我能，我就感化你。如果不能，那我就认命。<br/>　　<br/>　　5．狂妄的人有救，自卑的人没有救。<br/>　　<br/>　　6．刚刚看见我的访客记录里有你，心震了一下。<br/>　　<br/>　　7．若能一切随他去，便是世间自在人。<br/>　　<br/>　　8．过错是暂时的遗憾，而错过则是永远的遗憾！不要害怕过错而错过……<br/>　　<br/>　　9．与你一起时，我就是我。<br/>　　<br/>　　10．世界原本就不是属于你，因此你用不着抛弃，要抛弃的是一切的执著。万物皆为我所用，但非我所属。<br/>　　<br/>　　11．没人催我早点睡，也没有人跟我说晚安<br/>　　<br/>　　12．不是某人使我烦恼，而是我拿某人的言行来烦恼自己。<br/>　　<br/>　　13．我们应该一天给自己一个希望。希望，看似很平淡的字眼，却包含着各种不同的内涵。试着每天给自己一个希望，靠自己的努力和尝试去创造，去探索，去发展，就会进步，就会成功，你的生活也会因此变得美丽而又意义。<br/>　　<br/>　　14．宁可自己去原谅别人，莫让别人来原谅你。<br/>　　<br/>　　15．人，总在不同的时刻，不同的人的面前演绎不同的自己。<br/>　　<br/>　　16．活在别人的掌声中，是禁不起考验的人。<br/>　　<br/>　　17．我们总是在错误的时间，错误的地点，懵懵然就爱上那个人，然后，不得不用尽一生，遗忘。<br/>　　<br/>　　18．我的财富并不是因为我拥有很多，而是我要求的很少。<br/>　　<br/>　　19．如果你能每天呐喊二十一遍「我用不着为这一点小事而烦恼」，你会发现，你心里有一种不可思议的力量，试试看，很管用的。<br/>　　<br/>　　20．我们在一起的时间并不多，所以每件事我都记着。</p>', 'admin/Upload/20180418/e7a6ec25306f06df2cc4c9d1d559a412.jpg', '2018-04-18 10:20:09', '1');
INSERT INTO `mood` VALUES ('5', '在我们这个年龄，没有长久，只有珍惜', '<p style=\"text-indent: 2em; text-align: left;\">1．我们总说要做好每一件事，但在很多事面前我们却总是无能为力。<br/>　　<br/>　　2．QQ的在线率越高，说明这个人越寂寞。<br/>　　<br/>　　3．深深地伤害了最爱我的那个人，那一刻，我听见他心破碎的声音。直到转身，我才发现，原来那声心碎，其实，也是我自己的。<br/>　　<br/>　　4．其实，我很想念某些时候，某些人，某些事…<br/>　　<br/>　　5．最美的心碎，不过是：你遇见我，我遇见你，然后，挥手，说再见。<br/>　　<br/>　　6．在我们这个年龄，没有长久，只有珍惜。<br/>　　<br/>　　7．痛苦的不是过去，而是记忆。回首过往的点滴，感情就像一个沙漏，到最后握在手里的，只是一缕清风，和自己早已被风吹干的泪痕。<br/>　　<br/>　　8．我只想留住，身边很温暖、很重要的人。<br/>　　<br/>　　9．眼睛，不应该用来为伤害你的人哭泣，而应该用来寻找那个正确的人。<br/>　　<br/>　　10．在我孤独，无助，难过的时候，从来没有人站在我身后。<br/>　　<br/>　　11．我想，我们前生肯定有过相爱，不然为什么初次相遇，竟然第一次不愿放开被你轻握的手。过客万千，独为你钟情，一次相遇，注定一生的眷念。<br/>　　<br/>　　12．当你和我说完晚安后，不知道你又和谁聊的火热朝天<br/>　　<br/>　　13．一生只谈一次恋爱是最好的，经历太多，会麻木；换恋人多了，会比较；到最后，你不会再相信爱情。<br/>　　<br/>　　14．你是否也是每次看电视剧只要没字幕，就觉得听不清楚。<br/>　　<br/>　　15．一个人要知道自己的位置，就像一个人知道自己的脸面一样，这是最为清醒的自觉。洗尽铅华总是比随意的涂脂抹粉来得美。所以做能做的事，把它做的最好，这才是做人的重要。<br/>　　<br/>　　16．我不屑与任何一个人去争。爱我的，不用争。不爱的，争来也没用。<br/>　　<br/>　　17．自我是五个孩子的父亲，孩子分别是自卑、自信、自大、自嘲、自然。按个性，他最担心的是自大，最讨厌的是自卑，最不管的是自嘲，最心爱的是自然。<br/>　　<br/>　　18．你们有没有发现，很多童鞋说“我去睡了”等于电脑换手机了。<br/>　　<br/>　　19．当他说不愿欠你太多，等等，如果是为了要分手，那其实一万句话，都等于不爱你了，不珍惜你了。说的越多，就代表他离开的决心就越坚定。面对这样一个不珍惜你的人，又何必留恋。继续纠结下去，只会越来越烦恼，痛苦，受伤，还不如就此放手，长痛不如短痛，给自己一个重生的机会吧，再痛，也会过去的。<br/>　　<br/>　　20．青春，注定了要颠簸，要有眼泪和汗水，有委屈、不甘和失败。</p>', 'admin/Upload/20180418/e7a6ec25306f06df2cc4c9d1d559a412.jpg', '2018-04-18 10:34:28', '1');

-- ----------------------------
-- Table structure for reply
-- ----------------------------
DROP TABLE IF EXISTS `reply`;
CREATE TABLE `reply` (
  `replyId` int(11) NOT NULL AUTO_INCREMENT,
  `replyContent` text,
  `replyMid` int(11) DEFAULT NULL,
  `ator` varchar(20) DEFAULT NULL COMMENT '随机姓名',
  `atorIP` varchar(36) DEFAULT NULL,
  `addTime` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `status` int(1) NOT NULL DEFAULT '1',
  PRIMARY KEY (`replyId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of reply
-- ----------------------------
